    "timestamp": "",
    "birthday": "",
    "phone_number": "",
}) => {
    params = getRemoveEmptyObject(params);
    return post(endpoints.userManagement.create, params).then(value => {
        return value ? value.data : null;
    });
};

const updateUser = (attributes, username) => {
    attributes = getRemoveEmptyObject(attributes);
    return put(endpoints.userManagement.update.replace(":ID", username), attributes).then(value => {
        return value ? value.data : null;
    });
};

const addToken = (token, username) => {
    return post(endpoints.userManagement.addToken.replace('admin', username), {token: token}, {
        Authorization: `Bearer ${token}`,
    }).then(value => {
        return value ? value.data : null;
    });
};

const addPermissions = (data) => {
    return post(endpoints.userManagement.permissions, data).then(value => {
        return value ? value.data : null;
    });
};
const putPermissions = (id, data) => {
    return put(endpoints.userManagement.permissions + '/' + id, data).then(value => {
        return value ? value.data : null;
    });
};
const getPermissionsResource = () => {
    return get(endpoints.userManagement.permissionsResources).then(value => {
        return value ? {
                data: {
                    "items": [
                        {"title": "User management", "value": "admin-management", isTitle: true, isHidden: true},
                        {"title": "Normal user", "value": "user-management"},
                        {"title": "Business - User", "value": "biz-user"},
                        {"title": "Business - Manager", "value": "biz-manager"},

                        {"title": "Business Management", "value": "business-management", isTitle: true},
