                        {"title": "Emoji", "value": "biz-emoji"},
                        {"title": "Campaign", "value": "biz-campaign"},
                        {"title": "Access Code", "value": "biz-access-code"},
                        {"title": "Additional field", "value": "biz-option-fields"},

                        {"title": "Module Management", "value": "module-management", isTitle: true},
                        {"title": "Question Management", "value": "question-management"},
                        {"title": "Dialog Management", "value": "dialog-management"},

                        {"title": "Emoji Management", "value": "emoji-management", isTitle: true},
                    ]
                }
            } :
            null;
    });
};

const getPermissionsRelation = (permissions, action, resource, checked) => {
    console.log("Input:", permissions, action, resource, checked);
    if (roleRelation[resource] && roleRelation[resource][action] && checked) {
        roleRelation[resource][action].map(x => {
            const res = Object.keys(x)[0];
            x[res].map(y => {
                permissions = putPermissionSelected(permissions, y, res, true);
                permissions = getPermissionsRelation(permissions, y, res, true);
            });
        })
    }
    console.log("Result:", permissions);
    return permissions;
};

const getPermissionDisable = (permissions, action, resource) => {
    const checked = getPermissionSelected(permissions, action, resource);
    if (checked) {
        const parents = [];
        for (const roleRelationKey in roleRelation) {
            const actions = ['view', 'view-self', 'delete', 'create', 'edit'];
            actions.map(ac => {
                let rc = [];
                if (roleRelation[roleRelationKey] && roleRelation[roleRelationKey][ac] && roleRelation[roleRelationKey][ac] && roleRelation[roleRelationKey][ac].find(x => x[resource])) {
                    rc = roleRelation[roleRelationKey][ac].find(x => x[resource])[resource];
                }
                if (rc.find(x => x === action)) {
                    parents.push({resource: roleRelationKey, action: ac});
                }
            })
        }
        // If parent checked =>disable
        if (action === 'view-self' && getPermissionDisable(permissions, 'view', resource)) {
            return true;
        }
        return parents.find(x => getPermissionSelected(permissions, x.action, x.resource))
    } else {
        const parents = [];
        for (const roleRelationKey in roleRelation) {
            const actions = ['view', 'view-self', 'delete', 'create', 'edit'];
            actions.map(ac => {
                let rc = [];
                if (roleRelation[roleRelationKey] && roleRelation[roleRelationKey][ac] && roleRelation[roleRelationKey][ac] && roleRelation[roleRelationKey][ac].find(x => x[resource])) {
                    rc = roleRelation[roleRelationKey][ac].find(x => x[resource])[resource];
                }
                if (rc.find(x => x === action)) {
                    parents.push({resource: roleRelationKey, action: ac});
                }
            })
        }
        // If parent checked =>disable
        console.log(resource, action, parents);
        if (action === 'view-self' && getPermissionDisable(permissions, 'view', resource)) {
            console.log(resource, parents.filter(x => getPermissionSelected(permissions, x.action, x.resource)));
            return !!parents.filter(x => getPermissionSelected(permissions, x.action, x.resource)).find(x => x.resource !== resource);
        }
        return false;
    }
};

