    return del(endpoints.userManagement.delete.replace(":ID", username)).then(value => {
        return value ? value.data : null;
    });
};

const addGroupUser = (username, groupName) => {
    return null;
};

const disableToggleUser = (username, enable = false) => {
    return null;
};

const listUser = (dataSearch = {limit: 10}, type, isIndividual = false) => {
    dataSearch = getRemoveEmptyObject(dataSearch);
    if (isIndividual) {
        return get(endpoints.userManagement.listIndividual, dataSearch).then(value => {
            let dataUsers = value ? value.data : null;
            if (dataUsers && dataUsers.data && dataUsers.data.items) {
                dataUsers.data.items = dataUsers.data.items.map(u => {
                    return {
                        user_name: u.userName,
                        createdAt: u.createdAt,
