    if (action === 'edit' && checkPermission('view', resource) && !checkPermission(action, resource)) {
        return "Detail"
    }
    return "Update"
}

const putPermissionSelected = (permission, action, resource, checked) => {
    let newPermission = JSON.parse(JSON.stringify(permission));
    if (!newPermission.find(x => x.resources === resource)) {
        newPermission.push({resources: resource, actions: []});
    }
    return newPermission.map(x => {
        if (x.resources === resource) {
            if (x.actions.find(y => y === action)) {
                x.actions = x.actions.filter(y => y !== action);
