import React, {useState, useEffect} from "react";
import {Button, ButtonGroup, CircularProgress, makeStyles} from "@material-ui/core";
import {Paper, Table, TableHead, TableRow, TableCell, TableBody} from "@material-ui/core";

import {Form, Card, Col} from "react-bootstrap";
import ModalAntd from "../../common/antd/component/Modal";
import {Icon} from "@material-ui/core";
import {showErrorMessage, showSuccessMessage} from "../../actions/notification";
import SelectForm from "../../partials/common/SelectForm";
import {ACTIVE_STATUS, GENDER} from "../../config/common/user";
import "./user.css";
import {listUser, updateUser, deleteUser, checkPermissionStyle} from "../../services/CognitoService";
import {useDispatch} from "react-redux";
import {setHeaderTitle} from "../../store/actions/HeaderAction";
import {useHistory, useParams} from "react-router";
import ActionItem from "../../common/action";

const useStyles1 = makeStyles((theme) => ({
    root: {
        width: "100%",
        marginTop: theme.spacing(3),
        overflowX: "auto",
    },
    table: {
        minWidth: 650,
    },
}));
String.prototype.capitalize = function () {
    return this.charAt(0).toUpperCase() + this.slice(1);
}
export default function ListUser(props) {
    const history = useHistory();
    const dispatch = useDispatch();
    const params = useParams();
    const classes1 = useStyles1();
    const [rows, setRow] = useState();
    const [dataSearch, setData] = useState("");
    const [dataDelete, setDataDelete] = useState({visible: false});
    const [dataUpdate, setDataUpdate] = useState({visible: false});
    const inputNameBankRef = React.createRef();
    const inputEmailBankRef = React.createRef();
    const [LinkImage, setLinkImage] = useState("");
    const [srcImage, setSrcImage] = useState("");
    const [isSearch, setIsSearch] = useState(false);
    const [changeImage, setChangeImage] = useState(false);
    const [stackPage, setStackPage] = useState([]);
    const [page, setPage] = useState(0);
    const [rowsPerPage, setRowsPerPage] = useState(10);

    useEffect(() => {
        if (params.Type === "admin") {
            dispatch(setHeaderTitle(`Super Admin Management`));
        } else if (params.Type === "normal-admin") {
            dispatch(setHeaderTitle(`Normal Admin Management`));
        } else if (params.Type === "normal") {
            dispatch(setHeaderTitle(`User Management`));
        } else {
            dispatch(setHeaderTitle(`${params.Type.toString().capitalize()} Management`));
        }

        setPage(0);
        list(null, true);
    }, [params]);

    const list = (nextToken, isReset = false) => {
        let isIndividual = false;
        if (params.Type.toString().toLocaleLowerCase() === "normal") {
            isIndividual = true;
        }
        listUser({nextToken, limit: rowsPerPage}, params.Type, isIndividual).then((value) => {
            if (value && value.data) {
                setRow(value.data.items ? value.data.items : []);
                if (!stackPage.find(x => x === value.data.nextToken)) {
                    let stackPageNew = isReset ? [] : JSON.parse(JSON.stringify(stackPage));
                    stackPageNew.push(value.data.nextToken);
                    setStackPage(stackPageNew);
                }
            }
        });
    };

    const nextStackPage = () => {
        const nextToken = stackPage[page] || null;
        list(nextToken);
        setPage(page + 1);
    };

    const prevStackPage = async () => {
        if (page >= 1) {
            const nextToken = stackPage[page - 2] || null;
            list(nextToken);
            setPage(page - 1);
        }
    };

    const showModalUpdate = (row) => {
        history.push("/User/Edit", row);
    };

    const showModal = (user) => {
        setDataDelete({
            ...dataDelete,
            visible: true,
            user,
        });
    };


    const clickModalCancel = () => {
        setDataDelete({
            ...dataDelete,
            visible: false,
        });
    };


    const onChangeUpdateValue = (key, value) => {
        setDataUpdate({
            ...dataUpdate,
            [key]: value,
        });
    };
    const clickModalOk = () => {
        deleteUser(dataDelete.user.user_name).then((value) => {
            if (value && value.errorCode === 200) {
                showSuccessMessage("Deleted User Successfully!!");
            }
            list(null, true);
            clickModalCancel();
        });
    };


    const renderStatusText = (category) => {
        if (category !== "inactive") {
            return (
                <span
                    className="btn btn-label-success btn-bold btn-sm btn-icon-h"
                    style={{borderRadius: ".42rem"}}>
          Active
        </span>
            );
        } else {
            return (
                <span
                    className="btn btn-label-warning btn-bold btn-sm btn-icon-h"
                    style={{borderRadius: ".42rem", textTransform: "capitalize"}}>
          Inactive
        </span>
            );
        }
    };


    return (
        <>
            <Button {...checkPermissionStyle('create', !params.Type.toString().includes('admin') ? 'user-management' : 'admin-management')}
                    onClick={() => {
                        props.history.push(`/User/AddNew/${params.Type}`, {type: params.Type});
                    }} variant="contained" color="primary">Add User</Button>
            <div className="row kt-mt-15">
                <div className="col-md-12">
                    <div className="kt-section">
                        <div className="kt-section__content">
                            <Paper>
                                <Table className={classes1.table}>
                                    <TableHead>
                                        <TableRow>
                                            <TableCell>Username</TableCell>
                                            <TableCell>Full Name</TableCell>
                                            <TableCell>Email Address</TableCell>
                                            <TableCell>Phone</TableCell>
                                            <TableCell>Status</TableCell>
                                            <TableCell style={{width: 150}}>Action</TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {rows && rows.length ? (
                                            rows.map((row, key) => (
                                                <TableRow key={`user${key}`}>
                                                    <TableCell>{row.user_name}</TableCell>
                                                    <TableCell>{row.full_name}</TableCell>

                                                    <TableCell>{row.email}</TableCell>
                                                    <TableCell>{row.phone_number}</TableCell>
                                                    <TableCell>{renderStatusText(row.status)}</TableCell>
                                                    <TableCell>
                                                        <ActionItem
                                                            icon={'fa fa-pen'} color={"#ffa800"}
                                                            onClick={() => {
                                                                showModalUpdate(row)
                                                            }}
                                                            {...checkPermissionStyle('edit', !params.Type.toString().includes('admin') ? 'user-management' : 'admin-management')}
                                                        />
                                                        <ActionItem
                                                            {...checkPermissionStyle('delete', !params.Type.toString().includes('admin') ? 'user-management' : 'admin-management')}
                                                            icon={'fa fa-trash'} color={"rgb(220, 0, 78)"}
                                                            onClick={() => {
                                                                showModal(row)
                                                            }}/>
                                                    </TableCell>
                                                </TableRow>
                                            ))
                                        ) : rows && rows.length === 0 ? (
                                            <TableRow>
                                                <TableCell colSpan={10} align="center">
                                                    No data
                                                </TableCell>
                                            </TableRow>
                                        ) : (
                                            <TableRow>
                                                <TableCell colSpan={10} align="center">
                                                    <CircularProgress size={20}/>
                                                </TableCell>
                                            </TableRow>
                                        )}
                                    </TableBody>
                                </Table>
                            </Paper>
                            <div className="customSelector custom-svg text-center kt-margin-t-10">
                                <ButtonGroup variant="contained" color="primary"
                                             aria-label="contained primary button group">
                                    <Button onClick={() => prevStackPage()}
                                            disabled={!stackPage[page - 1]}>Prev</Button>
                                    <Button onClick={() => nextStackPage()} disabled={!stackPage[page]}>Next</Button>
                                </ButtonGroup>
                            </div>
                        </div>
                        <ModalAntd
                            title="Delete User"
                            visible={dataDelete.visible}
                            onOk={clickModalOk}
                            onCancel={clickModalCancel}
                            cancelText="Cancel"
                            okText="Ok">
                            <p>Do you want to delete this User?</p>
                        </ModalAntd>
                    </div>
                </div>
            </div>
        </>
    );
}
