import React, {useState, useEffect} from "react";
import {showErrorMessage, showSuccessMessage} from "../../actions/notification";
import {Form, Card, Col} from "react-bootstrap";
import ButtonLoading from "../../partials/common/ButtonLoading";
import {Redirect, useHistory} from "react-router-dom";
import {Checkbox, DatePicker, Modal, Select} from "antd";
import "./user.css";
import {
    updateUser,
    getUser,
    changeGroupUser,
    getPermissionsResource,
    getPermissionsAction,
    getPermissionSelected,
    putPermissionSelected,
    getPermissionsDetail,
    addPermissions,
    putPermissions, checkPermissionStyle, checkPermissionTextView, getPermissionsRelation, getPermissionDisable
} from "../../services/CognitoService";
import {getChanged} from "../../services/AppService";
import {GENDER, ROLES} from "../../config/common/user";
import SelectForm from "../../partials/common/SelectForm";
import {useDispatch, useSelector} from "react-redux";
import {setHeaderTitle} from "../../store/actions/HeaderAction";
import PhoneInput, {parsePhoneNumber} from "react-phone-number-input";
import "react-phone-number-input/style.css";
import moment from "moment";
import {Table} from "reactstrap";
import {TableCell, TableHead, TableRow, TableBody} from "@material-ui/core";
import BusinessService from "../../services/BusinessService";
import {actions as AC} from "../../store/ducks/auth.duck";

const EditUser = (props) => {
    const dispatch = useDispatch();
    const history = useHistory();
    const currentUser = props.location.state;
    const [dataUpdate, setData] = useState(currentUser ? currentUser : {});
    const [isLoadSubmit, setLoadSubmit] = useState(false);
    const [isRefuse, setRefuse] = useState(false);
    const [isFirstLoad, setFirstLoad] = useState(false);
    const [dataCancel, setDataCancel] = useState({visible: false});
    const [phone, setPhone] = useState("");

    const [actions, setActions] = useState([]);
    const [resources, setResources] = useState([]);
    const [permission, setPermission] = useState([]);
    const [permissionId, setPermissionId] = useState();
    const [business, setBusiness] = useState([]);
    const [businesses, setBusinesses] = useState([]);

    const passwordRef = React.useRef();
    const me = useSelector(state => state.auth.user);


    const loadRoleData = (role) => {

        if (role === 'normal-admin' || role === 'admin') {
            getPermissionsResource().then(value => {
                if (value) {
                    setResources(value.data.items);
                }
            });
            getPermissionsAction().then(value => {
                if (value) {
                    setActions(value.data.items);
                }
            });
        }

        if (role === 'normal-admin') {
            BusinessService.searchBusiness({all: true}).then((value) => {
                if (value && value.data) {
                    setBusiness(value.data.items);
                }
            });
            getPermissionsDetail(currentUser.user_name).then((value) => {
                if (value && value.data && value.data.id) {
                    setBusinesses(value.data.business);
                    setPermission(value.data.permissions);
                    setPermissionId(value.data.id);
                }
            });
        }
    };

    useEffect(() => {
        dispatch(setHeaderTitle(`${checkPermissionTextView('edit', !(dataUpdate.role || '').includes('admin') ? 'user-management' : 'admin-management')} User`));
        if (!currentUser) {
            history.push("/User/List");
        } else {
            getUser(currentUser.user_name).then(value => {
                if (value) {
                    value.data.role = value.data.groups[0];
                    currentUser.role = value.data.groups[0];
                    currentUser.status = value.data.status === 'inactive' ? "inactive" : 'active';
                    setPhone(currentUser.phone_number);
                    loadRoleData(currentUser.role);
                    setData({
                        ...value.data,
                        status: value.data.status === 'inactive' ? "inactive" : 'active'
                    });
                }
            });

        }
    }, []);

    if (isRefuse) return <Redirect to="/Error403"/>;
    if (isFirstLoad) return <Redirect to="/"/>;
    const enableLoadSubmit = () => {
        setLoadSubmit(true);
    };

    const disableLoadSubmit = () => {
        setLoadSubmit(false);
    };
    const onChangeValue = (key, value) => {
        setData({
            ...dataUpdate,
            [key]: value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!dataUpdate.user_name) {
            return showErrorMessage("Username must not be empty!");
        }
        const filter = /^[A-Za-z0-9_]+$/;
        if (!filter.test(dataUpdate.user_name)) {
            return showErrorMessage("Only Allow username letters and numbers! ");
        }
        if (!dataUpdate.role) {
            return showErrorMessage("Group must not be empty!");
        }
        if (!dataUpdate.full_name) {
            return showErrorMessage("Fullname must not be empty!");
        }
        if (!dataUpdate.email) {
            return showErrorMessage("Email must not be empty!");
        }

        // if (!dataUpdate.email && !dataUpdate.password) {
        //   return showErrorMessage("Please enter email or password! ");
        // }
        if (dataUpdate.email) {
            dataUpdate.email = dataUpdate.email.trim();
        }
        if (dataUpdate.group === 'admin') {
            dataUpdate.email_verified = true
        }
        enableLoadSubmit();
        // Get changed
        const changed = getChanged(currentUser, dataUpdate);
        delete changed.groups;
        if (phone) {
            const phoneParse = parsePhoneNumber(phone);
            changed["phone_country_code"] = `+${phoneParse.countryCallingCode}`;
            changed["phone_number"] = parseInt(phoneParse.nationalNumber);
        }

        if (changed.role) {
            await changeGroupUser(currentUser.role, changed.role, currentUser.user_name);
        }
        delete changed.active;
        delete changed.createdAt;
        delete changed.email_verified;
        delete changed.id;
        // delete changed.status;
        delete changed.updatedAt;
        if (dataUpdate.role === 'admin' && dataUpdate.currentPassword && dataUpdate.password) {
            changed.current_password = dataUpdate.currentPassword;
            changed.password = dataUpdate.password;
            changed.access_token = me.accessToken;
            if (changed.password.length < 8 || changed.current_password.length < 8) {
                return showErrorMessage('Password must be at least 8 characters long');
            }
        } else {
            delete changed.password;
        }
        delete changed.currentPassword;
        console.log((permission || []).filter(x => x.actions.length > 0));
        if (dataUpdate.role === 'normal-admin' && (permission || []).filter(x => x.actions.length > 0).length <= 0) {
            showErrorMessage("Permission must not be empty!");
            disableLoadSubmit();
            return;
        }
        if (dataUpdate.role === 'normal-admin' && businesses.length <= 0) {
            showErrorMessage("Business must not be empty!");
            disableLoadSubmit();
            return;
        }
        changed
        await updateUser({
            ...changed,
            ...(dataUpdate.role === 'normal-admin' ? {
                user: currentUser.user_name,
                business: businesses,
                permissions: permission.map(x => {
                    x.actions = x.actions.filter((item, pos) => {
                        return x.actions.indexOf(item) === pos;
                    });
                    return x;
                }).filter(x => x.actions.length > 0)
            } : {})
        }, currentUser.user_name).then(async (value) => {
            if (dataUpdate.role === 'normal-admin') {
                if (value && value.message === 'Success') {
                    // const thread = !permissionId ? addPermissions({
                    //     user: currentUser.user_name,
                    //     business: businesses,
                    //     permissions: permission.map(x => {
                    //         x.actions = x.actions.filter((item, pos) => {
                    //             return x.actions.indexOf(item) === pos;
                    //         });
                    //         return x;
                    //     }).filter(x => x.actions.length > 0)
                    // }) : putPermissions(permissionId, {
                    //     user: currentUser.user_name,
                    //     business: businesses,
                    //     permissions: permission.map(x => {
                    //         x.actions = x.actions.filter((item, pos) => {
                    //             return x.actions.indexOf(item) === pos;
                    //         });
                    //         return x;
                    //     }).filter(x => x.actions.length > 0)
                    // });
                    //
                    // thread.then(async value1 => {
                    if (value) {
                        if (currentUser.user_name === me.username) {
                            const resultPermission = await getPermissionsDetail(me.username, me.signInUserSession.idToken.jwtToken);
                            dispatch(AC.fulfillUser(
                                {
                                    ...me,
                                    permission: resultPermission.data
                                },
                                'normal-admin',
                                //sessionExpiredAt
                                me.signInUserSession.idToken.exp,
                            ));
                        }
                        if (props.location.state.business) {
                            props.history.goBack();
                        } else {
                            if (dataUpdate.role === 'normal-admin') {
                                props.history.push(`/Users/${dataUpdate.role}`);
                            } else {
                                props.history.push(`/User/List/${dataUpdate.role}`);
                            }
                        }
                        showSuccessMessage("Updated User Successfully!!");
                    }
                    // }).catch(() => {
                    // });

                }
                disableLoadSubmit();
            } else {
                if (value && value.message === 'Success') {
                    if (props.location.state.business) {
                        props.history.goBack();
                    } else {
                        props.history.push(`/User/List/${dataUpdate.role}`);
                    }
                    showSuccessMessage("Updated User Successfully!!");
                    disableLoadSubmit();
                }
            }

        });

    };

    const showModalCancel = (iCan) => {
        setDataCancel({
            ...dataCancel,
            visible: true,
            iCan,
        });
    };
    const clickModalCancelCancel = () => {
        setDataCancel({
            ...dataCancel,
            visible: false,
        });
    };
    const clickModalOk = () => {
        if (props.location.state.business) {
            props.history.goBack();
        } else {
            props.history.goBack();
        }
    };
    const test = (s) => {
        try {
            const time = moment(s, 'YYYY-MM-DD').format('YYYY-MM-DD');
            console.log(time);
            return time === s ? moment(s, 'YYYY-MM-DD') : null;
        } catch (e) {
            return null
        }
    }

    const setPasswordAuto = () => {
        const dt = {...dataUpdate};
        dt.password = generatePassword();
        setData(dt);
        passwordRef.current.setAttribute("type", "text");
    };

    const generatePassword = () => {
        let length = 20,
            charset = "abcdefghijk^&@^#&!%#(lmno012345678pqrstuv012345678wxyzABCDEFGHIJKLMNOPQRST^&@^#&!%#(UVWXYZ0123456789^&@^#&!%#()",
            retVal = "";
        for (var i = 0, n = charset.length; i < length; ++i) {
            retVal += charset.charAt(Math.floor(Math.random() * n));
        }
        return retVal + "1A#";
    };

    return dataUpdate ? (
        <>
            <div className="row">
                <div className="col-md-12">
                    <div className="kt-section">
                        <Card>
                            <Card.Body>
                                <Form onSubmit={handleSubmit}>
                                    <h4 style={{marginBottom: 20}}>Account detail</h4>
                                    <Form.Row>
                                        <Form.Group as={Col} controlId="formBasicName">
                                            <Form.Label className="starDanger">Username</Form.Label>
                                            <Form.Control
                                                type="text"
                                                maxLength={255}
                                                readOnly={true}
                                                disabled={true}
                                                placeholder="Enter username"
                                                value={dataUpdate?.user_name || ""}
                                                onChange={(e) => onChangeValue("user_name", e.target.value)}
                                                onBlur={(e) => onChangeValue("user_name", e.target.value.trim())}
                                            />
                                        </Form.Group>
                                        <Form.Group as={Col}>
                                            <Form.Label className="starDanger">Email </Form.Label>
                                            <Form.Control
                                                type="email"
                                                maxLength={255}
                                                placeholder="Enter your email"
                                                value={dataUpdate.email || ""}
                                                onChange={(e) => onChangeValue("email", e.target.value)}
                                                onBlur={(e) => onChangeValue("email", e.target.value.trim())}
                                            />
                                        </Form.Group>
                                    </Form.Row>
                                    <Form.Row>
                                        <Form.Group as={Col} controlId="formBasicName">
                                            <Form.Label className="starDanger">Full Name</Form.Label>
                                            <Form.Control
                                                type="text"
                                                maxLength={255}
                                                autoFocus
                                                placeholder="Enter user full name"
                                                value={dataUpdate.full_name || ""}
                                                onChange={(e) => onChangeValue("full_name", e.target.value)}
                                                onBlur={(e) => onChangeValue("full_name", e.target.value.trim())}
                                            />
                                        </Form.Group>
                                        <Form.Group as={Col}>
                                            <Form.Label>Phone number</Form.Label>{" "}
                                            <PhoneInput
                                                international={true}
                                                placeholder="Enter phone number"
                                                value={phone}
                                                className="form-control"
                                                onChange={setPhone}/>
                                            {/*<Form.Control*/}
                                            {/*  maxLength={255}*/}
                                            {/*  placeholder="Enter user phone"*/}
                                            {/*  value={dataUpdate.phone_number || ""}*/}
                                            {/*  onChange={(e) => onChangeValue("phone_number", e.target.value)}*/}
                                            {/*/>*/}
                                        </Form.Group>
                                    </Form.Row>

                                    <Form.Row>
                                        <Form.Group as={Col} controlId="formBasicName">
                                            <Form.Label>Address</Form.Label>
                                            <Form.Control
                                                type="text"
                                                maxLength={255}
                                                placeholder="Enter address"
                                                value={dataUpdate.address || ""}
                                                onChange={(e) => onChangeValue("address", e.target.value)}
                                                onBlur={(e) => onChangeValue("address", e.target.value.trim())}
                                            />
                                        </Form.Group>
                                        <Form.Group as={Col}>
                                            <Form.Label>Birthday </Form.Label>{" "}
                                            <div className={'date-input'}>
                                                <DatePicker format={'DD/MM/YYYY'} style={{width: '100%'}}
                                                            placeholder={'Select birthday'}
                                                            type={'date'}
                                                            autocomplete={'new-password'} allowClear
                                                            name={'dateinput'}
                                                            value={test(dataUpdate.birthday)}
                                                            defaultValue={test(dataUpdate.birthday) ? test(dataUpdate.birthday) : null}
                                                            onChange={(e) => onChangeValue("birthday", e.format('YYYY-MM-DD'))}/>
                                            </div>
                                        </Form.Group>
                                    </Form.Row>
                                    <Form.Row>
                                        <Form.Group as={Col} controlId="formBasicName">
                                            <Form.Label>Gender</Form.Label>
                                            <SelectForm
                                                optionData={GENDER}
                                                keyString="id"
                                                labelString="name"

                                                value={dataUpdate.gender || ""}
                                                onChangeValue={(value) => {
                                                    onChangeValue("gender", value);
                                                }}
                                            />
                                        </Form.Group>
                                        <Form.Group as={Col}>
                                            <Form.Label className="starDanger">Group </Form.Label>{" "}
                                            <SelectForm
                                                optionData={ROLES}
                                                keyString="id"
                                                labelString="name"
                                                disabled={true}
                                                value={dataUpdate.role || ""}
                                                onChangeValue={(value) => {
                                                    onChangeValue("role", value);
                                                }}
                                            />
                                        </Form.Group>
                                    </Form.Row>
                                    <Form.Row>
                                        {
                                            dataUpdate.role === 'normal-admin' ? (
                                                <Form.Group as={Col}>
                                                    <Form.Label className="starDanger">Business </Form.Label>{" "}
                                                    <Select
                                                        value={businesses}
                                                        onChange={value => setBusinesses(value)}
                                                        showSearch
                                                        filterOption={(input, option) =>
                                                            option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                                                        }
                                                        mode={'multiple'} style={{display: 'block', width: '100%'}}>
                                                        {
                                                            business.map((value, index) => (
                                                                <Select.Option key={index.toString()}
                                                                               value={value.id}>{value.name}</Select.Option>
                                                            ))
                                                        }
                                                    </Select>
                                                </Form.Group>
                                            ) : null
                                        }
                                        <Form.Group as={Col} controlId="formBasicName">
                                            <Form.Label className="starDanger">Status</Form.Label>
                                            <Select className={'select-ant'} value={dataUpdate.status || "active"}
                                                    onChange={(e) => onChangeValue("status", e)}>
                                                <Select.Option value={'active'}>Active</Select.Option>
                                                <Select.Option value={'inactive'}>Inactive</Select.Option>
                                            </Select>
                                        </Form.Group>
                                    </Form.Row>
                                    {
                                        (dataUpdate.role === 'admin' || dataUpdate.role === 'normal-admin') && me.username === dataUpdate.user_name ? (
                                            <>
                                                <Form.Row>
                                                    <Form.Group as={Col} controlId="formBasicName">
                                                        <Form.Label>Current Password</Form.Label>
                                                        <Form.Control
                                                            name={'currentpassword'}
                                                            ref={passwordRef}
                                                            type="password"
                                                            maxLength={255}
                                                            autoFocus
                                                            placeholder="Enter user password"
                                                            value={dataUpdate.currentPassword || ""}
                                                            onChange={(e) => onChangeValue("currentPassword", (e.target.value || "").split(' ').join(''))}
                                                        />
                                                    </Form.Group>
                                                    <Form.Group as={Col} controlId="formBasicName">
                                                        <Form.Label>New Password (<span
                                                            onClick={() => setPasswordAuto()}
                                                            className="gen-pass">Generate secure passwords</span>)</Form.Label>
                                                        <Form.Control
                                                            name={'newpassword'}
                                                            ref={passwordRef}
                                                            type="password"
                                                            maxLength={255}
                                                            autoFocus
                                                            placeholder="Enter user password"
                                                            value={dataUpdate.password || ""}
                                                            onChange={(e) => onChangeValue("password", (e.target.value || "").split(' ').join(''))}
                                                        />
                                                    </Form.Group>
                                                </Form.Row>
                                            </>
                                        ) : null
                                    }
                                    {
                                        dataUpdate.role === 'normal-admin' ? (
                                            <>
                                                <div className={'table-permission'}>
                                                    <h4 style={{marginTop: 20}}>Permission Setting</h4>
                                                    <Table>
                                                        <TableHead>
                                                            <TableRow>
                                                                <TableCell key={-1}></TableCell>
                                                                {
                                                                    actions.map((value, index) => (
                                                                        <TableCell
                                                                            style={{textAlign: 'center'}}
                                                                            key={index.toString()}>{value.title}</TableCell>
                                                                    ))
                                                                }
                                                            </TableRow>
                                                        </TableHead>
                                                        <TableBody>
                                                            {
                                                                resources.map((value, index) => (
                                                                    <TableRow key={index.toString()}
                                                                              className={value.isTitle ? 'row-title' : ''}>
                                                                        <TableCell
                                                                            key={-1}>{!value.isTitle ? `- ${value.title}` : value.title}</TableCell>
                                                                        {
                                                                            actions.map((valueAction, indexAction) => (
                                                                                <TableCell
                                                                                    className={'user-manager'}
                                                                                    style={{textAlign: 'center'}}
                                                                                    key={indexAction.toString()}>
                                                                                    {
                                                                                        (value.value === 'business-management' && (valueAction.value === 'delete' || valueAction.value === 'edit'))
                                                                                        || (value.value === 'biz-info' && (valueAction.value === 'delete' || valueAction.value === 'create' || valueAction.value === 'view-self'))
                                                                                        || value.isHidden ? null : (
                                                                                            <Checkbox
                                                                                                disabled={getPermissionDisable(permission, valueAction.value, value.value)}
                                                                                                checked={getPermissionSelected(permission, valueAction.value, value.value)}
                                                                                                onChange={e => {
                                                                                                    const check = getPermissionSelected(permission, valueAction.value, value.value);
                                                                                                    let newPermission = putPermissionSelected(permission, valueAction.value, value.value, !check);
                                                                                                    // Process relation
                                                                                                    newPermission = getPermissionsRelation(newPermission, valueAction.value, value.value, !check);

                                                                                                    setPermission(newPermission);

                                                                                                }} name={value.value}/>
                                                                                        )
                                                                                    }
                                                                                </TableCell>
                                                                            ))
                                                                        }
                                                                    </TableRow>
                                                                ))
                                                            }
                                                        </TableBody>
                                                    </Table>
                                                </div>
                                            </>
                                        ) : null
                                    }

                                    <div className="kt-login__actions">
                                        <button
                                            type="button"
                                            id="cancelBtn"
                                            className="btn btn-secondary btn-elevate kt-login__btn-secondary"
                                            onClick={() => showModalCancel()}>
                                            Cancel
                                        </button>

                                        <ButtonLoading className="btn btn-primary" loading={isLoadSubmit}
                                                       type="submit" {...checkPermissionStyle('edit', !(dataUpdate.role || '').includes('admin') ? 'user-management' : 'admin-management', true, true)}>
                                            Update
                                        </ButtonLoading>
                                    </div>
                                </Form>
                            </Card.Body>
                        </Card>
                        <Modal
                            title="Cancel User"
                            visible={dataCancel.visible}
                            onOk={clickModalOk}
                            onCancel={clickModalCancelCancel}
                            cancelText="Cancel"
                            okText="Ok">
                            <p>Do you want to cancel and quit ?</p>
                        </Modal>
                    </div>
                </div>
            </div>
        </>
    ) : null;
};
export default EditUser;
