import React, {useState, useEffect} from "react";
import {Button, ButtonGroup, CircularProgress} from "@material-ui/core";
import {Paper, Table, TableHead, TableRow, TableCell, TableBody} from "@material-ui/core";

import {Icon} from "@material-ui/core";
import {showErrorMessage, showSuccessMessage} from "../../actions/notification";
import BusinessService from "../../services/BusinessService";
import {useDispatch} from "react-redux";
import {setHeaderTitle} from "../../store/actions/HeaderAction";
import {useHistory, useParams} from "react-router";
import ModalAntd from "../../common/antd/component/Modal";
import {ACTIONS, PAGES, LogPageView} from "../../services/LogService";
import {checkPermissionStyle} from "../../services/CognitoService";
import ActionItem from "../../common/action";
import {Pagination} from "antd";
import {loadPaging} from "../../services/AppService";


export default function ListBusiness(props) {
    const dispatch = useDispatch();
    const [rows, setRow] = useState(null);
    const [page, setPage] = useState(1);
    const [limit, setLimit] = useState(10);
    const [total, setTotal] = useState(1);
    const [modalDeleteVisible, setModalDeleteVisible] = useState(null);

    useEffect(() => {
        dispatch(setHeaderTitle(`Business management`));
        list();
    }, [page, limit]);

    const list = () => {
        BusinessService.searchBusiness({...loadPaging(page, limit)}).then((value) => {
            if (value && value.data) {
                LogPageView(PAGES.BUSINESS, ACTIONS.LIST, {...loadPaging(page, limit)});
                setRow(value.data.items);
                if (value.data.totalPage && value.data.totalPage >= 0) {
                    setTotal(value.data.totalPage * limit);
                }
            }
        });
    };

    const onDetail = (row) => {
        localStorage.removeItem("BusinessTab");
        props.history.push(`/Business/Detail/${row.id}?name=${row.name}`, row);
    };

    const onRemoveBusiness = () => {
        BusinessService.removeBusiness(modalDeleteVisible).then(() => {
            list(null, true);
            showSuccessMessage("Deleted Business Successfully!!");
            setModalDeleteVisible(null);
        });
    };

    const renderStatusText = (status) => {
        if (status === "active") {
            return (
                <span
                    className="btn btn-label-success btn-bold btn-sm btn-icon-h"
                    style={{borderRadius: ".42rem"}}>
          Active
        </span>
            );
        } else {
            return (
                <span
                    className="btn btn-label-warning btn-bold btn-sm btn-icon-h"
                    style={{borderRadius: ".42rem", textTransform: "capitalize"}}>
          Inactive
        </span>
            );
        }
    };

    return (
        <>
            <Button {...checkPermissionStyle('create', 'business-management')} onClick={() => {
                props.history.push(`/Business/Add`);
            }} variant="contained" color="primary">Add Business</Button>
            <div className="row kt-mt-15">
                <div className="col-md-12">
                    <div className="kt-section">
                        <div className="kt-section__content">
                            <Paper>
                                <Table>
                                    <TableHead>
                                        <TableRow>
                                            <TableCell>Image</TableCell>
                                            <TableCell>Name</TableCell>
                                            <TableCell>Company ID</TableCell>
                                            <TableCell>Created date</TableCell>
                                            <TableCell>Status</TableCell>
                                            <TableCell style={{width: 150}}>Action</TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {!(!rows) && rows.length > 0 ? (
                                            rows.map((row, key) => (
                                                <TableRow key={key}>
                                                    <TableCell key={0}>
                                                        <div className={'image-preview-container'}>
                                                            <img style={{width: 50}}
                                                                 className={'image-preview'}
                                                                 src={row.imageUrl}/>
                                                        </div>
                                                    </TableCell>
                                                    <TableCell>{row.name}</TableCell>
                                                    <TableCell>{row.companyId}</TableCell>
                                                    <TableCell>{row.createdAt}</TableCell>
                                                    <TableCell>{renderStatusText(row.status)}</TableCell>

                                                    <TableCell>

                                                        <ActionItem
                                                            icon={'fa fa-pen'} color={"#ffa800"}
                                                            onClick={() => {
                                                                onDetail(row)
                                                            }}
                                                            {...checkPermissionStyle('edit', 'business-management')}

                                                        />
                                                    </TableCell>
                                                </TableRow>
                                            ))
                                        ) : !rows ? (<TableRow>
                                            <TableCell colSpan={10} align="center">
                                                <CircularProgress size={20}/>
                                            </TableCell>
                                        </TableRow>) : (
                                            <TableRow>
                                                <TableCell colSpan={10} align="center">
                                                    No data
                                                </TableCell>
                                            </TableRow>
                                        )}
                                    </TableBody>
                                </Table>
                                <div className={'paging-app'}>
                                    <Pagination showSizeChanger={true} pageSize={limit} current={page} onChange={(pageIndex, pageSize) => {
                                        setPage(pageIndex);
                                        setLimit(pageSize);
                                    }} defaultCurrent={1} total={total}/>
                                </div>
                            </Paper>

                        </div>
                    </div>
                </div>
            </div>

            <ModalAntd
                title="Delete Business"
                visible={!(!modalDeleteVisible)}
                onOk={() => onRemoveBusiness()}
                onCancel={() => setModalDeleteVisible(null)}
                cancelText="Cancel"
                okText="Ok">
                <p>Do you want to delete this Business?</p>
            </ModalAntd>
        </>
    );
}
