import {APP_LOADING, HEADER_TITLE} from "./actionTypes";

export const setHeaderTitle = (value, breadcrumb = []) => {
    document.querySelector("title").innerText = `${value} | Layla CMS`;
    return {
        type: HEADER_TITLE,
        payload: {title: value, breadcrumb},
    };
};

export const setAppLoading=(loading)=>{
    return {
        type: APP_LOADING,
        payload: {loading: loading},
    };
}
