import React, {useState, useEffect} from "react";
import {showErrorMessage, showSuccessMessage} from "../../actions/notification";
import {Form, Card, Col} from "react-bootstrap";
import ButtonLoading from "../../partials/common/ButtonLoading";
import {Modal, Select} from "antd";
import SelectForm from "../../partials/common/SelectForm";
import {useDispatch} from "react-redux";
import {setHeaderTitle} from "../../store/actions/HeaderAction";
import BusinessService from "../../services/BusinessService";
import "./style.css";
import PetService from "../../services/PetService";

const AddBusiness = (props) => {
    const dispatch = useDispatch();
    const [modalCancelVisible, setModalCancelVisible] = useState(false);
    const [data, setData] = useState({});
    const [isLoadSubmit, setLoadSubmit] = useState(false);
    const [types, setTypes] = useState([]);

    useEffect(() => {
        console.log(props.location.state);
        dispatch(setHeaderTitle("Add New Business"));
        setData({
            ...data,
        });
        PetService.search({all: true}).then(value => {
            setTypes(value.data.items);
        })
    }, []);


    const onChangeValue = (key, value) => {
        setData({
            ...data,
            [key]: value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!data.name) {
            return showErrorMessage("Name must not be empty!");
        }
        if (!data.companyId) {
            return showErrorMessage("Company ID must not be empty!");
        }
        if (!data.pets || data.pets.length < 2) {
            return showErrorMessage("Pet Type must contain at least 2 elements!");
        }
        if (!data.imageUrl) {
            return showErrorMessage("Logo must not be empty!");
        }
        BusinessService.createBusiness(data).then(value => {
            if (value) {
                showSuccessMessage("Added Business Successfully!!");
                props.history.goBack();
            }
        });
    };

    const onCancel = () => {
        props.history.goBack();
    };

    const onSelectImage = (elm) => {
        if (elm.files && elm.files[0]) {
            BusinessService.uploadLogoBusiness(elm.files[0]).then(value => {
                if (value && value.errorCode === 200) {
                    const link = value.data.Location;
                    onChangeValue("imageUrl", link);
                }
            });
        }
    };

    return (
        <>

            <div className="row">
                <div className="col-md-12">
                    <div className="kt-section">
                        <Card>
                            <Card.Body>
                                <Form onSubmit={handleSubmit}>
                                    <Form.Row>
                                        <Form.Group as={Col} controlId="formBasicName">
                                            <Form.Label className="starDanger">Name</Form.Label>
                                            <Form.Control
                                                type="text"
                                                maxLength={255}
                                                autoFocus
                                                placeholder="Enter name"
                                                value={data.name || ""}
                                                onChange={(e) => onChangeValue("name", e.target.value)}
                                                onBlur={(e) => onChangeValue("name", e.target.value.trim())}
                                            />
                                        </Form.Group>
                                    </Form.Row>
                                    <Form.Row>
                                        <Form.Group as={Col} controlId="formBasicName">
                                            <Form.Label className="starDanger">Logo</Form.Label>
                                            <Form.Control
                                                className="input-file"
                                                type="file"
                                                autoFocus
                                                onChange={(e) => onSelectImage(e.target)}
                                            />
                                        </Form.Group>
                                        <Form.Group as={Col} controlId="formBasicName">
                                            <Form.Label className="starDanger">Company ID</Form.Label>
                                            <Form.Control
                                                type="text"
                                                maxLength={255}
                                                placeholder="Company ID"
                                                value={data.companyId || ""}
                                                onChange={(e) => onChangeValue("companyId", e.target.value)}
                                                onBlur={(e) => onChangeValue("companyId", e.target.value.trim())}
                                            />
                                        </Form.Group>
                                    </Form.Row>
                                    <Form.Row>
                                        <Form.Group as={Col} controlId="formBasicName">
                                            <Form.Label className="starDanger">Status</Form.Label>
                                            <Select defaultValue={'active'} className={'select-ant'}
                                                    onChange={(e) => onChangeValue("status", e)}>
                                                <Select.Option value={'active'}>Active</Select.Option>
                                                <Select.Option value={'inactive'}>Inactive</Select.Option>
                                            </Select>
                                        </Form.Group>
                                        <Form.Group as={Col} controlId="formBasicName">
                                            <Form.Label className="starDanger">Background Color</Form.Label>
                                            <Form.Control
                                                type="color"
                                                maxLength={255}
                                                placeholder="Background Color"
                                                value={data.backgroundColor || ""}
                                                onChange={(e) => onChangeValue("backgroundColor", e.target.value)}
                                                onBlur={(e) => onChangeValue("backgroundColor", e.target.value.trim())}
                                            />
                                        </Form.Group>
                                    </Form.Row>
                                    <Form.Row>
                                        <Form.Group as={Col} controlId="formBasicName">
                                            <Form.Label className="starDanger">Pet Types</Form.Label>
                                            <Select mode={"multiple"} className={'pet-select'}
                                                    style={{width: '100%', textTransform: "capitalize"}}
                                                    onChange={(e) => onChangeValue("pets", e)}>
                                                {
                                                    types.map((x, ind) => (
                                                        <Select.Option key={ind.toString()}
                                                                       style={{textTransform: "capitalize"}}
                                                                       value={x.name}>{x.name}</Select.Option>

                                                    ))
                                                }
                                            </Select>
                                        </Form.Group>
                                    </Form.Row>

                                    <div className="kt-login__actions">
                                        <button
                                            type="button"
                                            id="cancelBtn"
                                            className="btn btn-secondary btn-elevate kt-login__btn-secondary"
                                            onClick={() => setModalCancelVisible(true)}>
                                            Cancel
                                        </button>

                                        <ButtonLoading className="btn btn-primary ml-2" loading={isLoadSubmit}
                                                       type="submit">
                                            Add New
                                        </ButtonLoading>
                                    </div>
                                </Form>
                            </Card.Body>
                        </Card>
                        <Modal
                            title="Cancel Create Business"
                            visible={modalCancelVisible}
                            onOk={() => onCancel()}
                            onCancel={() => setModalCancelVisible(false)}
                            cancelText="Cancel"
                            okText="Ok">
                            <p>Do you want to cancel and quit ?</p>
                        </Modal>
                    </div>
                </div>
            </div>
        </>
    );
};
export default AddBusiness;
