import {get, del, post, put, upload} from "./HttpService";
import endpoints from "../config/endpoints";

export default class EmojiService {
    static search(dataSearch = {nextToken: "", limit: 10, query: {}}, businessId = null) {
        return get((businessId ? endpoints.emojiManagement.listByBiz.replace(':ID', businessId) : endpoints.emojiManagement.list), dataSearch).then(value => {
            return value ? value.data : null;
        });
    }

    static remove(id) {
        return del(endpoints.emojiManagement.delete.replace(":ID", id)).then(value => {
            return value ? value.data : null;
        });
    }

    static get(id) {
        return get(endpoints.emojiManagement.get.replace(":ID", id)).then(value => {
            return value ? value.data : null;
        });
    }

    static update(id, item) {
        return put(endpoints.emojiManagement.update.replace(":ID", id), item).then(value => {
            return value ? value.data : null;
        });
    }

    static create(item) {
        return post(endpoints.emojiManagement.create, item).then(value => {
            return value ? value.data : null;
        });
    }

    static upload(file) {
        var formdata = new FormData();
        formdata.append("files", file);
        formdata.append("entity", 'emoji');
        return upload(endpoints.uploadImageManagement.upload, formdata).then(value => {
            return value ? value.data : null;
        });
    }
}
