import React, {useEffect, useState} from "react";
import {Button, CircularProgress, Icon, Table, TableBody, TableCell, TableHead, TableRow} from "@material-ui/core";
import {showErrorMessage, showSuccessMessage} from "../../actions/notification";
import {useDispatch} from "react-redux";
import {setAppLoading, setHeaderTitle} from "../../store/actions/HeaderAction";
import {useParams} from "react-router";
import ModalAntd from "../../common/antd/component/Modal";
import ModuleService from "../../services/ModuleService";
import {Checkbox, Form, Input, Pagination, Popconfirm, Select} from "antd";
import BusinessService from "../../services/BusinessService";
import {getChanged, getNameById, loadPaging} from "../../services/AppService";
import {Col, Row} from "react-bootstrap";
import CampaignService from "../../services/CampaignService";
import {MinusCircleOutlined, MinusSquareOutlined, PlusOutlined, PlusSquareOutlined} from "@ant-design/icons";
import SubModuleService from "../../services/SubModuleService";
import ActionItem from "../../common/action";
import {useLocation} from "react-router-dom";
import AccessCodeService from "../../services/AccessCodeService";
import EmojiService from "../../services/EmojiService";
import {ImageUpload} from "../../common/upload/single";
import {checkPermission, checkPermissionStyle, checkPermissionTextView} from "../../services/CognitoService";
import FieldService from "../../services/FieldService";

export default function ListModuleBiz(props) {
    const dispatch = useDispatch();
    const params = useParams();
    const [rows, setRow] = useState([]);
    const [modules, setModules] = useState([]);
    const [codes, setCodes] = useState([]);
    const [page, setPage] = useState(1);
    const [limit, setLimit] = useState(10);
    const [total, setTotal] = useState(1);
    const [modalDeleteVisible, setModalDeleteVisible] = useState(null);
    const [modalDeleteCampaign, setModalDeleteCampaign] = useState(null);
    const [modalAddVisible, setModalAddVisible] = useState(null);
    const [modalExport, setModalExport] = useState(null);
    const [modalAddCampaign, setModalAddCampaign] = useState(null);
    const [loading, setLoading] = useState(true);
    const businessId = props.business;
    const [form] = Form.useForm();
    const [form2] = Form.useForm();
    const [selectedRow, setSelectedRow] = useState();
    const [selectedCampaign, setSelectedCampaign] = useState();
    const [showRows, setShowRows] = useState({});
    const [hideModules, setHideModules] = useState([]);
    const [fields, setFields] = useState([]);
    const [hideAccessCode, setHideAccessCode] = useState([]);
    const [hideFields, setHideFields] = useState([]);
    const [hideCampaignModules, setHideCampaignModules] = useState([]);
    const [submodules, setSubmodules] = useState([]);
    const [showRowsExport, setShowRowsExport] = useState({});
    const query = new URLSearchParams(useLocation().search);
    const [confirmLoading, setConfirmLoading] = React.useState(false);

    useEffect(() => {
        dispatch(setHeaderTitle(`Campaign management`, [{
            title: 'Business management',
            page: 'Business/List'
        }, {title: query.get('name'), page: ''}]));
        initData().then();
    }, [params]);

    useEffect(() => {
        if (modalAddVisible) {
            dispatch(setHeaderTitle(`${selectedRow ? checkPermissionTextView('edit', 'biz-campaign') : 'Add'} Module`, [{
                title: 'Business management',
                page: 'Business/List'
            }, {title: query.get('name'), page: ''}]));
        } else {
            dispatch(setHeaderTitle(`Campaign management`, [{
                title: 'Business management',
                page: 'Business/List'
            }, {title: query.get('name'), page: ''}]));
        }
    }, [modalAddVisible]);

    useEffect(() => {
        list();
    }, [page, limit]);

    const list = () => {
        setLoading(true);
        CampaignService.search({...loadPaging(page, limit), businessId}).then((value) => {
            if (value && value.data) {
                setRow(value.data.items ? value.data.items : []);
                const cache = {};
                value.data.items.map(x => {
                    cache[x.id] = null;
                });
                setShowRows(cache);

                value.data.items.map(x => {
                    listModule(x.id);
                });
                if (value.data.totalPage && value.data.totalPage >= 0) {
                    setTotal(value.data.totalPage * limit);
                }
            }
        }).finally(() => {
            setLoading(false)
        });
    };

    const listModule = (campaignId) => {
        CampaignService.searchModule({all: true}, campaignId).then((value) => {
            if (value && value.data) {
                setShowRows(rs => {
                    const showRowsCache = {...rs};
                    let values = (value.data.items ? value.data.items : []);
                    let result = [];
                    values.map(x => {
                        const findIndex = result.findIndex(y => y.urlKey === x.urlKey);
                        if (findIndex >= 0) {
                            result[findIndex].modules.push(x);
                        } else {
                            result.push({...x, modules: [x]});
                        }
                    });

                    showRowsCache[campaignId] = result;
                    return showRowsCache;
                });

            }
        }).finally(() => {
            setLoading(false)
        });
    }

    const initData = async () => {
        ModuleService.search({all: true}).then(value => {
            setModules(value.data.items.sort((a, b) => {
                return a.name.localeCompare(b.name);
            }));
        })
        AccessCodeService.search({all: true, businessId: businessId}).then(value => {
            setCodes(value.data.items.sort((a, b) => {
                return a.name.localeCompare(b.name);
            }));
        });
        FieldService.searchField({all: true, includeCommondField: true}, businessId).then(value => {
            setFields([...(value.data.commonFields || []).map(x => {
                x.isCommon = true;
                return x;
            }), ...(value.data.optionalFields.items || [])].sort((a, b) => {
                return a.name.localeCompare(b.name);
            }));
        });
    };

    const updateSubmoduleList = async (campaignId) => {
        CampaignService.searchModule({all: true}, campaignId).then(value => {
            setHideModules(value.data.items.map(x => x.moduleId));
        })
    };

    const onAddModule = () => {
        const da = form.getFieldsValue();
        da.campaign = selectedCampaign.id;
        da.offLogoBusiness = !da.offLogoBusiness;
        da.hiddenDescChooseIdentity = !da.hiddenDescChooseIdentity;
        da.hideButtons = !da.hideButtons;
        da.hiddenDescLogin = !da.hiddenDescLogin;
        da.urlKey = (da.urlKey || '').split(' ').join('');
        da.modules = da.modules.filter(x => x.moduleId);

        if (!da.welcomeText) {
            delete da.welcomeText
        }
        if (!da.anonymousButtonText) {
            delete da.anonymousButtonText
        }
        if (!da.identityButtonText) {
            delete da.identityButtonText
        }

        if (da.identity === 'login') {
            da.isRequireIdentity = true;
            da.isRequireAnonymous = false;
        }
        if (da.identity === 'anonymous') {
            da.isRequireIdentity = false;
            da.isRequireAnonymous = true;
        }
        if (da.identity === 'all') {
            da.isRequireIdentity = true;
            da.isRequireAnonymous = true;
        }

        if (!da.modules || da.modules.length <= 0) {
            showErrorMessage("Module must not be empty!");
            return;
        }
        if (!da.urlKey) {
            showErrorMessage("Url Key must not be empty!");
            return;
        }

        setConfirmLoading(true);
        if (selectedRow) {

            if (da.urlKey === selectedRow.urlKey) {
                delete da.urlKey;
            }
            if (da.displayName === selectedRow.displayName) {
                delete da.displayName;
            }
            BusinessService.updateModule(businessId, {
                ...getChanged(selectedRow, da),
                currentUrlKey: selectedRow.urlKey,
                welcomeText: da.welcomeText,
                identityButtonText: da.identityButtonText,
                anonymousButtonText: da.anonymousButtonText,
                hideButtons: da.hideButtons,
                centerImageUrl: da.centerImageUrl || undefined,
                rightImageUrl: da.rightImageUrl || undefined,
                leftImageUrl: da.leftImageUrl || undefined,
            }).then(value => {
                if (value) {
                    showSuccessMessage("Update Module Successfully!!");
                    listModule(da.campaign);
                    setModalAddVisible(false);
                }
            }).finally(() => {
                setConfirmLoading(false);
            });
        } else {
            const data = {
                ...da,
                centerImageUrl: da.centerImageUrl || undefined,
                rightImageUrl: da.rightImageUrl || undefined,
                leftImageUrl: da.leftImageUrl || undefined
            }
