            da.name = (da.name || '').trim();
            console.log(da.optionalFields);
            da.commonFields = (da.optionalFields || []).filter(x => x.optionalFieldId && fields.find(y => y.id === x.optionalFieldId && y.isCommon)).map(x => {
                x.commonFieldId = x.optionalFieldId.toString();
                delete x.optionalFieldId;
                return {
                    commonFieldId: x.commonFieldId,
                    isRequire: x.isRequire
                };
