                setConfirmLoading(false);
            });
        } else {
            const da = form2.getFieldsValue();
            da.name = (da.name || '').trim();
            da.commonFields = (da.optionalFields || []).filter(x => x.optionalFieldId && fields.find(y => y.id === x.optionalFieldId && y.isCommon)).map(x => {
                x.commonFieldId = x.optionalFieldId.toString();
                delete x.optionalFieldId;
                return {
                    commonFieldId: x.commonFieldId,
                    isRequire: x.isRequire
                };
            });
            da.optionalFields = (da.optionalFields || []).filter(x => x.optionalFieldId && !fields.find(y => y.id === x.optionalFieldId && y.isCommon)).map(x => {
                delete x.commonFieldId;
                return {
                    optionalFieldId: x.optionalFieldId,
                    isRequire: x.isRequire
                };
            });

            if (!da.name) {
                setConfirmLoading(false);
                showErrorMessage("Name must not be empty!");
                return;
            }
            CampaignService.create({
