        setSubmodules(null);
        let values = [];
        for (let i = 0; i < moduleIds.length; i++) {
            const value = await SubModuleService.search({all: true, moduleId: moduleIds[i]});
            if (value && value.data) {
                values = [...values, ...(value.data.items.map(x => {
                    return {
                        ...x,
                        campaignId,
                        moduleId: moduleIds[i],
                        hasQuestion: x.items.filter(x => x.itemType === 'question').length > 0,
                        hasExportContent: true
                    }
                }))]
            }
        }
        setSubmodules(values);
    }

