    const exportData = (rowValue, hasQuestion, isModuleOnly = false, byCamp = false, userCompleted = false) => {
        if (!hasQuestion) {
            showErrorMessage('No data to export');
            return;
        }
        const hasExportContent = submodules.find(i => i.moduleId === rowValue.moduleId)?.hasExportContent;
        const onResult = (value) => {
            if (value && value.data) {
                if (!value.data.Location) {
                    showSuccessMessage("No data export!!");
                } else {
                    window.open(value.data.Location, '_blank');
                }
            }
        }
        dispatch(setAppLoading(true));
        if (byCamp) {
            SubModuleService.exportSocket({
                "moduleId": rowValue.moduleId,
                "businessId": businessId,
                "action": "requestExport",
                "campaignId": rowValue.campaignId,
                "submoduleId": !isModuleOnly ? rowValue.id : undefined,
                "userCompleted": userCompleted ? userCompleted : undefined,
                hasContent: hasExportContent
            }).then(onResult).finally(() => {
                dispatch(setAppLoading(false));
            });
        } else {
            SubModuleService.exportSocket({
                "action": "requestExport",
                "moduleId": rowValue.moduleId,
                "businessId": businessId,
                "submoduleId": !isModuleOnly ? rowValue.id : undefined,
