                                    <Col>
                                        <label>Welcome Text</label>
                                        <Form.Item initialValue={'Your personal companion for living healthier'}
                                                   name={'welcomeText'}>
                                            <Input.TextArea rows={3} size={"large"}/>
                                        </Form.Item>
                                    </Col>
                                </Row>

                                <Row>
                                    <Col>
                                        <label>Anonymous Button Text</label>
                                        <Form.Item initialValue={'Anonymous'} name={'anonymousButtonText'}>
                                            <Input size={"large"}/>
                                        </Form.Item>
                                    </Col>
                                    <Col>
                                        <label>Identity Button Text</label>
                                        <Form.Item initialValue={'Use my real identity'} name={'identityButtonText'}>
                                            <Input size={"large"}/>
                                        </Form.Item>
                                    </Col>
                                    <Col>
                                        <label>Status</label>
                                        <Form.Item name={'status'}>
                                            <Select>
                                                <Select.Option value={'active'}>Active</Select.Option>
                                                <Select.Option value={'inactive'}>Inactive</Select.Option>
                                            </Select>
                                        </Form.Item>
                                    </Col>
                                </Row>

                                <Row>
                                    <Col>
                                        <Form.Item className={'hiddenDescLogin'} valuePropName="checked"
                                                   name={'hiddenDescLogin'}
                                                   label={'Login Description'}>
                                            <Checkbox/>
                                        </Form.Item>
                                    </Col>
                                    <Col>
                                        <Form.Item className={'hiddenDescChooseIdentity'} valuePropName="checked"
                                                   name={'hiddenDescChooseIdentity'}
                                                   label={'Landing page Description'}>
                                            <Checkbox/>
                                        </Form.Item>
                                    </Col>
                                    <Col>
                                        <Form.Item className={'isRequireIdentity'} valuePropName="checked"
                                                   name={'offLogoBusiness'}
                                                   label={'DA Logo'}>
                                            <Checkbox/>
                                        </Form.Item>
                                    </Col>
                                    <Col>
                                        <Form.Item className={'hideButtons'} valuePropName="checked"
                                                   name={'hideButtons'}
                                                   label={'Landing page button'}>
                                            <Checkbox/>
                                        </Form.Item>
                                    </Col>
                                </Row>

                                <Row>
                                    <Col>
                                        <label>Left Image</label>
                                        <Form.Item name={'leftImageUrl'}>
                                            <ImageUpload
                                                methodUploadImage={EmojiService.upload}
                                                type={'image'}/>
                                        </Form.Item>
                                    </Col>
                                    <Col>
                                        <label>Center Image</label>
                                        <Form.Item name={'centerImageUrl'}>
                                            <ImageUpload
                                                methodUploadImage={EmojiService.upload}
                                                type={'image'}/>
                                        </Form.Item>
                                    </Col>
                                    <Col>
                                        <label>Right Image</label>
                                        <Form.Item name={'rightImageUrl'}>
                                            <ImageUpload
                                                methodUploadImage={EmojiService.upload}
                                                type={'image'}/>
                                        </Form.Item>
                                    </Col>
                                </Row>

                            </Form>
                            <div className="kt-login__actions">
                                <button
                                    type="button"
                                    className="btn btn-secondary btn-elevate kt-login__btn-secondary"
                                    onClick={() => setModalAddVisible(false)}>
                                    Cancel
                                </button>
                                <button onClick={() => onAddModule()} className="btn btn-primary ml-2" type="button">
                                    {selectedRow ? (checkPermission('edit', 'biz-campaign') ? 'Update' : '') : 'Add'}
                                </button>
                            </div>
                        </div>
                    </div>
                ) : null
            }


            <ModalAntd
                title={`${selectedRow ? checkPermissionTextView('edit', 'biz-campaign') : 'Add'} Campaign`}
                visible={!(!modalAddCampaign)}
                onOk={() => onAddCampaign()}
                onCancel={() => setModalAddCampaign(false)}
                confirmLoading={confirmLoading}
                cancelText="Cancel"
                okText={selectedRow ? (checkPermission('edit', 'biz-campaign') ? 'Update' : '') : 'Ok'}>
                <Form form={form2}>
                    <label>Name</label>
                    <Form.Item name={'name'}>
                        <Input onKeyPress={event => {
                            if (event.key === 'Enter') {
                                onAddCampaign();
                            }
                        }} size={"large"}/>
                    </Form.Item>

                    <label>Status</label>
                    <Form.Item name={'status'} initialValue={'active'}>
                        <Select>
                            <Select.Option value={'active'}>Active</Select.Option>
                            <Select.Option value={'inactive'}>Inactive</Select.Option>
                        </Select>
                    </Form.Item>

                    <label>Additional Field</label>
                    <Form.List name="optionalFields">
                        {(fs, {add, remove}) => (
                            <>
                                {
                                    fs.map(field => (
                                        <div className={'value-row-module'} key={field.fieldKey}>
                                            <div style={{width: '100%'}}>
                                                <label>Option field</label>
                                                <Form.Item name={[field.name, 'optionalFieldId']}>
                                                    <Select
                                                        onChange={(value, option) => {
                                                            let rs = (form2.getFieldsValue().optionalFields || []).map(x => (x || {}).optionalFieldId);
                                                            setHideFields(rs);
                                                        }}
                                                        showSearch
                                                        filterOption={(input, option) =>
                                                            option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                                                        }
                                                    >
                                                        {
                                                            fields.map((value, index) => {
                                                                let rs = [...hideFields];
                                                                return (
                                                                    <Select.Option
                                                                        disabled={!!rs.find(y => y === value.id)}
                                                                        key={index.toString()}
                                                                        value={value.id}>{value.name}</Select.Option>
                                                                )
                                                            })
                                                        }
                                                    </Select>
                                                </Form.Item>
                                            </div>

                                            <div className={'open-view-app'}>
                                                <Form.Item className={'hideButtons'} initialValue={false}
                                                           valuePropName="checked"
                                                           name={[field.name, 'isRequire']}
                                                           label={'Required'}>
                                                    <Checkbox/>
                                                </Form.Item>
                                            </div>
                                            <MinusCircleOutlined style={{marginTop: 18}}
                                                                 onClick={() => {
                                                                     remove(field.name);
                                                                     let rs = (form2.getFieldsValue().optionalFields || []).map(x => (x || {}).optionalFieldId);
                                                                     setHideFields(rs);
                                                                 }}/>

                                        </div>
                                    ))
                                }
                                <Form.Item>
                                    <Button className={'add-module-btn'} type="dashed" onClick={() => add()} block
                                            icon={<PlusOutlined/>}>
                                        Add field
                                    </Button>
                                </Form.Item>
                            </>
                        )}
                    </Form.List>
                </Form>

            </ModalAntd>

            <ModalAntd
                title={`Export response module`}
                visible={modalExport !== null}
                onCancel={() => setModalExport(null)}
                cancelText="Cancel"
                width={840}
                confirmLoading="confirmLoading"
                okText="">

                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Submodule</TableCell>
                            <TableCell>Module</TableCell>
                            <TableCell style={{textAlign: "center"}}>Export</TableCell>
                            <TableCell style={{textAlign: "center"}}>Export by Campaign</TableCell>
                            <TableCell style={{textAlign: "center"}}>Export with Content</TableCell>
                            {/*<TableCell>Export user Completed</TableCell>*/}
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {
                            !(!submodules) ? submodules.map((value, index) => {
                                let moduleRow = (
                                    <TableRow key={index + 0.5}>
                                        <TableCell style={{backgroundColor: '#f5f5f5'}}>
                                            {showRowsExport[value.moduleId] ?
                                                <MinusSquareOutlined style={{cursor: 'pointer'}} onClick={event => {
                                                    window.cacheModule = null;
                                                    setShowRowsExport(prevState => {
                                                        prevState = {...prevState};
                                                        prevState[value.moduleId] = false;
                                                        return prevState;
                                                    })
                                                }}/> :
                                                <PlusSquareOutlined style={{cursor: 'pointer'}} onClick={event => {
                                                    window.cacheModule = null;
                                                    setShowRowsExport(prevState => {
                                                        prevState = {...prevState};
                                                        prevState[value.moduleId] = true;
                                                        return prevState;
                                                    })
                                                }}/>}
                                        </TableCell>
                                        <TableCell style={{backgroundColor: '#f5f5f5'}}
                                                   colSpan={1}>{getNameById(value.moduleId, modules)}</TableCell>
                                        <TableCell style={{backgroundColor: '#f5f5f5', textAlign: "center"}}>
                                            <Icon
                                                className="fa fa-download"
                                                onClick={(e) => exportData(value, true, true)}
                                                style={{
                                                    color: "#000000",
                                                    fontSize: 15,
                                                   // marginLeft: 15,
                                                    cursor: "pointer",
                                                    textAlign: 'center'
                                                }}
                                            />
                                        </TableCell>
                                        <TableCell style={{backgroundColor: '#f5f5f5', textAlign: "center"}}>
                                            <Icon
                                                className="fa fa-download"
                                                onClick={(e) => exportData(value, true, true, true, false)}
                                                style={{
                                                    color: "#000000",
                                                    fontSize: 15,
                                                    // marginLeft: 15,
                                                    cursor: "pointer"
                                                }}
                                            />
                                        </TableCell>
                                        <TableCell style={{backgroundColor: '#f5f5f5', textAlign: "center"}}>
                                            <Checkbox
                                                name={value.moduleId}
                                                defaultChecked={true}
                                                onChange={($event) => onCheckDownLoadWithContent($event.target.checked, value.moduleId)}/>
                                        </TableCell>
                                        {/*<TableCell style={{backgroundColor: '#f5f5f5'}}>*/}
                                        {/*    <Icon*/}
                                        {/*        className="fa fa-download"*/}
                                        {/*        onClick={(e) => exportData(value, true, true, true, true)}*/}
                                        {/*        style={{*/}
                                        {/*            color: "#000000",*/}
                                        {/*            fontSize: 15,*/}
                                        {/*            marginLeft: 15,*/}
                                        {/*            cursor: "pointer"*/}
                                        {/*        }}*/}
                                        {/*    />*/}
                                        {/*</TableCell>*/}
                                    </TableRow>
                                )
                                if (window.cacheModule !== value.moduleId) {
                                    window.cacheModule = value.moduleId;
                                } else {
                                    moduleRow = null;
                                }
                                return (
                                    <>
                                        {moduleRow}
                                        {
                                            showRowsExport[value.moduleId] ? (
                                                <TableRow key={index}>
                                                    <TableCell>{value.name}</TableCell>
                                                    <TableCell>{getNameById(value.moduleId, modules)}</TableCell>
                                                    <TableCell style={{textAlign: "center"}}>
                                                        <Icon
                                                            className="fa fa-download"
                                                            onClick={(e) => exportData(value, value.hasQuestion)}
                                                            style={{
                                                                color: value.hasQuestion ? "#000000" : '#929292',
                                                                fontSize: 15,
                                                               // marginLeft: 15,
                                                                cursor: "pointer"
                                                            }}
                                                        />
                                                    </TableCell>
                                                    <TableCell style={{textAlign: "center"}}>
                                                        <Icon
                                                            className="fa fa-download"
                                                            onClick={(e) => exportData(value, value.hasQuestion, false, true)}
                                                            style={{
                                                                color: value.hasQuestion ? "#000000" : '#929292',
                                                                fontSize: 15,
                                                                // marginLeft: 15,
                                                                cursor: "pointer"
                                                            }}
                                                        />
                                                    </TableCell>
                                                    {/*<TableCell>*/}
                                                    {/*    <Icon*/}
                                                    {/*        className="fa fa-download"*/}
                                                    {/*        onClick={(e) => exportData(value, value.hasQuestion, false, true, true)}*/}
                                                    {/*        style={{*/}
                                                    {/*            color: value.hasQuestion ? "#000000" : '#929292',*/}
                                                    {/*            fontSize: 15,*/}
                                                    {/*            marginLeft: 15,*/}
                                                    {/*            cursor: "pointer"*/}
                                                    {/*        }}*/}
                                                    {/*    />*/}
                                                    {/*</TableCell>*/}
                                                </TableRow>
                                            ) : null
                                        }
                                    </>
                                )
                            }) : <TableRow>
                                <TableCell colSpan={4} align="center">
                                    <CircularProgress size={20}/>
                                </TableCell>
                            </TableRow>
                        }
                    </TableBody>
                </Table>
            </ModalAntd>
        </>
    );
}
