import React, {useState, useEffect} from "react";
import {Button, ButtonGroup, CircularProgress} from "@material-ui/core";
import {Paper, Table, TableHead, TableRow, TableCell, TableBody} from "@material-ui/core";

import {useDispatch} from "react-redux";
import {setHeaderTitle} from "../../store/actions/HeaderAction";
import {useHistory, useParams} from "react-router";
import ModalAntd from "../../common/antd/component/Modal";
import {Checkbox, DatePicker, Form, Input, InputNumber, Select, Row, Col} from "antd";
import {useLocation} from "react-router-dom";
import "./style.css";


export default function AccessCodeDetail(props) {
    const dispatch = useDispatch();
    const params = useParams();
    const [rows, setRow] = useState([]);
    const [stackPage, setStackPage] = useState([]);
    const [page, setPage] = useState(0);
    const [rowsPerPage, setRowsPerPage] = useState(10);
    const [modalDeleteVisible, setModalDeleteVisible] = useState(null);
    const [modalAdd, setModalAdd] = useState(null);
    const [loading, setLoading] = useState(true);
    const businessId = props.business;
    const [form2] = Form.useForm();
    const [selectedRow, setSelectedRow] = useState();
    const [type, setType] = useState('fixed');
    const query = new URLSearchParams(useLocation().search);
    const [confirmLoading, setConfirmLoading] = React.useState(false);

    useEffect(() => {
        dispatch(setHeaderTitle(`Access Code`, [{
            title: 'Business management',
            page: 'Business/List'
        }, {title: query.get('name'), page: ''}]));
        setPage(0);
        list(null, true);
    }, [params]);

    const list = (nextToken, isReset = false) => {
        setLoading(true);
    };


    const nextStackPage = () => {
        const nextToken = stackPage[page] || null;
        list(nextToken);
        setPage(page + 1);
    };

    const prevStackPage = async () => {
        if (page >= 1) {
            const nextToken = stackPage[page - 2] || null;
            list(nextToken);
            setPage(page - 1);
        }
    };


    const renderStatusText = (status) => {
        if (status === "active") {
            return (
                <span
                    className="btn btn-label-success btn-bold btn-sm btn-icon-h"
                    style={{borderRadius: ".42rem"}}>
          Active
        </span>
            );
        } else {
            return (
                <span
                    className="btn btn-label-warning btn-bold btn-sm btn-icon-h"
                    style={{borderRadius: ".42rem", textTransform: "capitalize"}}>
          Inactive
        </span>
            );
        }
    };

    const onAddCampaign = () => {

    }

    return (
        <>
            <Button onClick={() => {
                setModalAdd(true)
            }} variant="contained" color="primary">Add Access Code</Button>
            <div className="row kt-mt-15 ">
                <div className="col-md-12">
                    <div className="kt-section">
                        <div className="kt-section__content">
                            <Paper>
                                <Table>
                                    <TableHead>
                                        <TableRow>
                                            <TableCell>Name</TableCell>
                                            <TableCell>Type</TableCell>
                                            <TableCell>Code</TableCell>
                                            <TableCell>Quantity</TableCell>
                                            <TableCell>Start Date</TableCell>
                                            <TableCell>End Date</TableCell>
                                            <TableCell>Redemption Limit</TableCell>
                                            <TableCell>Status</TableCell>
                                            <TableCell style={{width: 150, textAlign: 'right'}}>Action</TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {
                                            loading ? (
                                                <TableRow>
                                                    <TableCell colSpan={10} align="center">
                                                        <CircularProgress size={20}/>
                                                    </TableCell>
                                                </TableRow>
                                            ) : null
                                        }
                                        {rows.map((value, index) => (
                                            <></>
                                        ))}


                                    </TableBody>
                                </Table>
                            </Paper>
                            <div className="customSelector custom-svg text-center kt-margin-t-10">
                                <ButtonGroup variant="contained" color="primary"
                                             aria-label="contained primary button group">
                                    <Button onClick={() => prevStackPage()}
                                            disabled={!stackPage[page - 1]}>Prev</Button>
                                    <Button onClick={() => nextStackPage()} disabled={!stackPage[page]}>Next</Button>
                                </ButtonGroup>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

            <ModalAntd
                title="Delete Module"
                visible={!(!modalDeleteVisible)}
                onOk={() => onRemoveModule()}
                onCancel={() => setModalDeleteVisible(null)}
                cancelText="Cancel"
                okText="Ok">
                <p>Do you want to delete this Module?</p>
            </ModalAntd>


            <ModalAntd
                title={`${selectedRow ? 'Update' : 'Add'} Access Code`}
                visible={!(!modalAdd)}
                onOk={() => {
                    return onAddCampaign();
                }}
                onCancel={() => setModalAdd(false)}
                confirmLoading={confirmLoading}
                cancelText="Cancel"
                okText="Ok">
                <Form className={'access-code'} layout={'vertical'} {...{
                    labelCol: {span: 24},
                    wrapperCol: {span: 24},
                }} form={form2}>
                    <Row>
                        <Col span={11}>
                            <Form.Item label={'Name'} name={'name'}>
                                <Input size={"large"}/>
                            </Form.Item>
                        </Col>
                        <Col span={2}/>
                        <Col span={11}>
                            <Form.Item label={'Type'} name={'type'} initialValue={'fixed'}>
                                <Select onChange={value => setType(value)} style={{width: '100%'}}>
                                    <Select.Option value={'fixed'}>Fixed</Select.Option>
                                    <Select.Option value={'prefix'}>Prefix</Select.Option>
                                    <Select.Option value={'random'}>Random</Select.Option>
                                </Select>
                            </Form.Item>
                        </Col>
                    </Row>
                    <Row>
                        {
                            type === 'prefix' ? (
                                <>
                                    <Col span={11}>
                                        <Form.Item label={'Prefix'} name={'Prefix'}>
                                            <Input size={"large"}/>
                                        </Form.Item>
                                    </Col>
                                    <Col span={2}/>
                                </>
                            ) : null
                        }
                        <Col span={type === 'prefix' ? 11 : 24}>
                            <Form.Item label={'Quantity'} name={'Quantity'}>
                                <InputNumber style={{width: '100%'}} size={"large"}/>
                            </Form.Item>
                        </Col>
                    </Row>
                    <Row>
                        <Col span={type === 'fixed' ? 11 : 24}>
                            <Form.Item label={'Redemption Limit'} name={'Quanctity'}>
                                <InputNumber style={{width: '100%'}} size={"large"}/>
                            </Form.Item>
                        </Col>
                        {
                            type === 'fixed' ? (
                                <>
                                    <Col span={2}/>
                                    <Col span={11}>
                                        <Form.Item label={'Code'} name={'Prefix'}>
                                            <Input size={"large"}/>
                                        </Form.Item>
                                    </Col>
                                </>
                            ) : null
                        }
                    </Row>
                    <Form.Item label={'Date Applied'} name={'daterang'}>
                        <DatePicker.RangePicker size={"large"} style={{width: '100%'}}/>
                    </Form.Item>
                    <Form.Item label={'Status'} name={'status'} initialValue={'active'}>
                        <Select style={{width: '100%'}}>
                            <Select.Option value={'active'}>Active</Select.Option>
                            <Select.Option value={'inactive'}>Inactive</Select.Option>
                        </Select>
                    </Form.Item>
                </Form>

            </ModalAntd>

        </>
    );
}
