import React, {useEffect, useState} from "react";
import {PlusOutlined} from "@ant-design/icons";
import ExerciseService from "../../services/ExerciseService";
import {Modal, Upload} from "antd";

function getBase64(file) {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => resolve(reader.result);
    reader.onerror = error => reject(error);
  });
}

export function ImagesUpload(props) {
  const type = props.type;
  const methodUploadImage = props.methodUploadImage;
  const methodUploadVideo = props.methodUploadVideo;
  const [previewVisible, setPreviewVisible] = useState(false)
  const [previewImage, setPreviewImage] = useState('')
  const [previewTitle, setPreviewTitle] = useState('')
  const [fileList, setFileList] = useState(props.value ? props.value.map(x => {
    return {
      id: x,
      url: x,
      status: 'done'
    }
  }) : [])

  useEffect(() => {
    props.onChange(fileList.map((x) => {
      if (x.url) {
        return x.url;
      } else {
        return x.response?.url || null;
      }
    }));
  }, [fileList]);

  const handleCancel = () => setPreviewVisible(false);

  const handlePreview = async (file) => {
    if (!file.url && !file.preview) {
      file.preview = await getBase64(file.originFileObj);
    }
    setPreviewImage(file.url || file.preview);
    setPreviewVisible(true);
    setPreviewTitle(file.name || file.url.substring(file.url.lastIndexOf('/') + 1))
  };

  const handleChange = (fl) => setFileList(fl.fileList);

  const uploadButton = (
    <div>
      <PlusOutlined/>
      <div style={{marginTop: 8}}>Upload</div>
    </div>
  );

  const beforeUpload = (file) => {
    return true;
  }
  const onUploadImage = ({onSuccess, onError, file}) => {
    if (type === 'image') {
      methodUploadImage(file).then(value => {
        if (value) {
          onSuccess({
            uid: value.data.key,
            status: 'done',
            url: value.data.Location
          });
        } else {
          onError({});
        }
      })
    } else {
      methodUploadVideo(file).then(value => {
        if (value) {
          onSuccess({
            uid: value.data.key,
            status: 'done',
            url: value.data.Location,
            duration: value.data.duration
          });
        } else {
          onError({});
        }
      })
    }
  }


  return (
    <>
      <Upload beforeUpload={beforeUpload}
              customRequest={options => onUploadImage(options)}
              listType="picture-card"
              fileList={fileList} onChange={handleChange}>
        {fileList.length >= 8 ? null : uploadButton}
      </Upload>
      <Modal visible={previewVisible} title={previewTitle} footer={null} onCancel={handleCancel}>
        <img alt="example" style={{width: '100%'}} src={previewImage}/>
      </Modal>
    </>
  );
}
