import React, {useState, useEffect} from "react";
import {Button, ButtonGroup, CircularProgress, Icon} from "@material-ui/core";
import {Paper, Table, TableHead, TableRow, TableCell, TableBody} from "@material-ui/core";

import {useDispatch} from "react-redux";
import {setHeaderTitle} from "../../store/actions/HeaderAction";
import {useHistory, useParams} from "react-router";
import ModalAntd from "../../common/antd/component/Modal";
import {Checkbox, DatePicker, Form, Input, InputNumber, Select, Row, Col, Pagination} from "antd";
import {useLocation} from "react-router-dom";
import "./style.css";
import CampaignService from "../../services/CampaignService";
import AccessCodeService from "../../services/AccessCodeService";
import {showErrorMessage, showSuccessMessage} from "../../actions/notification";
import {getChanged, getNameById, loadPaging} from "../../services/AppService";
import moment from "moment";
import ActionItem from "../../common/action";
import {checkPermission, checkPermissionStyle, checkPermissionTextView} from "../../services/CognitoService";

const dateFormat = 'YYYY-MM-DD';

export default function AccessCodeDetail(props) {
    const dispatch = useDispatch();
    const params = useParams();
    const history = useHistory();
    const [rows, setRow] = useState([]);
    const [page, setPage] = useState(1);
    const [limit, setLimit] = useState(10);
    const [total, setTotal] = useState(1);
    const [stackPageCode, setStackPageCode] = useState([]);
    const [pageCode, setPageCode] = useState(0);
    const [rowsPerPage, setRowsPerPage] = useState(10);
    const [modalDeleteVisible, setModalDeleteVisible] = useState(null);
    const [modalAdd, setModalAdd] = useState(null);
    const [loading, setLoading] = useState(true);
    const businessId = props.business;
    const [form2] = Form.useForm();
    const [selected, setSelected] = useState();
    const [codes, setCodes] = useState();
    const [type, setType] = useState('fix');
    const query = new URLSearchParams(useLocation().search);
    const [confirmLoading, setConfirmLoading] = React.useState(false);
    const [modalExport, setModalExport] = useState(null);

    useEffect(() => {
        dispatch(setHeaderTitle(`Access Code`, [{
            title: 'Business management',
            page: 'Business/List'
        }, {title: query.get('name'), page: ''}]));
    }, []);

    useEffect(() => {
        list();
    }, [page, limit]);

    const list = () => {
        setLoading(true);
        AccessCodeService.search({...(loadPaging(page, limit)), businessId}).then((value) => {
            if (value && value.data) {
                setRow(value.data.items);
                if (value.data.totalPage && value.data.totalPage >= 0) {
                    setTotal(value.data.totalPage * limit);
                }
            }
        }).finally(() => {
            setLoading(false)
        });
    };


    const nextStackPageCode = () => {
        const nextToken = stackPageCode[pageCode] || null;
        getCodes(selected.id, nextToken);
        setPageCode(pageCode + 1);
    };

    const prevStackPageCode = async () => {
        if (pageCode >= 1) {
            const nextToken = stackPageCode[pageCode - 2] || null;
            getCodes(selected.id, nextToken);
            setPageCode(pageCode - 1);
        }
    };


    const renderStatusText = (status) => {
        if (status === "active") {
            return (
                <span
                    className="btn btn-label-success btn-bold btn-sm btn-icon-h"
                    style={{borderRadius: ".42rem"}}>
          Active
        </span>
            );
        } else {
            return (
                <span
                    className="btn btn-label-warning btn-bold btn-sm btn-icon-h"
                    style={{borderRadius: ".42rem", textTransform: "capitalize"}}>
          Inactive
        </span>
            );
        }
    };
    const onlyLetters = (str) => {
        return (str || '').match("^[A-Za-z0-9]+$");
    }

    const onAdd = () => {
        const da = form2.getFieldsValue();
        setConfirmLoading(true);
        da.name = (da.name || '').trim();

        if (!da.name) {
            setConfirmLoading(false);
            showErrorMessage("Name must not be empty!");
            return;
        }
        if (!da.daterange) {
            setConfirmLoading(false);
            showErrorMessage("Start date & end date must not be empty!");
            return;
        }
        if (da.type === 'prefix' && !(da.prefix || '').trim()) {
            setConfirmLoading(false);
            showErrorMessage("Prefix must not be empty!");
            return;
        }
        if (da.type === 'prefix' && !onlyLetters(da.prefix)) {
            setConfirmLoading(false);
            showErrorMessage("Prefix only allows letters and numbers");
            return;
        }
        if (da.type === 'prefix' && da.prefix.length > 5) {
            setConfirmLoading(false);
            showErrorMessage("Prefix must have length less than or equal to 5");
            return;
        }
        if (da.type === 'fix' && !(da.code || '').trim()) {
            setConfirmLoading(false);
            showErrorMessage("Code must not be empty!");
            return;
        }
        if (da.type === 'fix' && !onlyLetters(da.code)) {
            setConfirmLoading(false);
            showErrorMessage("Code only allows letters and numbers");
            return;
        }
        if (da.type === 'fix' && (da.code).length < 5) {
            setConfirmLoading(false);
            showErrorMessage("Code must be longer than or equal to 5 characters");
            return;
        }
        da.startDate = moment(da.daterange[0]).format('MM/DD/YYYY');
        da.endDate = moment(da.daterange[1]).format('MM/DD/YYYY');
        delete da.daterange;
        if (selected) {
            AccessCodeService.update(selected.id, getChanged(selected, da)).then(value => {
                if (value) {
                    showSuccessMessage("Update Access Code Successfully!!");
                    setPage(1);
                    setModalAdd(false);
                    list();
                }
            }).finally(() => {

                setConfirmLoading(false);
            });
        } else {
            AccessCodeService.create({
                ...da,
                businessId
            }).then(value => {
                if (value) {
                    showSuccessMessage("Add Access Code Successfully!!");
                    setPage(1);
                    setModalAdd(false);
                    list();
                }
            }).finally(() => {
                // setModalAdd(false);
                setConfirmLoading(false);
            });
        }
    }

    const onRemove = () => {
        AccessCodeService.remove(modalDeleteVisible.id).then((value) => {
            if (value && value.message === 'Success') {
                showSuccessMessage("Deleted Access Code Successfully!!");
                setModalDeleteVisible(null);
            }
            setPage(1);
            list();
        });
    }
    const getCodes = (id, nextToken, isReset = false) => {
        if (!id) {
            id = selected.id;
        }

        setCodes(null);
        AccessCodeService.getCodes(id, {nextToken, limit: rowsPerPage}).then((value) => {
            setCodes(value.data.items);
            if (!stackPageCode.find(x => x === value.data.nextToken)) {
                let stackPageNew = isReset ? [] : JSON.parse(JSON.stringify(stackPageCode));
                stackPageNew.push(value.data.nextToken);
                setStackPageCode(stackPageNew);
                if (isReset) {
                    setPageCode(0);
                }
            }
        });
    }

    const onDetail = (value) => {
        history.push(`/Business/Detail/${businessId}/AccessCodeDetail/${value.id}`)
    }

    return (
        <>
            <Button {...checkPermissionStyle('create', 'biz-access-code')}
                    onClick={() => {
                        setSelected(null);
                        form2.resetFields();
                        setType('fix');
                        setModalAdd(true);
                    }} variant="contained" color="primary">Add Access Code</Button>
            <div className="row kt-mt-15 ">
                <div className="col-md-12">
                    <div className="kt-section">
                        <div className="kt-section__content">
                            <Paper>
                                <Table>
                                    <TableHead>
                                        <TableRow>
                                            <TableCell>Name</TableCell>
                                            <TableCell>Type</TableCell>
                                            <TableCell>Code</TableCell>
                                            <TableCell>Quantity</TableCell>
                                            <TableCell>Start Date</TableCell>
                                            <TableCell>End Date</TableCell>
                                            <TableCell>Redemption Limit</TableCell>
                                            <TableCell>Status</TableCell>
                                            <TableCell style={{width: 150, textAlign: 'right'}}>Action</TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {
                                            loading ? (
                                                <TableRow>
                                                    <TableCell colSpan={10} align="center">
                                                        <CircularProgress size={20}/>
                                                    </TableCell>
                                                </TableRow>
                                            ) : null
                                        }
                                        {rows.map((value, index) => (
                                            <>
                                                <TableRow key={index.toString()}>
                                                    <TableCell>
                                                        {value.name}
                                                    </TableCell>
                                                    <TableCell
                                                        style={{textTransform: "capitalize"}}>{value.type}</TableCell>
                                                    <TableCell>{value.type === 'prefix' ? `${value.prefix}----` : value.code}</TableCell>
                                                    <TableCell>{value.type === 'prefix' ? value.quantity : 1}</TableCell>
                                                    <TableCell>{value.startDate}</TableCell>
                                                    <TableCell>{value.endDate}</TableCell>
                                                    <TableCell>{value.redemptionLimit || 'Unlimited'}</TableCell>
                                                    <TableCell>{renderStatusText(value.status)}</TableCell>
                                                    <TableCell style={{width: 150, textAlign: 'right'}}>
                                                        <ActionItem
                                                            icon={'fa fa-eye'} color={"rgb(0,170,122)"}
                                                            onClick={() => {
                                                                setSelected(value);
                                                                getCodes(value.id, null, true);
                                                                setModalExport(true);
                                                            }}/>
                                                        <ActionItem
                                                            icon={'fa fa-pen'}
                                                            color={"#ffa800"}
                                                            onClick={() => {
                                                                setSelected(value);
                                                                form2.setFieldsValue({
                                                                    ...value,
                                                                    daterange: [
                                                                        moment(value.startDate, 'MM/DD/YYYY'),
                                                                        moment(value.endDate, 'MM/DD/YYYY'),
                                                                    ]
                                                                });
                                                                setType(value.type);
                                                                setModalAdd(true);
                                                            }}
                                                            {...checkPermissionStyle('edit', 'biz-access-code')}
                                                        />
                                                        <ActionItem
                                                            {...checkPermissionStyle('delete', 'biz-access-code')}
                                                            icon={'fa fa-trash'}
                                                            color={"rgb(220, 0, 78)"}
                                                            onClick={() => {
                                                                setModalDeleteVisible(value);
                                                            }}/>
                                                    </TableCell>
                                                </TableRow>
                                            </>
                                        ))}


                                    </TableBody>
                                </Table>
                                <div className={'paging-app'}>
                                    <Pagination showSizeChanger={true} pageSize={limit} current={page} onChange={(pageIndex, pageSize) => {
                                        setPage(pageIndex);
                                        setLimit(pageSize);
                                    }} defaultCurrent={1} total={total}/>
                                </div>
                            </Paper>

                        </div>
                    </div>
                </div>
            </div>

            <ModalAntd
                title="Delete Access Code"
                visible={!(!modalDeleteVisible)}
                onOk={() => onRemove()}
                onCancel={() => setModalDeleteVisible(null)}
                cancelText="Cancel"
                okText="Ok">
                <p>Do you want to delete this Access Code?</p>
            </ModalAntd>


            <ModalAntd
                title={`${selected ? checkPermissionTextView('edit', 'biz-access-code') : 'Add'} Access Code`}
                visible={!(!modalAdd)}
                onOk={() => {
                    return onAdd();
                }}
                onCancel={() => setModalAdd(false)}
                confirmLoading={confirmLoading}
                cancelText="Cancel"
                okText={selected ? (checkPermission('edit', 'biz-access-code') ? 'Update' : '') : 'Add'}>
                <Form className={'access-code'} layout={'vertical'} {...{
                    labelCol: {span: 24},
                    wrapperCol: {span: 24},
                }} form={form2}>
                    <Row>
                        <Col span={11}>
                            <Form.Item label={'Name'} name={'name'}>
                                <Input size={"large"}/>
                            </Form.Item>
                        </Col>
                        <Col span={2}/>
                        <Col span={11}>
                            <Form.Item label={'Type'} name={'type'} initialValue={'fix'}>
                                <Select disabled={!(!selected)} onChange={value => setType(value)}
                                        style={{width: '100%'}}>
                                    <Select.Option value={'fix'}>Fix</Select.Option>
                                    <Select.Option value={'prefix'}>Prefix</Select.Option>
                                    {/*<Select.Option value={'random'}>Random</Select.Option>*/}
                                </Select>
                            </Form.Item>
                        </Col>
                    </Row>
                    <Row>
                        {
                            type === 'prefix' ? (
                                <>
                                    <Col span={11}>
                                        <Form.Item label={'Prefix'} name={'prefix'}>
                                            <Input disabled={!(!selected)} size={"large"}/>
                                        </Form.Item>
                                    </Col>
                                    <Col span={2}/>
                                    <Col span={type === 'prefix' ? 11 : 24}>
                                        <Form.Item label={'Quantity'} name={'quantity'}>
                                            <InputNumber
                                                disabled={!(!selected)}
                                                formatter={value => value.replace('.', '').replace(',', '')}
                                                style={{width: '100%'}} size={"large"}/>
                                        </Form.Item>
                                    </Col>
                                </>
                            ) : null
                        }

                    </Row>
                    <Row>
                        <Col span={type === 'fix' ? 11 : 24}>
                            <Form.Item label={'Redemption Limit'} name={'redemptionLimit'}>
                                <InputNumber min={1}
                                             formatter={value => value.replace('.', '').replace(',', '').replace('-', '')}
                                             style={{width: '100%'}} size={"large"}/>
                            </Form.Item>
                        </Col>
                        {
                            type === 'fix' ? (
                                <>
                                    <Col span={2}/>
                                    <Col span={11}>
                                        <Form.Item label={'Code'} name={'code'}>
                                            <Input disabled={!(!selected)} size={"large"}/>
                                        </Form.Item>
                                    </Col>
                                </>
                            ) : null
                        }
                    </Row>
                    <Form.Item label={'Date Applied'} name={'daterange'}>
                        <DatePicker.RangePicker
                            onPanelChange={(e) => console.log(e)}
                            disabledDate={(current) => {
                                return current && current < moment().startOf('day');
                            }}
                            format={dateFormat}
                            min={moment(new Date())}
                            size={"large"}
                            style={{width: '100%'}}
                        />
                    </Form.Item>
                    <Form.Item label={'Status'} name={'status'} initialValue={'active'}>
                        <Select style={{width: '100%'}}>
                            <Select.Option value={'active'}>Active</Select.Option>
                            <Select.Option value={'inactive'}>Inactive</Select.Option>
                        </Select>
                    </Form.Item>
                </Form>

            </ModalAntd>


            <ModalAntd
                title={`Access Code List`}
                visible={!(!modalExport)}
                onCancel={() => setModalExport(false)}
                cancelText="Cancel"
                confirmLoading="confirmLoading"
                okText="">

                <div style={{height: 475}}>
                    <Table>
                        <TableHead>
                            <TableRow>
                                <TableCell>#</TableCell>
                                <TableCell>Code</TableCell>
                                <TableCell style={{textAlign: 'right'}}>Number of use</TableCell>
                                <TableCell style={{textAlign: 'right'}}>Remaining Redemption</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {
                                !(!codes) ? codes.map((value, index) => {
                                    return (
                                        <TableRow key={index.toString()}>
                                            <TableCell>{index + pageCode * rowsPerPage + 1}</TableCell>
                                            <TableCell>{value.code}</TableCell>
                                            <TableCell
                                                style={{textAlign: 'right'}}>{value.used || '0'}</TableCell>
                                            <TableCell
                                                style={{textAlign: 'right'}}>{(typeof value.remaining === 'undefined') ? 'Unlimited' : value.remaining}</TableCell>
                                        </TableRow>
                                    )
                                }) : <TableRow>
                                    <TableCell colSpan={4} align="center">
                                        <CircularProgress size={20}/>
                                    </TableCell>
                                </TableRow>
                            }
                        </TableBody>
                    </Table>
                </div>
                <div className="customSelector custom-svg text-center kt-margin-t-10">
                    <ButtonGroup variant="contained" color="primary"
                                 aria-label="contained primary button group">
                        <Button onClick={() => prevStackPageCode()}
                                disabled={!stackPageCode[pageCode - 1]}>Prev</Button>
                        <Button onClick={() => nextStackPageCode()} disabled={!stackPageCode[pageCode]}>Next</Button>
                    </ButtonGroup>
                </div>
            </ModalAntd>
        </>
    );
}
