import React, {useEffect, useState} from "react";
import {showErrorMessage, showSuccessMessage} from "../../actions/notification";
import {Col, Form} from "react-bootstrap";
import ButtonLoading from "../../partials/common/ButtonLoading";
import {useDispatch} from "react-redux";
import {setHeaderTitle} from "../../store/actions/HeaderAction";
import {Paper} from "@material-ui/core";
import ListUserBusiness from "./users/ListUser";
import "./style.css";
import BusinessService from "../../services/BusinessService";
import {getChanged} from "../../services/AppService";
import {useLocation, useParams} from "react-router-dom";
import {Select, Tabs} from "antd";
import ListEmoji from "../emoji/List";
import ListModuleBiz from "./Module";
import AccessCode from "./AccessCode";
import OptionFieldList from "../field/List";
import FormGenerateList from "../form/List";
import PetService from "../../services/PetService";
import {checkPermission, checkPermissionStyle} from "../../services/CognitoService";

export default function (props) {
    const dispatch = useDispatch();
    const [data, setData] = useState();
    const [dataCache, setDataCache] = useState();
    const [isLoadSubmit, setLoadSubmit] = useState(false);
    const [valueTab, setValueTab] = React.useState(2);
    const [pets, setPets] = React.useState([]);
    let inputFile = null;
    const {id} = useParams();
    const query = new URLSearchParams(useLocation().search);
    const [types, setTypes] = useState([]);


    useEffect(() => {
        dispatch(setHeaderTitle(`Business Detail`, [{
            title: 'Business management',
            page: 'Business/List'
        }, {title: query.get('name'), page: ''}]));
        BusinessService.getBusiness(id).then(value => {
            PetService.search({}, id).then(value2 => {
                value.data.pets = value2 ? value2.data.items.map(x => x.name) : [];
                setPets(value.data.items);
                setDataCache(value.data);
                setData(value.data);
            }).catch(reason => {
                setDataCache(value.data);
                setData(value.data);
            });

        });

        PetService.search({all: true}).then(value => {
            if (value) {
                setTypes(value.data.items);
            }
        })
        console.log(localStorage.getItem("BusinessTab"));
        if (localStorage.getItem("BusinessTab")) {
            setValueTab(parseInt(localStorage.getItem("BusinessTab")));
        }
    }, []);

    const handleSubmit = (e) => {

        e.preventDefault();
        if (!data.name) {
            return showErrorMessage("Name must not be empty!");
        }
        if (!data.companyId) {
            return showErrorMessage("Company ID must not be empty!");
        }
        if (!data.pets || data.pets.length < 2) {
            return showErrorMessage("Pet Type must contain at least 2 elements!");
        }
        const dataChanged = getChanged(dataCache, data);
        BusinessService.updateBusiness(id, dataChanged).then(value => {
            if (value) {
                showSuccessMessage("Updated Business Successfully!!");
                props.location.state = data;
            }
        });
    };

    const canCancel = () => {
        props.history.push("/Business/List");
    };

    const onChangeValue = (key, value) => {
        setData({
            ...data,
            [key]: value,
        });
    };

    const handleTabChange = (newValue) => {
        if (parseInt(newValue) === 2) {
            dispatch(setHeaderTitle(`Business Detail`, [{
                title: 'Business management',
                page: 'Business/List'
            }, {title: query.get('name'), page: ''}]));
        }
        setValueTab(parseInt(newValue));
    };

    const onSelectImage = (elm) => {
        if (elm.files && elm.files[0]) {
            BusinessService.uploadLogoBusiness(elm.files[0]).then(value => {
                if (value && value.errorCode === 200) {
                    const link = value.data.Location;
                    onChangeValue("imageUrl", link);
                }
            });
        }
    };

    return data ? (
        <>
            <Paper>
                <Tabs
                    activeKey={valueTab.toString()}
                    onChange={handleTabChange}
                    centered
                >
                    {checkPermission('view', 'biz-info') ? (<Tabs.TabPane key={2} tab="Info"/>) : null}
                    {checkPermission('view', 'biz-user') ? (<Tabs.TabPane key={3} tab="User"/>) : null}
                    {checkPermission('view', 'biz-manager') ? (<Tabs.TabPane key={4} tab="Manager"/>) : null}
                    {checkPermission('view', 'biz-emoji') ? (<Tabs.TabPane key={5} tab="Emoji"/>) : null}
                    {checkPermission('view', 'biz-campaign') ? (<Tabs.TabPane key={6} tab="Campaign"/>) : null}
                    {checkPermission('view', 'biz-access-code') ? (<Tabs.TabPane key={7} tab="Access Code"/>) : null}
                    {checkPermission('view', 'biz-option-fields') ? (<Tabs.TabPane key={8} tab="Additional field"/>) : null}
                    {/*{checkPermission('view', 'form-generation') ? (<Tabs.TabPane key={9} tab="Form generation"/>) : null}*/}

                </Tabs>
                {
                    valueTab === 2 ? (<div className="row">
                        <div className="col-md-12">
                            <div className="kt-section" style={{padding: 12}}>
                                <Form onSubmit={handleSubmit}>
                                    <Form.Row className="text-center">
                                        <div className="avatar-input">
                                            {data.imageUrl ?
                                                <img src={data.imageUrl} className="main-profile-img"/> : null}
                                            <i className="fa fa-edit"
                                               onClick={() => (inputFile ? inputFile.click() : null)}/>
                                            <Form.Control
                                                ref={r => {
                                                    inputFile = r;
                                                }}
                                                className="input-file-none"
                                                type="file"
                                                autoFocus
                                                onChange={(e) => onSelectImage(e.target)}
                                            />
                                        </div>
                                    </Form.Row>
                                    <Form.Row>
                                        <Form.Group as={Col} controlId="formBasicName">
                                            <Form.Label className="starDanger">Name</Form.Label>
                                            <Form.Control
                                                type="text"
                                                maxLength={255}
                                                autoFocus
                                                placeholder="Enter name"
                                                value={data.name || ""}
                                                onChange={(e) => onChangeValue("name", e.target.value)}
                                                onBlur={(e) => onChangeValue("name", e.target.value.trim())}
                                            />
                                        </Form.Group>

                                        <Form.Group as={Col} controlId="formBasicName">
                                            <Form.Label className="starDanger">Company ID</Form.Label>
                                            <Form.Control
                                                type="text"
                                                maxLength={255}
                                                placeholder="Company ID"
                                                value={data.companyId || ""}
                                                onChange={(e) => onChangeValue("companyId", e.target.value)}
                                                onBlur={(e) => onChangeValue("companyId", e.target.value.trim())}
                                            />
                                        </Form.Group>
                                    </Form.Row>
                                    <Form.Row>
                                        <Form.Group as={Col} controlId="formBasicName">
                                            <Form.Label className="starDanger">Background Color</Form.Label>
                                            <Form.Control
                                                type="color"
                                                maxLength={255}
                                                placeholder="Background Color"
                                                value={data.backgroundColor || ""}
                                                onChange={(e) => onChangeValue("backgroundColor", e.target.value)}
                                                onBlur={(e) => onChangeValue("backgroundColor", e.target.value.trim())}
                                            />
                                        </Form.Group>

                                        <Form.Group as={Col} controlId="formBasicName">
                                            <Form.Label className="starDanger">Status</Form.Label>
                                            <Select className={'select-ant'} value={data.status || "active"}
                                                    onChange={(e) => onChangeValue("status", e)}>
                                                <Select.Option value={'active'}>Active</Select.Option>
                                                <Select.Option value={'inactive'}>Inactive</Select.Option>
                                            </Select>
                                        </Form.Group>
                                    </Form.Row>

                                    <Form.Row>
                                        <Form.Group as={Col} controlId="formBasicName">
                                            <Form.Label className="starDanger">Pet Types</Form.Label>
                                            <Select mode={"multiple"} className={'pet-select'}
                                                    value={data.pets}
                                                    style={{width: '100%', textTransform: "capitalize"}}
                                                    onChange={(e) => onChangeValue("pets", e)}>
                                                {
                                                    types.map((x, ind) => (
                                                        <Select.Option key={ind.toString()}
                                                                       style={{textTransform: "capitalize"}}
                                                                       value={x.name}>{x.name}</Select.Option>

                                                    ))
                                                }
                                            </Select>
                                        </Form.Group>
                                    </Form.Row>


                                    <div className="kt-login__actions">
                                        <button
                                            onClick={() => canCancel()}
                                            type="button"
                                            id="cancelBtn"
                                            className="btn btn-secondary btn-elevate kt-login__btn-secondary">
                                            Cancel
                                        </button>

                                        <ButtonLoading className="btn btn-primary ml-3" loading={isLoadSubmit}
                                                       type="submit" {...checkPermissionStyle('edit', 'biz-info', true, true)}>
                                            Update
                                        </ButtonLoading>
                                    </div>
                                </Form>
                            </div>
                        </div>
                    </div>) : null
                }
                {
                    valueTab === 3 ? (
                        <ListUserBusiness title={'User'} tab={3} business={data} canAppend={true} canBan={true}
                                          history={props.history}
                                          Type="normal"/>) : null
                }
                {
                    valueTab === 4 ? (
                        <ListUserBusiness title={'Manager'} tab={4} business={data} history={props.history}
                                          Type="manager"/>) : null
                }
                {
                    valueTab === 5 ? (
                        <div className={'no-shadown'} style={{padding: 12}}>
                            <ListEmoji tab={5} business={id} history={props.history} Type="manager"/>
                        </div>) : null
                }
                {
                    valueTab === 6 ? (
                        <div className={'no-shadown'} style={{padding: 12}}>
                            <ListModuleBiz business={id}/>
                        </div>) : null
                }
                {
                    valueTab === 7 ? (
                        <div className={'no-shadown'} style={{padding: 12}}>
                            <AccessCode business={id}/>
                        </div>) : null
                }
                {
                    valueTab === 8 ? (
                        <div className={'no-shadown'}  style={{padding: 12}}>
                            <OptionFieldList tab={8} business={id} history={props.history}/>
                        </div>) : null
                }
                {/*{*/}
                {/*    valueTab === 9 ? (*/}
                {/*        <div className={'no-shadown'}  style={{padding: 12}}>*/}
                {/*            <FormGenerateList tab={9} business={id} history={props.history}/>*/}
                {/*        </div>) : null*/}
                {/*}*/}
            </Paper>

        </>
    ) : null;
};
