/* eslint-disable no-script-url,jsx-a11y/anchor-is-valid */
import React from 'react';
import {connect, useSelector} from 'react-redux';
import objectPath from 'object-path';
import {useHistory, withRouter} from 'react-router-dom';
import {LayoutContextConsumer} from '../LayoutContext';
import * as builder from '../../ducks/builder';
import BreadCrumbs from './components/BreadCrumbs';

function SubHeader(props) {
    const {subheaderCssClasses, subheaderContainerCssClasses, subheaderMobileToggle} = props;
    const location = useHistory();
    const header = useSelector(state => state.header);
    console.log(header);
    return (
        <div id="kt_subheader" className={`kt-subheader ${subheaderCssClasses} kt-grid__item`}>
            <div className={`kt-container ${subheaderContainerCssClasses}`}>
                <div className="kt-subheader__main">
                    {subheaderMobileToggle && (
                        <button
                            className="kt-subheader__mobile-toggle kt-subheader__mobile-toggle--left"
                            id="kt_subheader_mobile_toggle">
                            <span/>
                        </button>
                    )}

                    <LayoutContextConsumer>
                        {({subheader: {title, breadcrumb}}) => {

                            breadcrumb = header.breadcrumb;
                            if (location.location.pathname.indexOf('/SubModule/List/') >= 0) {
                                title = '';
                                let tt2 = new URL(window.location.href);
                                let tt = tt2.searchParams.get('name');
                                breadcrumb = [{
                                    title: 'Module Management',
                                    page: 'Module/List'
                                }, {title: window.backModuleName || tt, page: tt2.pathname.replace('/', '')}]
                            }

                            return (
                                <>
                                    <h3 className="kt-subheader__title">{title}</h3>
                                    <BreadCrumbs items={breadcrumb}/>
                                </>
                            )
                        }}
                    </LayoutContextConsumer>

                    {/*<span className="kt-subheader__separator kt-subheader__separator--v" />*/}
                </div>
            </div>
        </div>
    );
}

const mapStateToProps = (store) => ({
    config: store.builder.layoutConfig,
    menuConfig: store.builder.menuConfig,
    subheaderMobileToggle: objectPath.get(store.builder.layoutConfig, 'subheader.mobile-toggle'),
    subheaderCssClasses: builder.selectors.getClasses(store, {
        path: 'subheader',
        toString: true,
    }),
    subheaderContainerCssClasses: builder.selectors.getClasses(store, {
        path: 'subheader_container',
        toString: true,
    }),
});

export default withRouter(connect(mapStateToProps)(SubHeader));
