import {Icon} from "@material-ui/core";
import React from "react";

export default function ActionItem(props = {
    icon: '',
    onClick: () => {
    }, color: '#000'
}) {
    return (
        <span style={{
            cursor: 'pointer',
            height: 20,
            display: 'inline-flex',
            alignItems: 'center',
            justifyContent: 'center', ...(props.style || {})
        }}>
            <Icon
                className={props.icon}
                onClick={props.onClick}
                style={{
                    color: props.color ? props.color : '#000',
                    fontSize: 14,
                    marginLeft: 15,
                    width: 20
                }}
            />
        </span>
    )
}
