import React, {useState, useEffect} from "react";
import {showErrorMessage, showSuccessMessage} from "../../actions/notification";
import {Card, Col, Row} from "react-bootstrap";
import {InputNumber, Modal, Radio} from "antd";
import {useDispatch} from "react-redux";
import {setHeaderTitle} from "../../store/actions/HeaderAction";
import ExerciseService from "../../services/ExerciseService";
import {Form, Input, Button, Checkbox, Select} from 'antd';
import CKEditor from "@ckeditor/ckeditor5-react";
import ClassicEditor from "ckeditor5-classic-with-mathtype";
import "./style.css";
import PropertiesService from "../../services/PropertiesService";
import PropertyTypes from "../../config/propertyTypes";
import {useHistory, useParams} from "react-router-dom";
import {getChanged} from "../../services/AppService";
import {ImagesUpload} from "../../common/upload";
import {ImageUpload} from "../../common/upload/single";
import {VideoWithThumbnailUpload} from "../../common/upload/videoWithThumbnail";
import {CircularProgress} from "@material-ui/core";
import WorkoutService from "../../services/WorkoutService";

const {Option} = Select;

const ckeditorConfig = {
  fontSize: {
    options: [9, 11, 13, "default", 17, 19, 21],
  },
  toolbar: {
    items: ["heading", "MathType", "ChemType", "fontSize", "|", "bold", "italic", "link", "bulletedList", "numberedList", "insertTable", "blockQuote", "undo", "redo"],
  },
};


export const EditorMce = (props) => {
  const value = props.value;

  return <div>
    <CKEditor data={value} editor={ClassicEditor} config={ckeditorConfig}
              onChange={(event, editor) => props.onChange(editor.getData())}/>
  </div>
}


export function PropertiesSelect(props) {
  const types = props.types;
  const radios = props.radios || [];
  const defaultValue = props.value;
  const [value, setValue] = useState(defaultValue || [])
  const [properties, setProperties] = useState([]);

  useEffect(() => {
    initData().then();
  }, [])

  const initData = async () => {
    let result = await PropertiesService.searchProperties(null, null);
    if (result.data) {
      const ps = result.data.items.map(item => {
        return {
          ...item,
          id: item.id,
          label: item.name,
          type: item.type,
        };
      });
      setProperties(ps);
    }
  };

  const getValues = (type, values, isRadio = false) => {
    const ps = properties.filter(y => y.type === type && !(!values.find(z => z === y.id))).map(x => x.id);
    if (isRadio) {
      return ps && ps.length > 0 ? ps[0] : null;
    }
    return ps;
  }

  useEffect(() => {
    if (props.onChange) {
      props.onChange(value);
    }
  }, [value])

  const onChangeValue = (v) => {
    setValue([...value, v]);
  }

  const onRemoveValue = (v) => {
    setValue([...value.filter(x => x !== v)]);
  }

  const onRemoveAllValue = (vs) => {
    setValue([...value.filter(x => !vs.find(y => y === x))]);
  }

  return (
    <div className={'right-form-item'}>
      {
        radios.map((item, index) => {
          return (
            <div key={index}>
              <div className="ant-col ant-form-item-label">
                <label>{item}</label>
              </div>
              <Radio.Group onChange={value1 => {
                let valueCached = [...value];
                const preValue = getValues(item, valueCached, true);
                valueCached = valueCached.filter(x => x !== preValue);
                valueCached.push(value1.target.value);
                setValue(valueCached);

              }} value={getValues(item, value, true)}>
                {properties.filter(x => x.type === item).map(((value, index1) => (
                  <Radio className={'radio-item'} key={index1} value={value.id}>{value.label}</Radio>
                )))}
              </Radio.Group>
            </div>
          )
        })
      }
      {types.map((item, index) => {
        return (
          <div key={index}>
            <div className="ant-col ant-form-item-label">
              <label>{item}</label>
            </div>
            <Select
              onClear={() => {
                const items = getValues(item, value);
                onRemoveAllValue(items);
              }}
              onDeselect={(value1 => {
                onRemoveValue(value1);
              })}
              onSelect={value1 => onChangeValue(value1)} defaultValue={getValues(item, value)}
              value={getValues(item, value)} mode="multiple" allowClear
              style={{width: '100%'}}>
              {properties.filter(x => x.type === item).map(((value, index1) => (
                <Option key={index1} value={value.id}>{value.label}</Option>
              )))}
            </Select>
          </div>
        )
      })
      }
    </div>
  )
}

export default function AddExercise(props) {
  const dispatch = useDispatch();
  const history = useHistory();
  const [modalCancelVisible, setModalCancelVisible] = useState(false);
  const [data, setData] = useState();
  const {id} = useParams();
  const isEditMode = !(!id);

  useEffect(() => {
    dispatch(setHeaderTitle(isEditMode ? "Update Exercise" : "Add New Exercise"));
  }, []);

  useEffect(() => {
    if (isEditMode) {
      ExerciseService.getExercise(id).then(value => {
        if (value) {
          setData(value.data);
          console.log(value.data);
        }
      })
    }
  }, [isEditMode]);


  const onCancel = () => {
    props.history.goBack();
  };

  const onFinish = (values) => {
    if (!values.name) {
      return showErrorMessage("Name must not be empty!");
    }
    if (!values.descriptions) {
      return showErrorMessage("Benefits must not be empty!");
    }

    if (!values.imageUrl) {
      return showErrorMessage("Image must not be empty!");
    }

    if (!values.videos || values.videos.length <= 0 || !values.videos[0].url) {
      return showErrorMessage("Videos must not be empty!");
    }

    if (!values.videos || !values.videos[0].thumbnail) {
      return showErrorMessage("Videos thumbnail must not be empty!");
    }

    if (isEditMode) {
      ExerciseService.updateExercise(data.id, getChanged(data, values)).then(value => {
        if (value) {
          showSuccessMessage("Updated Exercise Successfully!!");
          history.goBack();
        }
      })
    } else {
      ExerciseService.createExercise(values).then(value => {
        if (value) {
          showSuccessMessage("Added Exercise Successfully!!");
          history.goBack();
        }
      })
    }
  };

  const onFinishFailed = (errorInfo) => {
    console.log('Failed:', errorInfo);
  };


  return (
    <>
      {
        !isEditMode || data ? (
          <Form
            initialValues={data}
            layout="vertical"
            onFinish={onFinish}
            onFinishFailed={onFinishFailed}
          >
            <div className="row">
              <div className="col-md-9">
                <div className="kt-section">
                  <Card>
                    <Card.Body>

                      <Row>
                        <Col>
                          <Form.Item label="Name" name="name">
                            <Input/>
                          </Form.Item>
                        </Col>
                        <Col>
                          <Form.Item label="Calories" name="calories">
                            <InputNumber style={{width: '100%'}}/>
                          </Form.Item>
                        </Col>

                      </Row>

                      <Row>
                        <Col>
                          <Form.Item label="Guide Images" name="guideImageUrls">
                            <ImagesUpload methodUploadImage={ExerciseService.uploadLogoExercise} type={'image'}/>
                          </Form.Item>
                        </Col>
                      </Row>

                      <Row>
                        <Col>
                          <Form.Item label="Tutorial" name="tutorials">
                            <EditorMce/>
                          </Form.Item>
                        </Col>
                      </Row>

                      <Row>
                        <Col>
                          <Form.Item label="Benefits" name="descriptions">
                            <EditorMce/>
                          </Form.Item>
                        </Col>
                      </Row>

                      <Row>
                        <Col>
                          <Form.Item label="Cautions" name="cautions">
                            <EditorMce/>
                          </Form.Item>
                        </Col>
                      </Row>


                      <Row>
                        <Col>
                          <Form.Item label="What to expect after workout" name="expectImageUrls">
                            <ImagesUpload methodUploadImage={ExerciseService.uploadLogoExercise} type={'image'}/>
                          </Form.Item>
                        </Col>
                      </Row>

                      <Row>
                        <Col>
                          <Form.Item label="Videos" name="videos">
                            <VideoWithThumbnailUpload
                              methodUploadImage={ExerciseService.uploadLogoExercise}
                              methodUploadVideo={ExerciseService.uploadVideoExercise}
                            />
                          </Form.Item>
                        </Col>
                      </Row>

                    </Card.Body>
                  </Card>
                  <Modal
                    title={`Cancel ${isEditMode ? 'Edit' : 'Add'} Exercise`}
                    visible={modalCancelVisible}
                    onOk={() => onCancel()}
                    onCancel={() => setModalCancelVisible(false)}
                    cancelText="Cancel"
                    okText="Ok">
                    <p>Do you want to cancel and quit ?</p>
                  </Modal>
                </div>
              </div>
              <div className="col-md-3">
                <Card>
                  <Card.Body>
                    <Form.Item label="" name="properties">
                      <PropertiesSelect
                        radios={[PropertyTypes.Category, PropertyTypes.Level]}
                        types={[PropertyTypes.BodyPart, PropertyTypes.Equipment]}/>
                    </Form.Item>
                    <Row className={'image-single'}>
                      <Col>
                        <Form.Item label="Image" name="imageUrl">
                          <ImageUpload methodUploadImage={WorkoutService.uploadLogoWorkout} type={'image'}/>
                        </Form.Item>
                      </Col>
                    </Row>

                    <Form.Item>
                      <Button type="primary" htmlType="submit">
                        Submit
                      </Button>
                      <Button style={{marginLeft: 12}} htmlType="button" onClick={() => setModalCancelVisible(true)}>
                        Cancel
                      </Button>
                    </Form.Item>
                  </Card.Body>
                </Card>
              </div>
            </div>
          </Form>
        ) : <CircularProgress size={20}/>
      }
    </>
  );
};
