import React, {useEffect, useState} from "react";
import moment from "moment";
import {
    Button,
    CircularProgress,
    Icon,
    Paper,
    Table,
    TableBody,
    TableCell,
    TableHead,
    TableRow
} from "@material-ui/core";
import {Modal, Pagination, Popover} from "antd";
import {showSuccessMessage, showSuccessMessageIcon} from "../../actions/notification";
import "./field.css";
import {setHeaderTitle} from "../../store/actions/HeaderAction";
import {useDispatch} from "react-redux";
import {indexPaging, loadPaging} from "../../services/AppService";
import {checkPermissionStyle} from "../../services/CognitoService";
import ActionItem from "../../common/action";
import {useLocation} from "react-router-dom";
import FormService from "../../services/FormService";
import ModuleService from "../../services/ModuleService";
import endpoints from "../../config/endpoints";

export default function List(props) {
    const dispatch = useDispatch();
    const [page, setPage] = useState(1);
    const [limit, setLimit] = useState(10);
    const [total, setTotal] = useState(1);
    const [rows, setRow] = useState();
    const [modules, setModules] = useState();
    const [dataDelete, setDataDelete] = useState({visible: false});
    const [isLoadSearch, setLoadSearch] = useState(false);
    const businessId = props.business;
    const query = new URLSearchParams(useLocation().search);

    useEffect(() => {
        dispatch(setHeaderTitle(`Form generation`, [{
            title: 'Business management',
            page: 'Business/List'
        }, {title: query.get('name'), page: ''}]));
        setLoadSearch(true);
        ModuleService.search({all: true}).then(value => {
            setModules(value.data.items.sort((a, b) => {
                return a.name.localeCompare(b.name);
            }));
        }).finally(() => setLoadSearch(false));
    }, []);

    useEffect(() => {
        setRow([]);
        setLoadSearch(true);
        searchForm().then().finally(() => setLoadSearch(false));
    }, [page, limit]);

    const searchForm = async () => {
        const value = await FormService.searchForm({
            businessId,
            ...loadPaging(page, limit)
        });
        if (value) {
            setRow(value.data.items);
            if (value.data.totalPage && value.data.totalPage >= 0) {
                setTotal(value.data.totalPage * limit);
            }
        }
        setLoadSearch(false);
    };

    const renderStatusText = (status) => {
        if (status === "active") {
            return (
                <span
                    className="btn btn-label-success btn-bold btn-sm btn-icon-h"
                    style={{borderRadius: ".42rem"}}>
                  Active
                </span>
            );
        } else {
            return (
                <span
                    className="btn btn-label-warning btn-bold btn-sm btn-icon-h"
                    style={{borderRadius: ".42rem", textTransform: "capitalize"}}>
                  Inactive
                </span>
            );
        }
    };


    const showModal = (idDel) => {
        setDataDelete({
            ...dataDelete,
            visible: true,
            ...idDel,
        });
    };

    const clickModalCancel = () => {
        setDataDelete({
            ...dataDelete,
            visible: false,
            idDel: 0,
        });
    };

    const openUpdatePage = (row) => {
        localStorage.setItem("BusinessTab", props.tab);
        props.history.push(`/Form/Update/${businessId}/${row.id}`);
    };

    const removeForm = async (id) => {
        const result = await FormService.removeForm(id, businessId);
        if (result && result.message === 'Success') {
            showSuccessMessageIcon("Deleted Form Successfully!");
        }
        setDataDelete({
            visible: false,
        });
        searchForm().then();
    };

    const clickModalOk = () => {
        removeForm(dataDelete.id).then();
    };

    const copyLink = (id) => {
        try {
            window.navigator.clipboard.writeText(endpoints.formManagement.getLink);
            showSuccessMessage("Copy Form Generate Link Successfully!!");
        } catch (e) {
        }
    }

    const copyKey = (id) => {
        try {
            window.navigator.clipboard.writeText(id);
            showSuccessMessage("Copy Key Successfully!!");
        } catch (e) {
        }
    }

    const generateKey = (row) => (
        <div style={{
            display: 'flex',
            justifyContent: 'space-between'
        }}>
            <Popover content={'Copy Form Generate Key'}>
                <span onClick={() => copyKey(row.id)} style={{cursor: 'pointer'}}>{row.id}</span>
            </Popover>
        </div>
    );

    const generateLink = (row) => (
        <div style={{
            display: 'flex',
            justifyContent: 'space-between'
        }}>
            <Popover content="Copy Form Generate Link">
                <div className={'link-share'}>
                    <span onClick={() => copyLink(row.id)}><Icon className="fa fa-clone"/></span>
                </div>
            </Popover>
        </div>
    );

    return (
        <>
            <Button {...checkPermissionStyle('create', 'form-generation')} onClick={() => {
                localStorage.setItem("BusinessTab", props.tab);
                props.history.push("/Form/Add/" + businessId);
            }} variant="contained" color="primary">Add Form</Button>
            <div className="row kt-mt-15">
                <div className="col-md-12">
                    <div className="kt-section">
                        <div className="kt-section__content">
                            <Paper>
                                <Table>
                                    <TableHead>
                                        <TableRow>
                                            <TableCell>No</TableCell>
                                            <TableCell>Name</TableCell>
                                            <TableCell>Module</TableCell>
                                            <TableCell>Key</TableCell>
                                            <TableCell>Share Link</TableCell>
                                            <TableCell>Status</TableCell>
                                            <TableCell>Date Created</TableCell>
                                            <TableCell style={{width: 150}}>Action</TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {
                                            isLoadSearch ? (
                                                <TableRow>
                                                    <TableCell colSpan={10} align="center">
                                                        <CircularProgress size={20}/>
                                                    </TableCell>
                                                </TableRow>
                                            ) : null
                                        }
                                        {rows && rows.length ? (
                                            rows.map((row, key) => (
                                                <TableRow key={`${key}`}>
                                                    <TableCell>{indexPaging(key, page, limit)}</TableCell>
                                                    <TableCell>{row.name}</TableCell>
                                                    <TableCell>
                                                        {modules ? modules.find((item) => row.moduleId === item.id)?.name : ''}
                                                    </TableCell>
                                                    <TableCell>{generateKey(row)}</TableCell>
                                                    <TableCell>{generateLink(row)}</TableCell>
                                                    <TableCell>
                                                        <span className={'__titlecase'}>{renderStatusText(row.status)}</span>
                                                    </TableCell>
                                                    <TableCell>
                                                        {moment(row.createdAt).format("HH:mm DD-MM-YYYY")}
                                                    </TableCell>
                                                    <TableCell>
                                                        <ActionItem
                                                            icon={'fa fa-pen'} color={"#ffa800"}
                                                            onClick={() => {
                                                                openUpdatePage(row)
                                                            }}
                                                            {...checkPermissionStyle('edit', 'form-generation')}/>
                                                        <ActionItem
                                                            {...checkPermissionStyle('delete', 'form-generation')}
                                                            icon={'fa fa-trash'} color={"rgb(220, 0, 78)"}
                                                            onClick={() => {
                                                                showModal(row)
                                                            }}/>
                                                    </TableCell>
                                                </TableRow>
                                            ))
                                        ) : !isLoadSearch && rows && rows.length === 0 ? (
                                            <TableRow>
                                                <TableCell colSpan={10} align="center">
                                                    No data
                                                </TableCell>
                                            </TableRow>
                                        ) : null}
                                    </TableBody>
                                </Table>

                                <div className={'paging-app'}>
                                    <Pagination showSizeChanger={true} pageSize={limit} current={page} onChange={(pageIndex, pageSize) => {
                                        setPage(pageIndex);
                                        setLimit(pageSize);
                                    }} defaultCurrent={1} total={total}/>
                                </div>
                            </Paper>

                        </div>
                        <Modal
                            title="Delete Form"
                            visible={dataDelete.visible}
                            onOk={clickModalOk}
                            onCancel={clickModalCancel}
                            cancelText="Cancel"
                            okText="Ok">
                            <p>Do you want to delete this Form?</p>
                        </Modal>
                    </div>
                </div>
            </div>
        </>
    );
}
