import React, {useState, useEffect} from "react";
import moment from "moment";
import Loading from "../loading";
import {ButtonGroup, CircularProgress, Tab, Tabs} from "@material-ui/core";
import {Paper, Table, TableHead, TableRow, TableCell, TableBody, Button} from "@material-ui/core";
import {Input, Modal, Pagination} from "antd";
import {showSuccessMessageIcon} from "../../actions/notification";
import "./question.css";
import QuestionService from "../../services/QuestionService";
import {setHeaderTitle} from "../../store/actions/HeaderAction";
import {useDispatch} from "react-redux";
import EmojiStateService from "../../services/EmojiStateService";
import {getNameById, indexPaging, loadPaging} from "../../services/AppService";
import ActionItem from "../../common/action";
import {checkPermissionStyle} from "../../services/CognitoService";

export default function ListQuestion(props) {
    const dispatch = useDispatch();
    const [page, setPage] = useState(1);
    const [limit, setLimit] = useState(10);
    const [total, setTotal] = useState(1);
    const [rows, setRow] = useState();
    const [valueTab, setValueTab] = React.useState(parseInt(localStorage.getItem("QuestionTab") || 0));
    const [dataDelete, setDataDelete] = useState({visible: false});
    const [search, setSearch] = useState('');
    const [dataDetail, setDataDetail] = useState({
        topicData: "",
        paperData: "",
        levelData: "",
        titleData: "",
        typeData: "",
        descriptionData: "",
        partialData: "",
        answerData: "",
        hintData: "",
        timeData: "",
        visible: false,
    });
    const [isLoadSearch, setLoadSearch] = useState(false);
    const [stackPage, setStackPage] = useState([]);
    const [states, setStates] = useState([]);

    useEffect(() => {
        dispatch(setHeaderTitle("Question management"));
        EmojiStateService.search({all: true}).then(value => {
            setStates(value.data.items);
        });
        if (localStorage.getItem("QuestionTab")) {
            setValueTab(parseInt(localStorage.getItem("QuestionTab")));
        }
    }, []);

    useEffect(() => {
        setStackPage([]);
        setRow(null);
        searchQuestion().then();
    }, [limit, valueTab, search, page]);

    const searchQuestion = async () => {
        const value = await QuestionService.searchQuestion({
            ...(loadPaging(page, limit)),
            keyword: search ? search : undefined
        }, !valueTab ? 'not-used' : 'used');
        if (value) {
            setRow(value.data.items);
            if (value.data.totalPage && value.data.totalPage >= 0) {
                setTotal(value.data.totalPage * limit);
            }
        }
        setLoadSearch(false);
    };


    const showModal = (idDel) => {
        setDataDelete({
            ...dataDelete,
            visible: true,
            ...idDel,
        });
    };


    const clickModalDetailCancel = () => {
        setDataDetail({
            ...dataDetail,
            visible: false,
        });
    };
    const clickModalCancel = () => {
        setDataDelete({
            ...dataDelete,
            visible: false,
            idDel: 0,
        });
    };

    const openUpdatePage = (row) => {
        props.history.push(`/Question/Update/${row.id}`);
    };

    const removeQuestion = async (id) => {
        const result = await QuestionService.removeQuestion(id);
        if (result) {
            showSuccessMessageIcon("Deleted Question Successfully!");
        }
        setDataDelete({
            visible: false,
        });
        searchQuestion();
    };

    const clickModalOk = () => {
        removeQuestion(dataDelete.id).then();
    };

    const handleTabChange = (e, next) => {
        setTotal(1);
        setPage(1);
        setValueTab(next);
        localStorage.setItem("QuestionTab", next)
    };
    const onSearch = (value) => {
        setPage(1);
        setSearch(value);
    }
    return (
        <>
            <div className={'top-panel'}>
                <Button {...checkPermissionStyle('create', 'question-management')} onClick={() => {
                    localStorage.setItem("QuestionTab", '0')
                    props.history.push("/Question/Add");
                }} variant="contained" color="primary">Add Question</Button>
                <Input.Search allowClear onChange={event => onSearch(event.target.value)}
                              placeholder="Input search text"
                              onSearch={onSearch} style={{width: 200}}/>
            </div>
            <div className="row kt-mt-15">


                <div className="col-md-12">
                    <div className="kt-section">
                        <div className="kt-section__content">
                            <Paper>
                                <Tabs
                                    value={valueTab}
                                    onChange={handleTabChange}
                                    indicatorColor="primary"
                                    textColor="primary"
                                    centered
                                >
                                    <Tab label="Not Used"/>
                                    <Tab label="Used"/>
                                </Tabs>
                                {isLoadSearch ? (
                                    <Loading/>
                                ) : (
                                    <Table>
                                        <TableHead>
                                            <TableRow>
                                                <TableCell>No</TableCell>
                                                <TableCell style={{width: '40%'}}>Question</TableCell>
                                                <TableCell>Type</TableCell>
                                                <TableCell>State</TableCell>
                                                <TableCell>Date Created</TableCell>
                                                <TableCell style={{width: 150}}>Action</TableCell>
                                            </TableRow>
                                        </TableHead>
                                        <TableBody>
                                            {rows && rows.length ? (
                                                rows.map((row, key) => (
                                                    <TableRow key={`${key}`}>
                                                        <TableCell>{indexPaging(key, page, limit)}</TableCell>
                                                        <TableCell>{row.title}</TableCell>
                                                        <TableCell
                                                            style={{textTransform: "capitalize"}}>{row.type}</TableCell>
                                                        <TableCell>{getNameById(row.stateId, states) || '---'}</TableCell>
                                                        <TableCell>
                                                            {moment(row.createdAt).format("HH:mm DD-MM-YYYY")}
                                                        </TableCell>
                                                        <TableCell>
                                                            <ActionItem
                                                                icon={'fa fa-pen'} color={"#ffa800"}
                                                                onClick={() => {
                                                                    openUpdatePage(row)
                                                                }}
                                                                {...checkPermissionStyle('edit', 'question-management')}
                                                            />
                                                            <ActionItem
                                                                {...checkPermissionStyle('delete', 'question-management')}
                                                                icon={'fa fa-trash'} color={"rgb(220, 0, 78)"}
                                                                onClick={() => {
                                                                    showModal(row)
                                                                }}/>
                                                        </TableCell>
                                                    </TableRow>
                                                ))
                                            ) : rows && rows.length === 0 ? (
                                                <TableRow>
                                                    <TableCell colSpan={10} align="center">
                                                        No data
                                                    </TableCell>
                                                </TableRow>
                                            ) : <TableRow>
                                                <TableCell colSpan={10} align="center">
                                                    <CircularProgress size={20}/>
                                                </TableCell>
                                            </TableRow>}
                                        </TableBody>
                                    </Table>
                                )}
                                <div className={'paging-app'}>
                                    <Pagination showSizeChanger={true} pageSize={limit} current={page} onChange={(pageIndex, pageSize) => {
                                        setPage(pageIndex);
                                        setLimit(pageSize);
                                    }} defaultCurrent={1} total={total}/>
                                </div>
                            </Paper>
                        </div>
                        <Modal
                            title="Delete Question"
                            visible={dataDelete.visible}
                            onOk={clickModalOk}
                            onCancel={clickModalCancel}
                            cancelText="Cancel"
                            okText="Ok">
                            <p>Do you want to delete this Question?</p>
                        </Modal>
                    </div>
                </div>
            </div>
        </>
    );
}
