import React, {useState, useEffect} from "react";
import {Button, ButtonGroup, Icon} from "@material-ui/core";
import {Paper, TableHead, TableRow, TableCell, TableBody} from "@material-ui/core";
import {useDispatch} from "react-redux";
import {useHistory, useParams} from "react-router";
import BusinessService from "../../../services/BusinessService";
import ModalAntd from "../../../common/antd/component/Modal";
import {showSuccessMessage} from "../../../actions/notification";
import {deleteUser, listUser} from "../../../services/CognitoService";
import {setHeaderTitle} from "../../../store/actions/HeaderAction";
import {Transfer, Switch, Table} from "antd";
import difference from "lodash/difference";
import {Card} from "react-bootstrap";
import ButtonLoading from "../../../partials/common/ButtonLoading";

// Customize Table Transfer
const TableTransfer = ({leftColumns, rightColumns, ...restProps}) => (
    <Transfer {...restProps} showSelectAll={false} titles={["Normal Users", "Users Normal Add To Business"]}>
        {({
              direction,
              filteredItems,
              onItemSelectAll,
              onItemSelect,
              selectedKeys: listSelectedKeys,
              disabled: listDisabled,
          }) => {
            const columns = direction === "left" ? leftColumns : rightColumns;

            const rowSelection = {
                getCheckboxProps: item => ({disabled: listDisabled || item.disabled}),
                onSelectAll(selected, selectedRows) {
                    const treeSelectedKeys = selectedRows
                        .filter(item => !item.disabled)
                        .map(({key}) => key);
                    const diffKeys = selected
                        ? difference(treeSelectedKeys, listSelectedKeys)
                        : difference(listSelectedKeys, treeSelectedKeys);
                    onItemSelectAll(diffKeys, selected);
                },
                onSelect({key}, selected) {
                    onItemSelect(key, selected);
                },
                selectedRowKeys: listSelectedKeys,
            };

            return (
                <Table
                    rowSelection={rowSelection}
                    columns={columns}
                    dataSource={filteredItems}
                    size="small"
                    style={{pointerEvents: listDisabled ? "none" : null}}
                    onRow={({key, disabled: itemDisabled}) => ({
                        onClick: () => {
                            if (itemDisabled || listDisabled) return;
                            onItemSelect(key, !listSelectedKeys.includes(key));
                        },
                    })}
                />
            );
        }}
    </Transfer>
);

export default function AddUserToBusiness(props) {
    const dispatch = useDispatch();
    const business = props.location.state.business;
    const [rows, setRow] = useState([]);
    const [rowsSelected, setRowSelected] = useState([]);
    const [titleAdd, setTitleAdd] = useState("Add");

    useEffect(() => {
        dispatch(setHeaderTitle("Add Users To Business"));
        list(null, true);
    }, [props.Type]);

    const list = (nextToken, isReset = false) => {
        listUser({all: true}, null, true, true).then(value => {
            if (value) {
                setRow(value.data.items.map(u => {
                    return {...u, key: u.user_name};
                }));
                console.log(value.data.items.map(u => {
                    return {...u, key: u.user_name};
                }));
            }
        });
    };

    const onChangeSelected = (rs) => {
        const newRowsSelected = rows.filter(u => rs.find(u2 => u2 === u.user_name));
        console.log(newRowsSelected);
        setRowSelected(newRowsSelected);
        // setRow([...rows].filter(u => !rs.find(u2 => u2 === u.user_name)));
    };

    const columns = [
        {
            dataIndex: "user_name",
            title: "Username",
        },
        {
            dataIndex: ['full_name'],
            title: "Fullname",
        },
        {
            dataIndex: "email",
            title: "Email",
        },
    ];

    const addUserToBiz = async () => {
        console.log(rowsSelected);
        for (let i = 0; i < rowsSelected.length; i++) {
            const result = await BusinessService.addUserToBusiness(business.id, rowsSelected[i].user_name);
            setTitleAdd(`Adding (${i}/${rowsSelected.length})...`);
        }
        showSuccessMessage("Added User To Business Successfully!");
        onCancel();
    };

    const onCancel = () => {
        props.history.goBack();
    };

    return (
        <>
            <div className="row kt-mt-15">
                <div className="col-md-12">
                    <div className="kt-section">
                        <div className="kt-section__content">
                            <Card>
                                <Card.Body>
                                    <TableTransfer
                                        showSearch={true}
                                        dataSource={rows}
                                        targetKeys={rowsSelected.map(u => u.user_name)}
                                        onChange={(evt) => onChangeSelected(evt)}
                                        leftColumns={columns}
                                        rightColumns={columns}
                                        filterOption={(inputValue, item) =>
                                            item.user_name.toLowerCase().indexOf(inputValue.toLowerCase()) !== -1 || item.email.toLowerCase().indexOf(inputValue.toLowerCase()) !== -1 || item.full_name.toLowerCase().indexOf(inputValue.toLowerCase()) !== -1
                                        }
                                    />
                                    <div className="kt-login__actions mt-5">
                                        <button
                                            type="button"
                                            id="cancelBtn"
                                            className="btn btn-secondary btn-elevate kt-login__btn-secondary"
                                            onClick={() => onCancel()}>
                                            Cancel
                                        </button>

                                        <ButtonLoading onClick={() => addUserToBiz()} className="btn btn-primary ml-2"
                                                       type="button">
                                            {titleAdd}
                                        </ButtonLoading>
                                    </div>
                                </Card.Body>
                            </Card>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}
