import React, {useState, useEffect} from "react";
import {Button, ButtonGroup, Icon} from "@material-ui/core";
import {Paper, Table, TableHead, TableRow, TableCell, TableBody} from "@material-ui/core";
import {useDispatch} from "react-redux";
import {useHistory, useParams} from "react-router";
import BusinessService from "../../../services/BusinessService";
import ModalAntd from "../../../common/antd/component/Modal";
import {showSuccessMessage} from "../../../actions/notification";
import {checkPermissionStyle, deleteUser} from "../../../services/CognitoService";
import {setHeaderTitle} from "../../../store/actions/HeaderAction";
import {useLocation} from "react-router-dom";
import ActionItem from "../../../common/action";
import {loadPaging} from "../../../services/AppService";
import {Pagination} from "antd";


export default function ListUserBusiness(props) {
    const business = props.business;
    const [rows, setRow] = useState([]);
    const [page, setPage] = useState(1);
    const [limit, setLimit] = useState(10);
    const [total, setTotal] = useState(1);
    const [modalActive, setModalActive] = useState(null);
    const [modalBanVisible, setModalBanVisible] = useState(null);
    const dispatch = useDispatch();
    const query = new URLSearchParams(useLocation().search);

    useEffect(() => {
        dispatch(setHeaderTitle(`${props.title} management`, [{
            title: 'Business management',
            page: 'Business/List'
        }, {title: query.get('name'), page: ''}]));
    }, [props.Type]);

    useEffect(() => {
        list();
    }, [page, limit]);

    const list = () => {
        BusinessService.listUserInBusiness(business.id, props.Type, {
            ...(loadPaging(page, limit)),
        }, props.Type).then((value) => {
            if (value && value.data) {
                if (value) {
                    setRow(value.data.items);
                    if (value.data.totalPage && value.data.totalPage >= 0) {
                        setTotal(value.data.totalPage * limit);
                    }
                }
            }
        });
    };

    const renderStatusText = (category) => {
        if (category === "active") {
            return (
                <span
                    className="btn btn-label-success btn-bold btn-sm btn-icon-h"
                    style={{borderRadius: ".42rem"}}>
          Active
        </span>
            );
        } else {
            return (
                <span
                    className="btn btn-label-warning btn-bold btn-sm btn-icon-h"
                    style={{borderRadius: ".42rem", textTransform: "capitalize"}}>
          {category.toString().toLocaleLowerCase().split("_").join(" ")}
        </span>
            );
        }
    };

    const onEdit = (data) => {
        localStorage.setItem("BusinessTab", props.tab);
        props.history.push("/User/Edit", {
            ...{
                user_name: data.userName,
                phone_number: data.userInfo.phoneNumber,
                full_name: data.userInfo.fullName,
                email: data.userInfo.email,
                address: data.userInfo.address,
            },
            business,
        });

    };

    const onRemoveFromBusiness = () => {
        BusinessService.removeUserFromBusiness(business.id, modalBanVisible.userName).then(value => {
            if (value) {
                list(null, true);
                showSuccessMessage("Removed User From Business Successfully!!");
                setModalBanVisible(null);
            }
        });
    };

    const onActiveUser = async () => {
        BusinessService.addUserToBusiness(business.id, modalActive.userName).then((value) => {
            if (value) {
                showSuccessMessage("Added User Successfully!!");
            }
            list(null, true);
            setModalActive(null);
        });
    };

    const onCreateClick = () => {
        localStorage.setItem("BusinessTab", props.tab);
        props.history.push(`/User/AddNew/${props.Type.toString().toLocaleLowerCase()}`, {
            type: props.Type,
            requireBack: true,
            business
        });
    };
    const onAddClick = () => {
        localStorage.setItem("BusinessTab", props.tab);
        props.history.push(`/Business/AddUserToBusiness`, {type: props.Type, requireBack: true, business});
    };

    return (
        <>
            {/*<Button className="ml-3" variant="contained" color="primary">Add User</Button>*/}
            <div className="row kt-mt-15">
                <div className="col-md-12">
                    <div className="kt-section">
                        <div className="kt-section__content">
                            <Table>
                                <TableHead>
                                    <TableRow>
                                        <TableCell>Username</TableCell>
                                        <TableCell>Full Name</TableCell>
                                        <TableCell>Email Address</TableCell>
                                        <TableCell>Phone</TableCell>
                                        <TableCell>Status</TableCell>
                                        <TableCell className="text-center">
                                            <Button {...checkPermissionStyle('create', props.Type === "normal" ? 'biz-user' : "biz-manager")}
                                                    onClick={() => onCreateClick()} variant="contained" size="small"
                                                    color="primary">
                                                Create User
                                            </Button>
                                            {
                                                props.canAppend ? (
                                                    <Button {...checkPermissionStyle('create', props.Type === "normal" ? 'biz-user' : "biz-manager")}
                                                            onClick={() => onAddClick()} className="ml-2"
                                                            variant="contained" size="small"
                                                            color="primary">
                                                        Add User
                                                    </Button>
                                                ) : null
                                            }
                                        </TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {rows.length ? (
                                        rows.map((row, key) => (
                                            <TableRow key={`user${key}`}>
                                                <TableCell>{row.userName}</TableCell>
                                                <TableCell>{row.userInfo.fullName}</TableCell>
                                                <TableCell>{row.userInfo.email}</TableCell>
                                                <TableCell>{row.userInfo.phoneNumber}</TableCell>

                                                <TableCell>{renderStatusText(row.status)}</TableCell>
                                                <TableCell className="text-center">
                                                    <ActionItem
                                                        icon={'fa fa-pen'} color={"#ffa800"}
                                                        onClick={() => {
                                                            onEdit(row)
                                                        }}
                                                        {...checkPermissionStyle('edit', props.Type === "normal" ? 'biz-user' : "biz-manager")}
                                                    />
                                                    {row.status !== "Active" ? null : (
                                                        <ActionItem
                                                            {...checkPermissionStyle('delete', props.Type === "normal" ? 'biz-user' : "biz-manager")}
                                                            icon={'fa fa-ban'} color={"rgb(220, 0, 78)"}
                                                            onClick={() => {
                                                                setModalBanVisible(row)
                                                            }}/>
                                                    )}
                                                    {props.canBan ? (
                                                        <ActionItem
                                                            {...checkPermissionStyle('delete', props.Type === "normal" ? 'biz-user' : "biz-manager")}
                                                            icon={'fa fa-ban'} color={"rgb(220, 0, 78)"}
                                                            onClick={() => {
                                                                setModalBanVisible(row)
                                                            }}/>
                                                    ) : null}
                                                </TableCell>
                                            </TableRow>
                                        ))
                                    ) : (
                                        <TableRow>
                                            <TableCell colSpan={10} align="center">
                                                No data
                                            </TableCell>
                                        </TableRow>
                                    )}
                                </TableBody>
                            </Table>
                            <div className={'paging-app'}>
                                <Pagination showSizeChanger={true} pageSize={limit} current={page} onChange={(pageIndex, pageSize) => {
                                    setPage(pageIndex);
                                    setLimit(pageSize);
                                }} defaultCurrent={1} total={total}/>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            <ModalAntd
                title="Add User"
                visible={!(!modalActive)}
                onOk={() => onActiveUser()}
                onCancel={() => setModalActive(null)}
                cancelText="Cancel"
                okText="Ok">
                <p>Do you want to add this User?</p>
            </ModalAntd>
            <ModalAntd
                title="Delete User"
                visible={!(!modalBanVisible)}
                onOk={() => onRemoveFromBusiness()}
                onCancel={() => setModalBanVisible(null)}
                cancelText="Cancel"
                okText="Ok">
                <p>Do you want to remove this user from business?</p>
            </ModalAntd>
        </>
    );
}
