import {get, del, post, put, upload} from "./HttpService";
import endpoints from "../config/endpoints";
import moment from "moment";

export default class WorkoutService {
  static searchWorkout(dataSearch = {nextToken: "", limit: 10, query: {}}) {
    return get(endpoints.workoutManagement.list, dataSearch).then(value => {
      return value ? value.data : [];
    });
  }

  static removeWorkout(id) {
    return del(endpoints.workoutManagement.delete.replace(":ID", id)).then(value => {
      return value ? value.data : null;
    });
  }

  static updateWorkout(id, workout) {
    return put(endpoints.workoutManagement.update.replace(":ID", id), workout).then(value => {
      return value ? value.data : null;
    });
  }

  static getWorkout(id) {
    return get(endpoints.workoutManagement.get.replace(":ID", id)).then(value => {
      return value ? value.data : null;
    });
  }

  static createWorkout(workout) {
    return post(endpoints.workoutManagement.create, workout).then(value => {
      return value ? value.data : null;
    });
  }

  static listUserInWorkout(workoutId, group, params) {
    return get(endpoints.workoutManagement.listUser.replace(":ID", workoutId), {...params, group}).then(value => {
      return value ? value.data : null;
    });
  }

  static addUserToWorkout(workoutId, username) {
    return put(endpoints.workoutManagement.addUser.replace(":ID", workoutId), {user: username}).then(value => {
      return value ? value.data : null;
    });
  }

  static uploadLogoWorkout(file) {
    var formdata = new FormData();
    formdata.append("file", file);
    return upload(endpoints.workoutManagement.upload, formdata).then(value => {
      return value ? value.data : null;
    });
  }

  static removeUserFromWorkout(workoutId, username) {
    return put(endpoints.workoutManagement.removeUser.replace(":ID", workoutId), {user: username}).then(value => {
      return value ? value.data : null;
    });
  }
}
