import axios from "axios";
import {getStore} from "./StoreService";
import {API_KEY} from "../config/auth";
import {showSuccessMessageIcon, showErrorMessage, showSuccessMessage} from "../actions/notification";
import endpoints from "../config/endpoints";
import {LogError, LogErrorRequest} from "./LogService";
import {setAppLoading} from "../store/actions/HeaderAction";

setInterval(() => {
    if (!!getStore().getState() &&
        !!getStore().getState().auth &&
        !!getStore().getState().auth.user &&
        !!getStore().getState().auth.user.signInUserSession &&
        !!getStore().getState().auth.user.signInUserSession.idToken &&
        !!getStore().getState().auth.user.signInUserSession.idToken.exp) {
        const exp = getStore().getState().auth.user.signInUserSession.idToken.exp;
        if (window.location.href.indexOf('/login') > 0) {
            return;
        }
        // checkToken().then(value => {
        //     if (value && value.data && value.data.isVerify === false) {
        //         showErrorMessage('Your account is already logged in from another browser. Please try again');
        //         setTimeout(() => {
        //             localStorage.clear();
        //             window.location.reload();
        //         }, 2000)
        //     }
        // });
        if (exp * 1000 <= new Date().getTime()) {
            showErrorMessage('Your session has expired. please log in');
            localStorage.clear();
            window.location.reload();
        }
    }
}, 5000);

const checkToken = () => {
    return post(endpoints.userManagement.checkToken.replace('admin', getUsername()), {token: getToken()}).then(value => {
        return value ? value.data : null;
    });
};

export const getHeaders = () => {
    const currentUser = getStore();
    let token = "";
    if (!!getStore().getState() &&
        !!getStore().getState().auth &&
        !!getStore().getState().auth.user &&
        !!getStore().getState().auth.user.signInUserSession &&
        !!getStore().getState().auth.user.signInUserSession.idToken &&
        !!getStore().getState().auth.user.signInUserSession.idToken.jwtToken) {
        token = getStore().getState().auth.user.signInUserSession.idToken.jwtToken;
    }
    return {
        Authorization: `Bearer ${token}`,
        "x-api-key": API_KEY,
    };
};

export const getToken = () => {
    let token = "";

    if (!!getStore().getState() &&
        !!getStore().getState().auth &&
        !!getStore().getState().auth.user &&
        !!getStore().getState().auth.user.signInUserSession &&
        !!getStore().getState().auth.user.signInUserSession.idToken &&
        !!getStore().getState().auth.user.signInUserSession.idToken.jwtToken) {
        token = getStore().getState().auth.user.signInUserSession.idToken.jwtToken;
    }
    return token;
};

export const getUsername = () => {
    let username = "";
    if (!!getStore().getState() &&
        !!getStore().getState().auth &&
        !!getStore().getState().auth.user) {
        username = getStore().getState().auth.user.username;
    }
    return username;
};

const error = (e) => {
    console.error("Http request Error:", e);
    if (e.response && e.response.data && e.response.data.message) {
        if (e.response.status === 401) {
            showErrorMessage('Your account is already logged in from another browser. Please try again');
            setTimeout(() => {
                localStorage.clear();
                window.location.reload();
            }, 2000)
        } else {
            showErrorMessage(e.response.data.message.toString());
        }
    } else {
        showErrorMessage(e.message);
    }
    return null;
};

export function get(url, params, headers = {}) {
    return axios.get(url, {
        headers: {
            ...getHeaders(),
            ...headers
        },
        params,
    }).catch((reason) => {
        LogErrorRequest({
            method: 'GET',
            url: url,
            params: params,
            reason: reason
        });
        return error(reason);
    })
}

export function put(url, data) {
    getStore().dispatch(setAppLoading(true));
    return axios.put(url, data, {
        headers: {
            ...getHeaders(),
        },
    }).catch((reason) => {
        LogErrorRequest({
            method: 'PUT',
            url: url,
            params: data,
            reason: reason
        });
        return error(reason);
    }).finally(() => {
        getStore().dispatch(setAppLoading(false));
    });
}

export function post(url, data, headers = {}) {
    getStore().dispatch(setAppLoading(true));

    return axios.post(url, data, {
        headers: {
            ...getHeaders(),
            ...headers
        },
    }).catch((reason) => {
        LogErrorRequest({
            method: 'POST',
            url: url,
            params: data,
            reason: reason
        });
        return error(reason);
    }).finally(() => {
        getStore().dispatch(setAppLoading(false));
    });
}

export function upload(url, data) {
    return axios.post(url, data, {
        headers: {
            ...getHeaders(),
            "Content-Type": "multipart/form-data",
        },
    }).catch((reason) => {
        LogErrorRequest({
            method: 'UPLOAD',
            url: url,
            params: {},
            reason: reason
        });
        return error(reason);
    });
}

export function uploadPut(url, data, contentType) {
    return axios.put(url, data, {
        headers: {
            // ...getHeaders(),
            "Content-Type": contentType,
        },
    }).catch((reason) => {
        LogErrorRequest({
            method: 'UPLOAD',
            url: url,
            params: {},
            reason: reason
        });
        return error(reason);
    });
}

export function del(url) {
    getStore().dispatch(setAppLoading(true));

    return axios.delete(url, {
        headers: {
            ...getHeaders(),
        },
    }).catch((reason) => {
        LogErrorRequest({
            method: 'DELETE',
            url: url,
            params: {},
            reason: reason
        });
        return error(reason);
    }).finally(() => {
        getStore().dispatch(setAppLoading(false));
    });
}
