import React, {useEffect, useState} from "react";
import {DeleteOutlined} from "@ant-design/icons";
import {Button, Upload} from "antd";
import {UploadOutlined} from '@ant-design/icons';
import {Table, TableBody, TableCell, TableHead, TableRow} from "@material-ui/core";
import './style.css';

function getBase64(file) {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.readAsDataURL(file);
    reader.onload = () => resolve(reader.result);
    reader.onerror = error => reject(error);
  });
}

export function VideoWithThumbnailUpload(props) {
  const type = props.type;
  const methodUploadImage = props.methodUploadImage;
  const methodUploadVideo = props.methodUploadVideo;
  const [previewVisible, setPreviewVisible] = useState(false)
  const [previewImage, setPreviewImage] = useState('')
  const [previewTitle, setPreviewTitle] = useState('')
  const [fileList, setFileList] = useState(props.value ? props.value.map(x => {
    return {
      id: x,
      url: x,
      status: 'done'
    }
  }) : []);
  const [videos, setVideos] = useState(props.value || []);

  const onRemove = (index, field) => {
    const vs = [...videos];
    vs[index][field] = null;
    setVideos(vs);
    props.onChange(vs);
  }

  const onUploadImage = ({onSuccess, onError, file}, index, field, type) => {
    if (type === 'image') {
      methodUploadImage(file).then(value => {
        if (value) {
          const vs = [...videos];
          vs[index][field] = value.data.Location;
          setVideos(vs);
          props.onChange(vs);
          onSuccess({
            uid: value.data.key,
            status: 'done',
            url: value.data.Location
          });
        } else {
          onError({});
        }
      })
    } else {
      methodUploadVideo(file).then(value => {
        if (value) {
          const vs = [...videos];
          vs[index][field] = value.data.Location;
          if (value.data.duration) {
            vs[index].duration = value.data.duration;
          }
          setVideos(vs);
          props.onChange(vs);
          onSuccess({
            uid: value.data.key,
            status: 'done',
            url: value.data.Location,
            duration: value.data.duration
          });
        } else {
          onError({});
        }
      })
    }
  }


  return (
    <>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Video</TableCell>
            <TableCell>Thumbnail</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {videos.length ? (
            videos.map((row, key) => (
              <TableRow key={key}>
                <TableCell>
                  {!row.url ? (
                    <Upload
                      customRequest={options => onUploadImage(options, key, 'url', 'video')}
                    >
                      <Button icon={<UploadOutlined/>}>Click to Upload</Button>
                    </Upload>
                  ) : (
                    <div className={'preview-video'}>
                      <video muted={true} controls={false} autoPlay={true} loop={true}
                             style={{width: 200, height: 'auto'}} src={row.url}/>
                      <div><DeleteOutlined onClick={event => onRemove(key, 'url')}/></div>
                    </div>

                  )}
                </TableCell>
                <TableCell>
                  {!row.thumbnail ? (
                    <Upload
                      customRequest={options => onUploadImage(options, key, 'thumbnail', 'image')}
                    >
                      <Button icon={<UploadOutlined/>}>Click to Upload</Button>
                    </Upload>
                  ) : (
                    <div className={'preview-video'}>
                      <img style={{width: 200, height: 'auto'}} src={row.thumbnail}/>
                      <div><DeleteOutlined onClick={event => onRemove(key, 'thumbnail')}/></div>
                    </div>

                  )}
                </TableCell>
              </TableRow>
            ))
          ) : null}
          {videos.length === 0 ? <TableRow>
            <TableCell colSpan={10} align="center">
              <Button onClick={event => {
                setVideos([...videos, {url: null, thumbnail: null, duration: 0}])
              }}>Add Video</Button>
            </TableCell>
          </TableRow> : null}
        </TableBody>
      </Table>
    </>
  );
}
