import React, {useState, useEffect} from "react";
import {Button, ButtonGroup, CircularProgress} from "@material-ui/core";
import {Paper, Table, TableHead, TableRow, TableCell, TableBody} from "@material-ui/core";

import {Icon} from "@material-ui/core";
import {showErrorMessage, showSuccessMessage} from "../../actions/notification";
import ExerciseService from "../../services/ExerciseService";
import {useDispatch} from "react-redux";
import {setHeaderTitle} from "../../store/actions/HeaderAction";
import {useHistory, useParams} from "react-router";
import ModalAntd from "../../common/antd/component/Modal";
import moment from "moment";
import PropertiesService from "../../services/PropertiesService";
import PropertyTypes from "../../config/propertyTypes";

import './style.css';

export default function ListExercise(props) {
  const history = useHistory();
  const dispatch = useDispatch();
  const params = useParams();
  const [rows, setRow] = useState([]);
  const [stackPage, setStackPage] = useState([]);
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [modalDeleteVisible, setModalDeleteVisible] = useState(null);
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    initData().then();
  }, [])

  useEffect(() => {
    dispatch(setHeaderTitle(`Exercise management`));
    setPage(0);
    list(null, true);
  }, [params]);

  const initData = async () => {
    let result = await PropertiesService.searchProperties(null, null);
    if (result.data) {
      const ps = result.data.items.map(item => {
        return {
          ...item,
          id: item.id,
          label: item.name,
          type: item.type,
        };
      });
      setProperties(ps);
    }
  };

  const list = (nextToken, isReset = false) => {
    setLoading(true);
    setRow([]);
    ExerciseService.searchExercise({nextToken, limit: rowsPerPage}, params.Type).then((value) => {
      if (value && value.data) {

        setRow(value.data.items ? value.data.items : []);
        if (!stackPage.find(x => x === value.data.nextToken)) {
          let stackPageNew = isReset ? [] : JSON.parse(JSON.stringify(stackPage));
          stackPageNew.push(value.data.nextToken);
          setStackPage(stackPageNew);
        }
      }
    }).finally(() => {
      setLoading(false);
    });
  };

  const nextStackPage = () => {
    const nextToken = stackPage[page] || null;
    list(nextToken);
    setPage(page + 1);
  };

  const prevStackPage = async () => {
    if (page >= 1) {
      const nextToken = stackPage[page - 2] || null;
      list(nextToken);
      setPage(page - 1);
    }
  };

  const onDetail = (row) => {
    localStorage.removeItem("ExerciseTab");
    props.history.push(`/Exercise/Edit/${row.id}`, row);
  };

  const onRemoveExercise = () => {
    ExerciseService.removeExercise(modalDeleteVisible.id).then(() => {
      list(null, true);
      showSuccessMessage("Deleted Exercise Successfully!!");
      setModalDeleteVisible(null);
    });
  };

  const renderStatusText = (status) => {
    if (status === "Active") {
      return (
        <span
          className="btn btn-label-success btn-bold btn-sm btn-icon-h"
          style={{borderRadius: ".42rem"}}>
          Active
        </span>
      );
    } else {
      return (
        <span
          className="btn btn-label-warning btn-bold btn-sm btn-icon-h"
          style={{borderRadius: ".42rem", textTransform: "capitalize"}}>
          Inactive
        </span>
      );
    }
  };

  return (
    <>
      <Button onClick={() => {
        props.history.push(`/Exercise/Add`, {type: params.Type});
      }} variant="contained" color="primary">Add Exercise</Button>
      <div className="row kt-mt-15">
        <div className="col-md-12">
          <div className="kt-section">
            <div className="kt-section__content">
              <Paper>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>Image</TableCell>
                      <TableCell>Exercise Name</TableCell>
                      <TableCell>Calories</TableCell>
                      <TableCell>Level</TableCell>
                      <TableCell>Status</TableCell>
                      <TableCell style={{width: 150}}>Action</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {
                      loading ? (
                        <TableRow>
                          <TableCell colSpan={10} align="center">
                            <CircularProgress size={20}/>
                          </TableCell>
                        </TableRow>
                      ) : null
                    }
                    {rows.length ? (
                      rows.map((row, key) => (
                        <TableRow key={key}>
                          <TableCell>
                            <img className={'image-preview'} src={row.imageUrl}/>
                          </TableCell>
                          <TableCell>{row.name}</TableCell>
                          <TableCell>{row.calories}</TableCell>
                          <TableCell>
                            {row.properties ? properties.filter(p => row.properties.find(x => x === p.id && p.type === PropertyTypes.Level)).map(p => p.name).join(", ") : null}
                          </TableCell>
                          <TableCell>{renderStatusText(row.status)}</TableCell>

                          <TableCell>
                            <span
                              style={{cursor: "pointer"}}
                              data-toggle="tooltip"
                              data-placement="top"
                              title="Detail">
                              <Icon
                                className="fa fa-pen"
                                onClick={(e) => onDetail(row)}
                                style={{
                                  color: "#ffa800",
                                  fontSize: 15,
                                  marginLeft: 15,
                                }}
                              />
                            </span>
                            <span
                              style={{cursor: "pointer"}}
                              data-toggle="tooltip"
                              data-placement="top"
                              title="Delete">
                              <Icon
                                className="fa fa-trash"
                                onClick={(e) => setModalDeleteVisible(row)}
                                style={{
                                  color: "rgb(220, 0, 78)",
                                  fontSize: 15,
                                  marginLeft: 15,
                                }}
                              />
                            </span>
                          </TableCell>
                        </TableRow>
                      ))
                    ) : (
                      !loading ? (
                        <TableRow>
                          <TableCell colSpan={10} align="center">
                            No data
                          </TableCell>
                        </TableRow>
                      ) : null
                    )}
                  </TableBody>
                </Table>
              </Paper>
              <div className="customSelector custom-svg text-center kt-margin-t-10">
                <ButtonGroup variant="contained" color="primary" aria-label="contained primary button group">
                  <Button onClick={() => prevStackPage()} disabled={!stackPage[page - 1]}>Prev</Button>
                  <Button onClick={() => nextStackPage()} disabled={!stackPage[page]}>Next</Button>
                </ButtonGroup>
              </div>
            </div>
          </div>
        </div>
      </div>

      <ModalAntd
        title="Delete Exercise"
        visible={!(!modalDeleteVisible)}
        onOk={() => onRemoveExercise()}
        onCancel={() => setModalDeleteVisible(null)}
        cancelText="Cancel"
        okText="Ok">
        <p>Do you want to delete this Exercise?</p>
      </ModalAntd>
    </>
  );
}
