import React, {useState, useEffect} from "react";
import {showErrorMessage, showSuccessMessage} from "../../actions/notification";
import {Card, Col, Row} from "react-bootstrap";
import {InputNumber, Modal} from "antd";
import {useDispatch, useSelector} from "react-redux";
import {setHeaderTitle} from "../../store/actions/HeaderAction";
import ExerciseService from "../../services/ExerciseService";
import {Form, Input, Button, Select} from 'antd';
import "./style.css";
import PropertyTypes from "../../config/propertyTypes";
import {useHistory, useParams} from "react-router-dom";
import {getChanged} from "../../services/AppService";
import {ImageUpload} from "../../common/upload/single";
import {CircularProgress} from "@material-ui/core";
import {EditorMce, PropertiesSelect} from "../exercise/Add";
import WorkoutService from "../../services/WorkoutService";
import BusinessService from "../../services/BusinessService";
import {AddSteps} from "./Edit";

const {Option} = Select;


export default function AddWorkout(props) {
  const dispatch = useDispatch();
  const history = useHistory();
  const [modalCancelVisible, setModalCancelVisible] = useState(false);
  const [data, setData] = useState();
  const {id} = useParams();
  const isEditMode = !(!id);
  const user = useSelector(state => state.auth.user);

  useEffect(() => {
    dispatch(setHeaderTitle(isEditMode ? "Update Workout" : "Add New Workout"));
  }, []);

  useEffect(() => {
    BusinessService.searchBusiness({})
    if (isEditMode) {
      WorkoutService.getWorkout(id).then(value => {
        if (value) {
          setData(value.data);
        }
      })
    }
  }, [isEditMode]);


  const onCancel = () => {
    props.history.goBack();
  };

  const onFinish = (values) => {
    if (!values.name) {
      return showErrorMessage("Name must not be empty!");
    }
    // if (!values.calories) {
    //   return showErrorMessage("Calories must not be empty!");
    // }

    if (!values.imageUrl) {
      return showErrorMessage("Image must not be empty!");
    }
    if (!values.durations) {
      return showErrorMessage("Durations must not be empty!");
    }


    if (values && values.steps && values.steps.length > 0 && !values.steps[values.steps.length - 1].exerciseId) {
      values.steps = values.steps.filter((x, i) => i < (values.steps.length - 1))
    }

    if (!values.steps || values.steps.length <= 0) {
      return showErrorMessage("Steps must not be empty!");
    }


    if (isEditMode) {
      WorkoutService.updateWorkout(data.id, getChanged(data, values)).then(value => {
        if (value) {
          showSuccessMessage("Updated Workout Successfully!!");
          history.goBack();
        }
      })
    } else {
      WorkoutService.createWorkout(values).then(value => {
        if (value) {
          showSuccessMessage("Added Workout Successfully!!");
          history.goBack();
        }
      })
    }
  };

  const onFinishFailed = (errorInfo) => {
    console.log('Failed:', errorInfo);
  };


  return (
    <>
      {
        !isEditMode || data ? (
          <Form
            initialValues={data}
            layout="vertical"
            onFinish={onFinish}
            onFinishFailed={onFinishFailed}
          >
            <div className="row">
              <div className="col-md-9">
                <div className="kt-section">
                  <Card>
                    <Card.Body>

                      <Row>
                        <Col>
                          <Form.Item label="Name" name="name">
                            <Input/>
                          </Form.Item>
                        </Col>

                      </Row>

                      <Row>
                        <Col>
                          <Form.Item label="Calories" name="calories">
                            <InputNumber style={{width: '100%'}}/>
                          </Form.Item>
                        </Col>
                        <Col>
                          <Form.Item label="Durations" name="durations">
                            <InputNumber style={{width: '100%'}}/>
                          </Form.Item>
                        </Col>
                      </Row>

                      <Form.Item hidden={true} label="" name="owner" initialValue={user.username}>
                        <Input/>
                      </Form.Item>


                      <Row>
                        <Col>
                          <Form.Item label="Descriptions" name="descriptions">
                            <EditorMce/>
                          </Form.Item>
                        </Col>
                      </Row>
                      <Row style={{marginBottom: 20}}>
                        <Col>
                          <Form.Item name="steps">
                            <AddSteps/>
                          </Form.Item>
                        </Col>
                      </Row>


                    </Card.Body>
                  </Card>
                  <Modal
                    title={`Cancel ${isEditMode ? 'Edit' : 'Add'} Workout`}
                    visible={modalCancelVisible}
                    onOk={() => onCancel()}
                    onCancel={() => setModalCancelVisible(false)}
                    cancelText="Cancel"
                    okText="Ok">
                    <p>Do you want to cancel and quit ?</p>
                  </Modal>
                </div>
              </div>
              <div className="col-md-3">
                <Card>
                  <Card.Body>
                    <Form.Item label="" name="properties">
                      <PropertiesSelect
                        radios={[PropertyTypes.Category, PropertyTypes.Level]}
                        types={[PropertyTypes.BodyPart, PropertyTypes.Equipment]}/>
                    </Form.Item>
                    <Row className={'image-single'}>
                      <Col>
                        <Form.Item label="Image" name="imageUrl">
                          <ImageUpload methodUploadImage={WorkoutService.uploadLogoWorkout} type={'image'}/>
                        </Form.Item>
                      </Col>
                    </Row>

                    <Form.Item>
                      <Button type="primary" htmlType="submit">
                        Submit
                      </Button>
                      <Button style={{marginLeft: 12}} htmlType="button" onClick={() => setModalCancelVisible(true)}>
                        Cancel
                      </Button>
                    </Form.Item>
                  </Card.Body>
                </Card>
              </div>
            </div>
          </Form>
        ) : <CircularProgress size={20}/>
      }
    </>
  );
};
