import {get, del, post, put, upload} from "./HttpService";
import endpoints from "../config/endpoints";

export default class FieldService {
    static searchField(dataSearch = {nextToken: "", limit: 10, query: {}}, bizId = '') {
        return get(endpoints.fieldManagement.list.replace(":BIZ_ID", bizId), dataSearch).then(value => {
            return value ? value.data : null;
        });
    }

    static removeField(id, bizId) {
        return del(endpoints.fieldManagement.delete.replace(":BIZ_ID", bizId).replace(":ID", id)).then(value => {
            return value ? value.data : null;
        });
    }

    static getField(id, bizId) {
        return get(endpoints.fieldManagement.get.replace(":BIZ_ID", bizId).replace(":ID", id)).then(value => {
            return value ? value.data : null;
        });
    }

    static updateField(id, dialog, bizId) {
        return put(endpoints.fieldManagement.update.replace(":BIZ_ID", bizId).replace(":ID", id), dialog).then(value => {
            return value ? value.data : null;
        });
    }

    static createField(dialog, bizId) {
        return post(endpoints.fieldManagement.create.replace(":BIZ_ID", bizId), dialog).then(value => {
            return value ? value.data : null;
        });
    }

    static upload(file, bizId) {
        var formdata = new FormData();

        formdata.append("file", file);
        return upload(endpoints.fieldManagement.upload.replace(":BIZ_ID", bizId), formdata).then(value => {
            return value ? value.data : null;
        });
    }
}
