import {get, del, post, put, upload} from "./HttpService";
import endpoints from "../config/endpoints";

export default class BusinessService {
    static searchBusiness(dataSearch = {nextToken: "", limit: 10, query: {}}) {
        return get(endpoints.businessManagement.list, dataSearch).then(value => {
            return value ? value.data : null;
        });
    }

    static listModule(dataSearch = {nextToken: "", limit: 10, query: {}}, id) {
        return get(endpoints.businessManagement.listModule.replace(":ID", id), dataSearch).then(value => {
            return value ? value.data : null;
        });
    }

    static getBusiness(id) {
        return get(endpoints.businessManagement.get.replace(":ID", id)).then(value => {
            return value ? value.data : null;
        });
    }

    static removeBusiness(id) {
        return del(endpoints.businessManagement.delete.replace(":ID", id)).then(value => {
            return value ? value.data : null;
        });
    }

    static removeModule(id, modules, campaign) {
        return post(endpoints.businessManagement.removeModule.replace(":ID", id), {modules, campaign}).then(value => {
            return value ? value.data : null;
        });
    }

    static addModule(id, data) {
        return post(endpoints.businessManagement.addModule.replace(":ID", id), data).then(value => {
            return value ? value.data : null;
        });
    }

    static updateModule(id, data) {
        return put(endpoints.businessManagement.updateModule.replace(":ID", id), data).then(value => {
            return value ? value.data : null;
        });
    }

    static updateBusiness(id, business) {
        return put(endpoints.businessManagement.update.replace(":ID", id), business).then(value => {
            return value ? value.data : null;
        });
    }

    static createBusiness(business) {
        return post(endpoints.businessManagement.create, business).then(value => {
            return value ? value.data : null;
        });
    }

    static listUserInBusiness(businessId, group, params) {
        return get(endpoints.businessManagement.listUser.replace(":ID", businessId), {...params, group}).then(value => {
            return value ? value.data : null;
        });
    }

    static addUserToBusiness(businessId, username) {
        return put(endpoints.businessManagement.addUser.replace(":ID", businessId), {user: username}).then(value => {
            return value ? value.data : null;
        });
    }

    static uploadLogoBusiness(file) {
        var formdata = new FormData();
        formdata.append("files", file);
        formdata.append("entity", 'business');
        return upload(endpoints.uploadImageManagement.upload, formdata).then(value => {
            return value ? value.data : null;
        });
    }

    static removeUserFromBusiness(businessId, username) {
        return put(endpoints.businessManagement.removeUser.replace(":ID", businessId), {user: username}).then(value => {
            return value ? value.data : null;
        });
    }
}
