import React, {useState, useEffect} from "react";
import moment from "moment";
import makeRequest from "../../libs/request";
import InputForm from "../../partials/common/InputForm";
import ButtonLoading from "../../partials/common/ButtonLoading";
import Loading from "../loading";
import {ButtonGroup, makeStyles} from "@material-ui/core";
import {Paper, Table, TableHead, TableRow, TableCell, TableBody, Button} from "@material-ui/core";
import MathJax from "react-mathjax-preview";
import {Form, Card, Col} from "react-bootstrap";
import {Modal, Pagination} from "antd";
import {Icon} from "@material-ui/core";
import {showSuccessMessageIcon, showErrorMessage} from "../../actions/notification";
import "./style.css";
import {setHeaderTitle} from "../../store/actions/HeaderAction";
import {useDispatch} from "react-redux";
import PropertyTypes from "../../config/propertyTypes";
import QuestionTypes from "../../config/questionTypes";
import EmojiService from "../../services/EmojiService";
import EmojiStateService from "../../services/EmojiStateService";
import {getNameById} from "../../services/AppService";
import {PlusSquareOutlined, MinusSquareOutlined} from '@ant-design/icons';
import {v4 as uuidv4} from 'uuid';
import {useLocation} from "react-router-dom";
import PetService from "../../services/PetService";
import {checkPermissionStyle} from "../../services/CognitoService";
import ActionItem from "../../common/action";

export default function ListEmoji(props) {
    const dispatch = useDispatch();
    const [page, setPage] = useState(0);
    const [rowsPerPage, setRowsPerPage] = useState(10);
    const [rows, setRow] = useState([]);
    const [dataSearch, setData] = useState({name: "", Active: 0});
    const [dataDelete, setDataDelete] = useState({visible: false});
    const [dataDetail, setDataDetail] = useState({
        topicData: "",
        paperData: "",
        levelData: "",
        titleData: "",
        typeData: "",
        descriptionData: "",
        partialData: "",
        answerData: "",
        hintData: "",
        timeData: "",
        visible: false,
    });
    const [states, setStates] = useState([]);
    const [isLoadSearch, setLoadSearch] = useState(false);
    const [stackPage, setStackPage] = useState([]);
    const [showRows, setShowRows] = useState({});
    const [types, setTypes] = useState([]);
    const businessId = props.business;
    const query = new URLSearchParams(useLocation().search);

    useEffect(() => {
        if (businessId) {
            dispatch(setHeaderTitle(`Emoji management`, [{
                title: 'Business management',
                page: 'Business/List'
            }, {title: query.get('name'), page: ''}]));
        } else {
            dispatch(setHeaderTitle("Emoji management"));
        }
        EmojiStateService.search({all: true}).then(value => {
            setStates(value.data.items);
        });
        PetService.search({}, businessId).then(value => {
            if (value) {
                setTypes(value.data.items);
            }
        })
    }, []);

    useEffect(() => {
        searchQuestion(null);
    }, [rowsPerPage]);


    const searchQuestion = async (nextToken, isReset) => {
        // const value = await EmojiService.search({nextToken, limit: rowsPerPage}, businessId);
        const value = await EmojiService.search({all: true}, businessId);
        if (value) {
            setRow(value.data);
            if (!stackPage.find(x => x === value.data.nextToken)) {
                let stackPageNew = isReset ? [] : JSON.parse(JSON.stringify(stackPage));
                stackPageNew.push(value.data.nextToken);
                setStackPage(stackPageNew);
            }
        }
        setLoadSearch(false);
    };


    const showModal = (idDel) => {
        setDataDelete({
            ...dataDelete,
            visible: true,
            ...idDel,
        });
    };


    const clickModalDetailCancel = () => {
        setDataDetail({
            ...dataDetail,
            visible: false,
        });
    };
    const clickModalCancel = () => {
        setDataDelete({
            ...dataDelete,
            visible: false,
            idDel: 0,
        });
    };

    const openUpdatePage = (row) => {
        localStorage.setItem("BusinessTab", props.tab);
        props.history.push(`/Emoji/Update${businessId ? '-Business' : ''}/${row}/${businessId ? businessId : ''}`);
    };

    const removeQuestion = async (id) => {
        const result = await EmojiService.remove(id);
        if (result && result.message === 'Success') {
            showSuccessMessageIcon("Deleted Emoji Successfully!");
        }
        setDataDelete({
            visible: false,
        });
        searchQuestion(null, true).then();
    };

    const clickModalOk = () => {
        removeQuestion(dataDelete.id).then();
    };

    const onChangeValueSearch = (key, value) => {
        setData({
            ...dataSearch,
            [key]: value,
        });
    };

    const nextStackPage = () => {
        const nextToken = stackPage[page] || null;
        searchQuestion(nextToken);
        setPage(page + 1);
    };

    const prevStackPage = async () => {
        if (page >= 1) {
            const nextToken = stackPage[page - 2] || null;
            searchQuestion(nextToken);
            setPage(page - 1);
        }
    };

    const renderStatusText = (status) => {
        if (status === "active") {
            return (
                <span
                    className="btn btn-label-success btn-bold btn-sm btn-icon-h"
                    style={{borderRadius: ".42rem"}}>
          Active
        </span>
            );
        } else {
            return (
                <span
                    className="btn btn-label-warning btn-bold btn-sm btn-icon-h"
                    style={{borderRadius: ".42rem", textTransform: "capitalize"}}>
          Inactive
        </span>
            );
        }
    };

    return (
        <>
            {
                (<Button {...checkPermissionStyle('create', businessId ? 'biz-emoji' : 'emoji-management')}
                         onClick={() => {
                             localStorage.setItem("BusinessTab", props.tab);
                             props.history.push(`/Emoji/Add${businessId ? '-Business' : ''}/${businessId ? businessId : ''}`);
                         }} variant="contained" color="primary">{businessId ? 'Custom' : 'Add'} Emoji</Button>)
            }
            <div className="row kt-mt-15">
                <div className="col-md-12">
                    <div className="kt-section">
                        <div className="kt-section__content">
                            <Paper>
                                {isLoadSearch ? (
                                    <Loading/>
                                ) : (
                                    <Table>
                                        <TableHead>
                                            <TableRow>
                                                <TableCell key={uuidv4()}>Image</TableCell>
                                                {/*<TableCell>Name</TableCell>*/}
                                                <TableCell key={uuidv4()}>States</TableCell>
                                                <TableCell key={uuidv4()}>Date Created</TableCell>
                                                <TableCell key={uuidv4()} style={{width: 150}}>Action</TableCell>
                                            </TableRow>
                                        </TableHead>
                                        <TableBody>
                                            {rows ? (
                                                Object.keys(rows).map((k, key) => {
                                                    return <>
                                                        <TableRow key={uuidv4()} className={'group-row'}>
                                                            <TableCell
                                                                style={{textTransform: "capitalize"}} colSpan={3}>
                                                                <span onClick={() => {
                                                                    const showRowsCache = {...showRows};
                                                                    showRowsCache[k] = !(showRowsCache[k]);
                                                                    setShowRows(showRowsCache);
                                                                }} className={'row-item-col'}>{showRows[k] ?
                                                                    <MinusSquareOutlined/> :
                                                                    <PlusSquareOutlined/>} {k}</span>
                                                            </TableCell>
                                                            <TableCell key={uuidv4()}>
                                                                <ActionItem
                                                                    icon={'fa fa-pen'} color={"#ffa800"}
                                                                    onClick={() => {
                                                                        openUpdatePage(k)
                                                                    }}
                                                                    {...checkPermissionStyle('edit', businessId ? 'biz-emoji' : 'emoji-management')}
                                                                />
                                                            </TableCell>
                                                        </TableRow>
                                                        {
                                                            showRows[k] ? rows[k].map((row, i) => (
                                                                <TableRow key={uuidv4()}>
                                                                    <TableCell key={0}>
                                                                        <img style={{width: 30}}
                                                                             className={'image-preview'}
                                                                             src={row.imageUrl}/>
                                                                    </TableCell>
                                                                    {/*<TableCell>{row.name}</TableCell>*/}
                                                                    <TableCell
                                                                        key={1}>{getNameById(row.stateId, states)}</TableCell>

                                                                    <TableCell key={2}>
                                                                        {moment(row.createdAt).format("HH:mm DD-MM-YYYY")}
                                                                    </TableCell>
                                                                    <TableCell key={3}>
                                                                    </TableCell>
                                                                </TableRow>
                                                            )) : null
                                                        }
                                                    </>

                                                })
                                            ) : (
                                                <TableRow key={uuidv4()}>
                                                    <TableCell colSpan={10} align="center">
                                                        No data
                                                    </TableCell>
                                                </TableRow>
                                            )}
                                        </TableBody>
                                    </Table>
                                )}
                            </Paper>
                            <div className="customSelector custom-svg text-center kt-margin-t-10">
                                <ButtonGroup variant="contained" color="primary"
                                             aria-label="contained primary button group">
                                    <Button onClick={() => prevStackPage()}
                                            disabled={!stackPage[page - 1]}>Prev</Button>
                                    <Button onClick={() => nextStackPage()} disabled={!stackPage[page]}>Next</Button>
                                </ButtonGroup>
                            </div>
                        </div>
                        <Modal
                            title="Delete Question"
                            visible={dataDelete.visible}
                            onOk={clickModalOk}
                            onCancel={clickModalCancel}
                            cancelText="Cancel"
                            okText="Ok">
                            <p>Do you want to delete this Question?</p>
                        </Modal>
                    </div>
                </div>
            </div>
        </>
    );
}
