import React, {useState, useEffect} from "react";
import {Button, ButtonGroup, CircularProgress} from "@material-ui/core";
import {Paper, Table, TableHead, TableRow, TableCell, TableBody} from "@material-ui/core";

import {showSuccessMessage} from "../../actions/notification";
import {useDispatch} from "react-redux";
import {setHeaderTitle} from "../../store/actions/HeaderAction";
import {useHistory, useParams} from "react-router";
import ModalAntd from "../../common/antd/component/Modal";
import ModuleService from "../../services/ModuleService";
import ActionItem from "../../common/action";
import {ACTIONS, LogPageView, PAGES} from "../../services/LogService";
import {checkPermissionStyle} from "../../services/CognitoService";
import {Checkbox, Input, Pagination} from "antd";
import {loadPaging} from "../../services/AppService";


export default function ListModule(props) {
    const history = useHistory();
    const dispatch = useDispatch();
    const params = useParams();
    const [rows, setRow] = useState([]);
    const [page, setPage] = useState(1);
    const [limit, setLimit] = useState(10);
    const [total, setTotal] = useState(1);
    const [modalDeleteVisible, setModalDeleteVisible] = useState(null);
    const [modalCloneVisible, setModalCloneVisible] = useState(null);
    const [modalPreviewVisible, setModalPreviewVisible] = useState(null);
    const [linkPreview, setLinkPreview] = useState(null);
    const [loading, setLoading] = useState(true);
    const [cloneRelation, setCloneRelation] = useState(false);
    const [cloneItem, setCloneItem] = useState(false);
    const [cloneSuffix, setCloneSuffix] = useState('');
    const [search, setSearch] = useState('');
    const [processing, setProcessing] = useState(false);

    useEffect(() => {
        LogPageView(PAGES.MODULE, ACTIONS.LIST, {});
        dispatch(setHeaderTitle(`Module management`));
        initData().then();
    }, [params]);

    useEffect(() => {
        list();
    }, [page, limit, search]);

    const list = () => {
        setLoading(true);
        ModuleService.search({
            ...loadPaging(page, limit),
            keyword: search ? search : undefined
        }, params.Type).then((value) => {
            if (value && value.data) {
                setRow(value.data.items);
                if (value.data.totalPage && value.data.totalPage >= 0) {
                    setTotal(value.data.totalPage * limit);
                }
            }
        }).finally(() => {
            setLoading(false)
        });
    };
    const initData = async () => {

    };

    const onDetail = (row) => {
        localStorage.removeItem("ProgramTab");
        props.history.push(`/Module/Update/${row.id}`, row);
    };

    const onRemoveModule = () => {
        setProcessing(true);
        ModuleService.remove(modalDeleteVisible.id).then((value) => {
            if (value && value.message === 'Success') {
                list();
                showSuccessMessage("Deleted Module Successfully!!");
                setModalDeleteVisible(null);
            }
        }).finally(() => {
            setProcessing(false);
        });
    };

    const clone = () => {
        setProcessing(true);
        ModuleService.clone(modalCloneVisible.id, cloneRelation, cloneItem, cloneSuffix).then((value) => {
            if (value && value.message === 'Success') {
                list();
                showSuccessMessage("Clone Module Successfully!!");
            }
            setModalCloneVisible(null);
        }).finally(() => {
            setProcessing(false);
        });
    };

    const renderStatusText = (status) => {
        if (status === "active") {
            return (
                <span
                    className="btn btn-label-success btn-bold btn-sm btn-icon-h"
                    style={{borderRadius: ".42rem"}}>
          Active
        </span>
            );
        } else {
            return (
                <span
                    className="btn btn-label-warning btn-bold btn-sm btn-icon-h"
                    style={{borderRadius: ".42rem", textTransform: "capitalize"}}>
          Inactive
        </span>
            );
        }
    };
    const onSearch = (value) => {
        setPage(1);
        setSearch(value);
    }
    return (
        <>
            <div className={'top-panel'}>
                <Button {...checkPermissionStyle('create', 'module-management')} onClick={() => {
                    props.history.push(`/Module/Add`);
                }} variant="contained" color="primary">Add Module</Button>
                <Input.Search allowClear onChange={event => onSearch(event.target.value)}
                              placeholder="Input search text" onSearch={onSearch} style={{width: 200}}/>
            </div>
            <div className="row kt-mt-15">
                <div className="col-md-12">
                    <div className="kt-section">
                        <div className="kt-section__content">
                            <Paper>
                                <Table>
                                    <TableHead>
                                        <TableRow>
                                            <TableCell style={{width: '70%'}}>Name</TableCell>
                                            <TableCell>Status</TableCell>
                                            <TableCell style={{width: 150, textAlign: 'right'}}>Action</TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {
                                            loading ? (
                                                <TableRow>
                                                    <TableCell colSpan={10} align="center">
                                                        <CircularProgress size={20}/>
                                                    </TableCell>
                                                </TableRow>
                                            ) : null
                                        }
                                        {rows.length ? (
                                            rows.map((row, key) => (
                                                <TableRow key={key}>
                                                    <TableCell><a style={{color: '#00bcd4'}} onClick={() => {
                                                        window.backModuleName = row.name;
                                                        props.history.push(`/SubModule/List/${row.id}?name=${row.name}`);
                                                    }}>{row.name}</a></TableCell>
                                                    <TableCell>{renderStatusText(row.status)}</TableCell>
                                                    <TableCell style={{width: 170, textAlign: 'right'}}>
                                                        <ActionItem
                                                            icon={'fa fa-eye'} color={"rgb(0,170,122)"}
                                                            onClick={() => {
                                                                setModalPreviewVisible(true);
                                                                setLinkPreview({
                                                                    ...row,
                                                                    time: new Date().getTime()
                                                                });
                                                            }}/>
                                                        <ActionItem
                                                            icon={'fa fa-pen'} color={"#ffa800"}
                                                            onClick={() => {
                                                                onDetail(row)
                                                            }}
                                                            {...checkPermissionStyle('edit', 'module-management')}

                                                        />
                                                        <ActionItem
                                                            {...checkPermissionStyle('delete', 'module-management')}
                                                            icon={'fa fa-trash'} color={"rgb(220, 0, 78)"}
                                                            onClick={() => {
                                                                setModalDeleteVisible(row)
                                                            }}/>
                                                        <ActionItem
                                                            {...checkPermissionStyle('create', 'module-management')}
                                                            icon={'fa fa-copy'} color={"rgb(128,128,128)"}
                                                            onClick={() => {
                                                                setCloneRelation(false);
                                                                setCloneItem(false);
                                                                setCloneSuffix('');
                                                                setModalCloneVisible(row)
                                                            }}/>
                                                    </TableCell>
                                                </TableRow>
                                            ))
                                        ) : !loading ? (
                                            <TableRow>
                                                <TableCell colSpan={10} align="center">
                                                    No data
                                                </TableCell>
                                            </TableRow>
                                        ) : null}
                                    </TableBody>
                                </Table>

                                <div className={'paging-app'}>
                                    <Pagination showSizeChanger={true} pageSize={limit} current={page}
                                                onChange={(pageIndex, pageSize) => {
                                                    setPage(pageIndex);
                                                    setLimit(pageSize);
                                                }} defaultCurrent={1} total={total}/>
                                </div>
                            </Paper>

                        </div>
                    </div>
                </div>
            </div>

            <div className={'model-preview'}>
                <ModalAntd
                    title="Delete Module"
                    visible={!(!modalDeleteVisible)}
                    onOk={() => onRemoveModule()}
                    onCancel={() => setModalDeleteVisible(null)}
                    cancelText="Cancel"
                    confirmLoading={processing}
                    okText="Ok">
                    <p>Do you want to delete this Module?</p>
                </ModalAntd>
                <ModalAntd
                    title="Clone Module"
                    visible={!(!modalCloneVisible)}
                    onOk={() => clone()}
                    onCancel={() => setModalCloneVisible(null)}
                    cancelText="Cancel"
                    confirmLoading={processing}
                    okText="Ok">
                    <p>Do you want to clone this Module?</p>
                    <div style={{marginBottom: 20}}>
                        <label style={{marginBottom: 3}}>Suffix:</label>
                        <Input onChange={event => setCloneSuffix(event.target.value)} value={cloneSuffix}/>
                    </div>

                    <Checkbox checked={cloneRelation} onChange={(e) => setCloneRelation(e.target.checked)}>Clone with
                        relation</Checkbox>
                    <Checkbox checked={cloneItem} onChange={(e) => setCloneItem(e.target.checked)}>Clone
                        Items</Checkbox>
                </ModalAntd>
                <ModalAntd
                    title="Preview Module Mode"
                    visible={!(!modalPreviewVisible)}
                    onCancel={() => {
                        setModalPreviewVisible(null);
                        setLinkPreview('');
                    }}
                    cancelText="Cancel"
                    width={400}
                    bodyStyle={{padding: 0}}
                    okText="">
                    {
                        modalPreviewVisible ? (
                            <iframe style={{width: 400, height: 650, border: 'none', margin: '0 auto'}}
                                    src={`${process.env.FE_ENDPOINT}/preview?moduleId=${linkPreview ? linkPreview.id : ''}&time=${linkPreview.time}`}/>
                        ) : null
                    }
                </ModalAntd>
            </div>
        </>
    );
}
