import React, {useState, useEffect} from "react";
import {Button, ButtonGroup, CircularProgress} from "@material-ui/core";
import {Paper, Table, TableHead, TableRow, TableCell, TableBody} from "@material-ui/core";

import {Icon} from "@material-ui/core";
import {showErrorMessage, showSuccessMessage} from "../../actions/notification";
import {useDispatch} from "react-redux";
import {setHeaderTitle} from "../../store/actions/HeaderAction";
import {useHistory, useParams} from "react-router";
import ModalAntd from "../../common/antd/component/Modal";
import SubModuleService from "../../services/SubModuleService";
import ReactFlow, {addEdge, Background, Controls, Handle} from "react-flow-renderer";
import "./node.css";
import "./style.css";
import "../test/style.css";
import ButtonEdge from "../test/Question/edge";
import {ACTIONS, LogPageView, PAGES} from "../../services/LogService";
import ActionItem from "../../common/action";
import {checkPermissionStyle} from "../../services/CognitoService";
import {Form, Input, Pagination} from "antd";
import {indexPaging, loadPaging} from "../../services/AppService";
import AddSubmodule from "../test/Test";
import ModuleService from "../../services/ModuleService";

const edgeTypes = {
    buttonEdge: ButtonEdge,
};


function ModuleNode({data}) {
    const indexValues = [];
    for (let i = 0; i < data.indexMax; i++) {
        indexValues.push(i);
    }

    const [updateDialogVisible, setUpdateDialogVisible] = useState();
    const [updateDialogId, setUpdateDialogId] = useState(Date.now());

    const submitUpdateDialog = (value = {}) => {
        data.onUpdateQuestionList ? data.onUpdateQuestionList(value) : null;
    }

    return <div className={'node-module'}>
        <div className={'node-module-view'}>
            <span className={data.type}>{data.type}</span>
            <button className={'btn-edit-inject-v2'} onClick={(e) => {
                e.preventDefault();
                data.onDialogVisible ? data.onDialogVisible(true) : null;
                setUpdateDialogVisible(data);
            }}>
                <img
                    src="https://img.icons8.com/external-android-line-2px-amoghdesign/24/000000/external-edit-multimedia-line-24px-android-line-2px-amoghdesign.png"/>
            </button>
            <p>{data.name}</p>
            <input readOnly={true} className={'item-index'} value={(data.index || 0) + 1}/>
        </div>

        <div>
            <Handle
                position="left"
                type="target"
                id={data.id + 'input'}
                className={'port-input'}
            />
        </div>
        <div>

            {
                data.rules && data.rules.length > 0 ? data.rules.map((r, index) => {
                    return (
                        <div key={index.toString()} className={'row-score'}>
                            <a>{r.minScore}-{r.maxScore}</a>
                            <Handle
                                position="right"
                                type="source"
                                id={`${index}-` + data.id + '-scoring'}
                                className={'port-output-score'}
                            />
                        </div>
                    )
                }) : data.type === 'normal' ? (
                    <Handle
                        position="right"
                        type="source"
                        id={data.id}
                        className={'port-output'}
                    />
                ) : null
            }
        </div>
        <ModalAntd
            width={750}
            title={`Update SubModule`}
            visible={!!updateDialogVisible}
            cancelText="Cancel"
            okText="Update"
            footer={true}
            onCancel={() => {
                setUpdateDialogVisible(false);
                data.onDialogVisible ? data.onDialogVisible(false) : null;
                setUpdateDialogId(Date.now)
            }}
            onOk={() => {
            }}>
            <div className="row">
                <div className="col-md-12">
                    <div className="kt-section">
                        <AddSubmodule visible={updateDialogId} onCancel={() => {
                            setUpdateDialogVisible(false);
                            data.onDialogVisible ? data.onDialogVisible(false) : null;
                            setUpdateDialogId(Date.now)
                        }} onSubmit={(value) => {
                            setUpdateDialogVisible(false);
                            submitUpdateDialog(value);
                            data.onDialogVisible ? data.onDialogVisible(false) : null;
                            setUpdateDialogId(Date.now)
                        }} inject={true} id={data.id}/>
                    </div>
                </div>
            </div>
        </ModalAntd>
    </div>
}

const nodeTypes = {
    module: ModuleNode,
};

export default function ListSubModule(props) {
    const history = useHistory();
    const dispatch = useDispatch();
    const params = useParams();
    const {module} = useParams();
    const [rows, setRow] = useState([]);
    const [page, setPage] = useState(1);
    const [limit, setLimit] = useState(10);
    const [total, setTotal] = useState(1);
    const [modalDeleteVisible, setModalDeleteVisible] = useState(null);
    const [loading, setLoading] = useState(true);
    const [properties, setProperties] = useState([]);
    const [elements, setElements] = useState([]);
    const [reactFlowInstance, setReactFlowInstance] = useState();
    const [search, setSearch] = useState('');
    const [dialogVisibleMain, setDialogVisibleMain] = useState(false);

    useEffect(() => {
        LogPageView(PAGES.SUBMODULE, ACTIONS.LIST, params);
        dispatch(setHeaderTitle(`SubModule management`));
        initData();
    }, [params]);

    useEffect(() => {
        list();
    }, [page, limit, search]);

    const list = (relation = true) => {
        setLoading(true);
        if (relation) {

            SubModuleService.relation(module).then(value => {
                if (value && value.data) {
                    // setElements(prevState => {
                    //     return [...prevState].filter(x => x.type === 'module');
                    // });
                    setElements(converterDataToElements(value.data.relationSubs));
                }
            });
        }
        SubModuleService.search({
            ...loadPaging(page, limit),
            moduleId: module,
            name: search
        }, params.Type).then((value) => {
            if (value && value.data) {
                setRow((value.data.items ? value.data.items : []).sort((a, b) => {
                    return a.serial < b.serial ? -1 : (a.serial > b.serial ? 1 : 0)
                }));
                if (value.data.totalPage && value.data.totalPage >= 0) {
                    setTotal(value.data.totalPage * limit);
                }
            }
        }).finally(() => {
            setLoading(false)
        });
    };
    const initData = async () => {

    };

    const onUpdateQuestionList = (item) => {
        console.log(item);
        list();
    }
    const onDialogVisible = (visible) => {
        setDialogVisibleMain(visible);
    }

    const converterDataToElements = (data) => {
        const nodes = [];
        const connects = [];
        if (data && data.length > 0) {
            let cachePosition = 0;
            let cacheSorted = data.filter(x => x?.position?.x);
            const sorted = data.map((x, index) => {
                const newPosition = (x.position ? x.position : {
                    "x": (100 + (parseInt((index + cachePosition) / 4) * 350)),
                    "y": (150 * ((index + cachePosition) % 4) + 50)
                });
                const checkOverlap = cacheSorted.find(y => y.position?.x && y.position.x === newPosition.x && y.position.y === newPosition.y && y.id !== x.id);
                cachePosition++;
                if (checkOverlap && !x.position) {
                    x.position = {
                        "x": (100 + (parseInt((index + cachePosition) / 4) * 350)),
                        "y": (150 * ((index + cachePosition) % 4) + 50)
                    };
                } else {
                    x.position = newPosition;
                }
                cacheSorted.push(x);
                return x;
            }).sort((a, b) => {
                if (a.position.x > b.position.x) {
                    return 1;
                }
                if (a.position.x < b.position.x) {
                    return -1;
                }
                if (a.position.x === b.position.x) {
                    return a.position.y > b.position.y ? 1 : -1;
                }
            });

            sorted.map((x, index) => {
                const itemId = x.id;
                nodes.push({
                    "id": itemId,
                    "type": 'module',
                    "position": (x.position ? x.position : {
                        "x": (100 + (parseInt(index / 4) * 350)),
                        "y": (150 * ((index) % 4) + 50)
                    }),
                    "data": {
                        "index": index,
                        "onUpdateQuestionList": onUpdateQuestionList,
                        "onDialogVisible": onDialogVisible,
                        "itemId": itemId,
                        "name": x.name,
                        "type": x.type,
                        "rules": x.scoringRules || [],
                        ...x
                    }
                });
                if (x.nextSubmodule) {
                    connects.push({
                        id: `connectT-${index}-${x.nextSubmodule}`,
                        source: itemId,
                        target: `${x.nextSubmodule}`,
                        sourceHandle: `${x.id}`,
                        targetHandle: `${x.nextSubmodule}-input`,
                        data: {
                            onRemoveEdge: onRemoveEdge
                        },
                        arrowHeadType: 'arrowclosed',
                        type: 'buttonEdge',
                    });
                }
                if (x.scoringRules) {
                    for (let i = 0; i < x.scoringRules.length; i++) {
                        const rule = x.scoringRules[i];
                        if (rule.nextSubmodule) {
                            connects.push({
                                id: `connect-${(index + 1) * (i + 10)}-${rule.nextSubmodule}`,
                                source: itemId,
                                target: `${rule.nextSubmodule}`,
                                sourceHandle: `${i}-${x.id}-scoring`,
                                targetHandle: `${rule.nextSubmodule}-input`,
                                data: {
                                    onRemoveEdge: onRemoveEdge
                                },
                                arrowHeadType: 'arrowclosed',
                                type: 'buttonEdge',
                            });
                        }
                    }
                }
            });
            console.log(connects);
        }
        return [...nodes, ...connects];
    }


    const nextStackPage = () => {
        const nextToken = stackPage[page] || null;
        list();
        setPage(page + 1);
    };

    const prevStackPage = async () => {
        if (page >= 1) {
            const nextToken = stackPage[page - 2] || null;
            list();
            setPage(page - 1);
        }
    };

    const onDetail = (row) => {
        localStorage.removeItem("ProgramTab");
        props.history.push(`/SubModule/Update/${module}/${row.id}`, row);
    };

    const onRemoveProgram = () => {
        SubModuleService.remove(modalDeleteVisible.id).then((value) => {
            if (value && value.message === 'Success') {
                list();
                showSuccessMessage("Deleted Sub Module Successfully!!");
                setModalDeleteVisible(null);
            }
        });
    };

    const saveData = (d) => {
        SubModuleService.relationSave(module, d).then(value => {
        });
    }

    const renderStatusText = (status) => {
        if (status === "active") {
            return (
                <span
                    className="btn btn-label-success btn-bold btn-sm btn-icon-h"
                    style={{borderRadius: ".42rem"}}>
          Active
        </span>
            );
        } else {
            return (
                <span
                    className="btn btn-label-warning btn-bold btn-sm btn-icon-h"
                    style={{borderRadius: ".42rem", textTransform: "capitalize"}}>
          Inactive
        </span>
            );
        }
    };
    const onSearch = (value) => {
        setPage(1);
        setSearch(value);
    }

    const updatePosition = (id, position) => {
        if (position < 0) {
            return;
        }
        SubModuleService.update(id, {
            serial: position
        }).then(value => {
            list(false);
        })
    }

    const onConnect = (params) => {
        console.log(params);
        updateConnector(params.source, params.target, (params.sourceHandle.indexOf('scoring') > 0 ? (params.sourceHandle.split('-')[0]) : null))
        setElements((els) => addEdge({
            ...params,
            type: 'buttonEdge',
            arrowHeadType: 'arrowclosed',
            data: {
                onRemoveEdge: onRemoveEdge
            }
        }, els));
    };

    const updateConnector = async (fromId, toId, index = null) => {
        const smodule = await SubModuleService.get(fromId).then(value => {
            if (value) {
                return value.data;
            }
            return null;
        });
        console.log(smodule);
        if (smodule) {
            if (parseInt(index) >= 0) { // index scoring
                const prevItems = smodule.scoringRules;
                prevItems[index].nextSubmodule = toId;
                const result = await SubModuleService.update(smodule.id, {scoringRules: prevItems});
            } else {
                const result = await SubModuleService.update(smodule.id, {nextSubmodule: toId});
            }
        }
        list();
    }

    const removeConnector = async (fromId, toId, index = null) => {
        const smodule = await SubModuleService.get(fromId).then(value => {
            if (value) {
                return value.data;
            }
            return null;
        });
        console.log(smodule);
        if (smodule) {
            if (parseInt(index) >= 0) { // index scoring
                const prevItems = smodule.scoringRules;
                prevItems[index].nextSubmodule = '';
                const result = await SubModuleService.update(smodule.id, {scoringRules: prevItems});
            } else {
                const result = await SubModuleService.update(smodule.id, {nextSubmodule: ''});
            }
        }
        list();
    }

    const onRemoveEdge = (id) => {
        setElements(prevState => {
            let elementsCache = [...prevState];
            // remove edge
            const item = elementsCache.find(x => x.id === id);
            removeConnector(item.source, item.target, (item.sourceHandle.indexOf('scoring') > 0 ? (item.sourceHandle.split('-')[0]) : null))

            elementsCache = elementsCache.filter(x => x.id !== id);
            console.log(item);
            return elementsCache;
        });
    }

    return (
        <>
            <div className={'kt-mt-15'}>
                <ReactFlow
                    nodesDraggable={!dialogVisibleMain}
                    onLoad={params => setReactFlowInstance(params)}
                    nodeTypes={nodeTypes}
                    edgeTypes={edgeTypes}
                    elements={elements}
                    onConnect={onConnect}
                    onNodeDragStop={() => {
                        try {
                            let elmsPosition = reactFlowInstance ? reactFlowInstance.toObject().elements : [];
                            const d = {
                                relationSubs: []
                            }
                            d.relationSubs = elmsPosition.filter(x => x.id.indexOf('connect') < 0).map(x => {
                                return {
                                    id: x.id,
                                    position: x.position
                                }
                            });
                            saveData(d);
                            //update node
                            const elms = reactFlowInstance.getElements();
                            const sorted = elms.filter((x) => x.type === "module").sort((a, b) => {
                                if (a.position.x > b.position.x) {
                                    return 1;
                                }
                                if (a.position.x < b.position.x) {
                                    return -1;
                                }
                                if (a.position.x === b.position.x) {
                                    return a.position.y > b.position.y ? -1 : 1;
                                }
                            });
                            setElements(prevState => {
                                const result = [...prevState].map(x => {
                                    if (x.type === "module") {
                                        x.data.index = sorted.findIndex(y => y.id === x.id);
                                    }
                                    return x;
                                });
                                ModuleService.updateSerial(module, {
                                    data: result.filter(x => x.type === "module").map(x => {
                                        return {
                                            id: x.id,
                                            serial: x.data.index
                                        }
                                    })
                                })
                                return result;
                            })

                        } catch (e) {
                            console.log(e);
                        }
                    }}
                    deleteKeyCode={46}
                    style={{
                        backgroundColor: '#38415f',
                        height: 600,
                        width: '100%'
                    }}
                >
                    <Controls htmlType="button"/>
                    <Background color="#aaa" gap={16}/>
                </ReactFlow>
            </div>

            <div className={'top-panel'} style={{marginTop: 15}}>
                <div>
                    <Button onClick={() => {
                        props.history.push(`/SubModule/Add/${module}`);
                    }} variant="contained"
                            color="primary" {...checkPermissionStyle('edit', 'module-management', true, true)}>Add Sub
                        Module</Button>

                    {/*<Button onClick={() => {*/}

                    {/*}} style={{marginLeft: 10}} variant="contained"*/}
                    {/*        color="primary" {...checkPermissionStyle('edit', 'module-management', true, true)}>Save*/}
                    {/*    Sort</Button>*/}
                </div>

                <Input.Search allowClear onChange={event => onSearch(event.target.value)}
                              placeholder="Input search text" onSearch={onSearch} style={{width: 200}}/>
            </div>

            <div className="row kt-mt-15">
                <div className="col-md-12">
                    <div className="kt-section">
                        <div className="kt-section__content">
                            <Paper>
                                <Table>
                                    <TableHead>
                                        <TableRow>
                                            <TableCell>Index</TableCell>
                                            <TableCell style={{width: '50%'}}>Name</TableCell>
                                            <TableCell>Type</TableCell>
                                            <TableCell>Visible</TableCell>
                                            <TableCell>Status</TableCell>
                                            <TableCell style={{width: 150, textAlign: 'right'}}>Action</TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {
                                            loading ? (
                                                <TableRow>
                                                    <TableCell colSpan={10} align="center">
                                                        <CircularProgress size={20}/>
                                                    </TableCell>
                                                </TableRow>
                                            ) : null
                                        }
                                        {rows.length ? (
                                            rows.map((row, key) => (
                                                <TableRow key={key}>
                                                    <TableCell>{row.serial ? row.serial : indexPaging(key, page, limit)}</TableCell>
                                                    <TableCell>{row.name}</TableCell>
                                                    <TableCell
                                                        style={{textTransform: 'capitalize'}}>{row.type.toString()}</TableCell>
                                                    <TableCell>{row.isHidden ? 'Hide' : 'Show'}</TableCell>
                                                    <TableCell>{renderStatusText(row.status)}</TableCell>
                                                    <TableCell style={{width: 150, textAlign: 'right'}}>
                                                        <div className={'up-downs'}>
                                                            <button onClick={event => {
                                                                updatePosition(row.id, row.serial || row.serial === 0 ? (row.serial - 1) : (indexPaging(key, page, limit) - 1))
                                                            }} className={'up-down-item'}>
                                                                <i className={'fa fa-angle-up'}/>
                                                            </button>
                                                            <button onClick={event => {
                                                                updatePosition(row.id, row.serial || row.serial === 0 ? (row.serial + 1) : (indexPaging(key, page, limit) + 1))
                                                            }} className={'up-down-item'}>
                                                                <i className={'fa fa-angle-down'}/>
                                                            </button>
                                                        </div>

                                                        <ActionItem
                                                            icon={'fa fa-pen'} color={"#ffa800"}
                                                            onClick={() => {
                                                                onDetail(row)
                                                            }}
                                                            {...checkPermissionStyle('edit', 'module-management')}

                                                        />
                                                        <ActionItem
                                                            {...checkPermissionStyle('edit', 'module-management', true, true)}
                                                            icon={'fa fa-trash'} color={"rgb(220, 0, 78)"}
                                                            onClick={() => {
                                                                setModalDeleteVisible(row)
                                                            }}/>
                                                    </TableCell>
                                                </TableRow>
                                            ))
                                        ) : !loading ? (
                                            <TableRow>
                                                <TableCell colSpan={10} align="center">
                                                    No data
                                                </TableCell>
                                            </TableRow>
                                        ) : null}
                                    </TableBody>
                                </Table>
                                <div className={'paging-app'}>
                                    <Pagination showSizeChanger={true} pageSize={limit} current={page}
                                                onChange={(pageIndex, pageSize) => {
                                                    setPage(pageIndex);
                                                    setLimit(pageSize);
                                                }} defaultCurrent={1} total={total}/>
                                </div>
                            </Paper>

                        </div>
                    </div>
                </div>
            </div>

            <ModalAntd
                title="Delete Submodule"
                visible={!(!modalDeleteVisible)}
                onOk={() => onRemoveProgram()}
                onCancel={() => setModalDeleteVisible(null)}
                cancelText="Cancel"
                okText="Ok">
                <p>Do you want to delete this Sub Module?</p>
            </ModalAntd>
        </>
    );
}
