import React, { useState, useEffect } from "react";
import moment from "moment";
import {
  Paper,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  Button, ButtonGroup, Icon,
} from "@material-ui/core";
import { Form, Col } from "react-bootstrap";
import PropertyTypes from "../../../config/propertyTypes";
import ModalAntd from "../../../common/antd/component/Modal";
import { showSuccessMessageIcon, showErrorMessage, showSuccessMessage } from "../../../actions/notification";
import { ACTIVE_STATUS } from "../../../config/common/topic";
import SelectForm from "../../../partials/common/SelectForm";
import PropertiesService from "../../../services/PropertiesService";
import { getChanged } from "../../../services/AppService";
import { setHeaderTitle } from "../../../store/actions/HeaderAction";
import "../style.css";
import { useDispatch } from "react-redux";
import { useParams } from "react-router-dom";

export default function Topic(props) {
  const dispatch = useDispatch();
  let { pageType } = useParams();
  /**
   * State
   */
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [rows, setRow] = useState([]);
  const [dataSearch, setData] = useState({ name: "", Active: 0 });
  const [dataDelete, setDataDelete] = useState({ visible: false });
  const [dataAdd, setDataAdd] = useState({});
  const [dataUpdate, setDataUpdate] = useState({ visible: false });
  const [dataDetail, setDataDetail] = useState({ visible: false });
  const [total, setTotal] = useState(0);
  const [isLoadSubmit, setLoadSubmit] = useState(false);
  const [isLoad, setLoad] = useState(false);
  const [stackPage, setStackPage] = useState([]);
  const [propertyType, setPropertyType] = useState(pageType);
  /**
   * Effect
   */

  useEffect(() => {
    setPage(0);
    dispatch(setHeaderTitle(`${pageType === "ExamType" ? "Exam Type" : pageType} management`));
    searchTopic(null, true);
  }, [rowsPerPage, pageType]);

  const showModalAdd = () => {
    setDataAdd({
      ...dataAdd,
      visible: true,
    });
  };

  const clickModalAddCancel = () => {
    setDataAdd({
      visible: false,
    });
  };

  const onChangeAddValue = (key, value) => {
    setDataAdd({
      ...dataAdd,
      [key]: value,
    });
  };

  const searchTopic = (nextToken = "", isReset = false) => {
    PropertiesService.searchProperties({ nextToken, limit: rowsPerPage }, PropertyTypes[pageType]).then(value => {
      if (value) {
        setRow(value.data.items);
        if (!stackPage.find(x => x === value.data.nextToken)) {
          let stackPageNew = isReset ? [] : JSON.parse(JSON.stringify(stackPage));
          stackPageNew.push(value.data.nextToken);
          setStackPage(stackPageNew);
        }
      }
    });
  };

  const nextStackPage = () => {
    const nextToken = stackPage[page] || null;
    searchTopic(nextToken);
    setPage(page + 1);
  };

  const prevStackPage = async () => {
    if (page >= 1) {
      const nextToken = stackPage[page - 2] || null;
      searchTopic(nextToken);
      setPage(page - 1);
    }
  };

  const showModalUpdate = (row) => {
    setDataUpdate({
      ...row,
      visible: true,
    });
  };

