    });
  };

  const clickModalUpdateCancel = () => {
    setDataUpdate({
      ...dataUpdate,
      visible: false,
    });
  };

  const clickModalDetailCancel = () => {
    setDataDetail({
      ...dataDetail,
      visible: false,
    });
  };

  const clickModalCancel = () => {
    setDataDelete({
      ...dataDelete,
      visible: false,
      idDel: 0,
    });
  };

