    e.preventDefault();
    const result = await PropertiesService.createProperties({
      descriptions: dataAdd.descriptions,
      name: dataAdd.name,
    }, PropertyTypes[pageType]);
    if (result) {
      showSuccessMessageIcon(`Added ${(pageType === "ExamType" ? "Exam Type" : pageType)} Successfully!`);
      clickModalAddCancel();
    }
    searchTopic("", true);
    setPage(0);
    disableLoadSubmit(false);
  };

  const submitUpdate = async (e) => {
    if (!dataUpdate.name) {
      return showErrorMessage(`Please enter ${pageType === "ExamType" ? "Exam Type" : pageType} name`);
    }
    // changed

    const currentTopic = rows.find(topic => topic.id === dataUpdate.id);
    const changed = getChanged(currentTopic, dataUpdate);
    delete changed.visible;
    const result = await PropertiesService.updateProperties(dataUpdate.id, changed);
    if (result) {
      showSuccessMessage(`Updated ${pageType === "ExamType" ? "Exam Type" : pageType} Successfully!`);
      clickModalUpdateCancel();
    }
    searchTopic(stackPage[page - 1] || null);
    // setPage(0);
  };

  const onChangeUpdateValue = (key, value) => {
    setDataUpdate({
      ...dataUpdate,
      [key]: value,
    });
  };

  const clickModalOk = async () => {
    let idDel = dataDelete.idDel;
    const result = await PropertiesService.removeProperties(idDel);
    if (result) {
      showSuccessMessage(`Deleted ${pageType === "ExamType" ? "Exam Type" : pageType} Successfully!`);
      clickModalCancel();
    }
    searchTopic("", true);
    setPage(0);
  };

  const onChangeValueSearch = (key, value) => {
    setData({
      ...dataSearch,
      [key]: value,
    });
  };

  const disableLoadSubmit = () => {
    setLoadSubmit(false);
  };

  const handleChangePage = (newPage) => {
    setPage(newPage);
    if (isLoad) {
      searchTopic({ ...dataSearch, page: newPage, limit: rowsPerPage });
      return;
    }
    searchTopic({ page: newPage, limit: rowsPerPage });
    return;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    searchTopic(dataSearch, setPage(1));
    setLoad(true);
  };

  const unfilteredData = (e) => {
    setData({
      name: "",
    });
    setPage(1);
    searchTopic({ page: 1, limit: rowsPerPage });
    setLoad(false);
  };

  const handleSubmitAdd = async (e) => {
    e.preventDefault();
    if (!dataAdd.name) {
      return showErrorMessage("Name must not be empty!");
    }
  };

  const handleSubmitUpdate = async (e) => {
    e.preventDefault();

  };

