    <div>
      <Button onClick={showModalAdd} id="tooltip" variant="contained" color="primary">Add {pageType === "ExamType" ? "Exam Type" : pageType}</Button>
      <div className="row kt-mt-15">
        <div className="col-md-12">
          <div className="kt-section">
            <div className="kt-section__content">
              <Paper>
                {/*<div className="col-md-12">*/}
                {/*  <Form onSubmit={handleSubmit}>*/}
                {/*    <div style={{ marginTop: 20, fontSize: 20 }}>*/}
                {/*      <label>Search</label>*/}
                {/*    </div>*/}

                {/*    <div className="form-row">*/}
                {/*      <div className="form-group col-md-2">*/}
                {/*        <div className="form-group" style={{ display: "flex" }}>*/}
                {/*          <InputForm*/}
                {/*            type="text"*/}
                {/*            placeholder="Name"*/}
                {/*            value={dataSearch.name || ""}*/}
                {/*            onChangeValue={(value) => {*/}
                {/*              onChangeValueSearch("name", value);*/}
                {/*            }}*/}
                {/*            focus={true}*/}
                {/*          />*/}
                {/*        </div>*/}
                {/*      </div>*/}
                {/*      <div className="form-group col-md-2">*/}
                {/*        <div className="form-group" style={{ display: "flex" }}>*/}
                {/*          <button*/}
                {/*            className="btn btn-label-primary btn-bold btn-sm btn-icon-h kt-margin-l-10"*/}
                {/*            onClick={unfilteredData}*/}
                {/*            style={{ marginLeft: 10, marginTop: 3 }}*/}
                {/*            type="button">*/}
                {/*            <span>Clear</span>*/}
                {/*          </button>*/}
                {/*          <ButtonLoading*/}
                {/*            type="submit"*/}
                {/*            loading={isLoadSearch}*/}
                {/*            className="btn btn-label-primary btn-bold btn-sm btn-icon-h kt-margin-l-10"*/}
                {/*            style={{ marginLeft: 10, marginTop: 3 }}>*/}
                {/*            <span>Search</span>*/}
                {/*          </ButtonLoading>*/}
                {/*        </div>*/}
                {/*      </div>*/}
                {/*    </div>*/}
                {/*  </Form>*/}
                {/*</div>*/}
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell>No</TableCell>
                      <TableCell>Name</TableCell>
                      <TableCell>Description</TableCell>
                      <TableCell>Created date</TableCell>
                      <TableCell>Status</TableCell>
                      <TableCell style={{ width: 150 }}>Action</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {rows.length ? (
                      rows.map((row, key) => (
                        <TableRow key={key}>
                          <TableCell>{key + page * rowsPerPage + 1}</TableCell>
                          <TableCell>{row.name}</TableCell>
                          <TableCell>{row.descriptions}</TableCell>
                          <TableCell>{moment(row.createdAt).format("HH:mm DD-MM-YYYY")}</TableCell>
                          <TableCell>
                            {row.status === "Active" ? (
                              <span
                                className="btn btn-label-success btn-bold btn-sm btn-icon-h"
                                style={{ borderRadius: ".42rem" }}>
                                Active
                              </span>
                            ) : (
                              <span
                                className="btn btn-label-danger btn-bold btn-sm btn-icon-h"
                                style={{ borderRadius: ".42rem" }}>
                                Inactive
                              </span>
                            )}
                          </TableCell>

                          <TableCell>
                                 <span
                                   style={{ cursor: "pointer" }}
                                   data-toggle="tooltip"
                                   data-placement="top"
                                   title="Detail data">
                              <Icon
                                className="fa fa-info-circle"
                                onClick={(e) => showModalDetail(row)}
                                style={{ color: "#ffa800", fontSize: 15 }}
                              />
                            </span>
                            <span
                              style={{ cursor: "pointer" }}
                              data-toggle="tooltip"
                              data-placement="top"
                              title="Edit data">
                              <Icon
                                className="fa fa-pen"
                                onClick={(e) => showModalUpdate(row)}
                                style={{
                                  color: "#ffa800",
