              onOk={submitAdd}>
              <div className="row">
                <div className="col-md-12">
                  <div className="kt-section">
                    <Form onSubmit={handleSubmitAdd}>
                      <Form.Row>
                        <Form.Group as={Col} controlId="formBasicNameBank">
                          <Form.Label className="starDanger">Name</Form.Label>
                          <Form.Control
                            type="text"
                            autoFocus
                            maxLength={255}
                            placeholder={`Enter Name ${pageType === "ExamType" ? "Exam Type" : pageType}`}
                            value={dataAdd.name || ""}
                            onChange={(e) => onChangeAddValue("name", e.target.value)}
                            onBlur={(e) => onChangeAddValue("name", e.target.value.trim())}
                          />
                        </Form.Group>
                      </Form.Row>
                      <Form.Row>
                        <Form.Group as={Col}>
                          <Form.Label>Description</Form.Label>
                          <Form.Control
                            style={{ height: 100 }}
                            type="text"
                            as="textarea"
                            rows="5"
                            maxLength={500}
                            placeholder="Enter Description"
                            value={dataAdd.descriptions || ""}
                            onChange={(e) => onChangeAddValue("descriptions", e.target.value)}
                            onBlur={(e) => onChangeAddValue("descriptions", e.target.value.trim())}
                          />
                        </Form.Group>
                      </Form.Row>
                    </Form>
                  </div>
                </div>
              </div>
            </ModalAntd>
            <ModalAntd
              title={`Update ${pageType === "ExamType" ? "Exam Type" : pageType}`}
              visible={!!dataUpdate.visible}
              cancelText="Cancel"
              okText="Update"
              onCancel={clickModalUpdateCancel}
              onOk={submitUpdate}>
              <div className="row">
                <div className="col-md-12">
                  <div className="kt-section">
                    <Form onSubmit={handleSubmitUpdate}>
                      <Form.Row>
                        <Form.Group as={Col} controlId="formBasicNameBank">
                          <Form.Label className="starDanger">Name</Form.Label>
                          <Form.Control
                            type="text"
                            autoFocus
                            maxLength={255}
                            placeholder={`Enter name ${pageType === "ExamType" ? "Exam Type" : pageType}`}
                            value={dataUpdate.name || ""}
                            onChange={(e) => onChangeUpdateValue("name", e.target.value)}
                            onBlur={(e) => onChangeUpdateValue("name", e.target.value.trim())}
                          />
                        </Form.Group>
                      </Form.Row>
                      <Form.Row>
                        <Form.Group as={Col}>
                          <Form.Label className="starDanger">Status</Form.Label>
                          <SelectForm
                            optionData={ACTIVE_STATUS}
                            keyString="id"
                            labelString="name"
                            value={dataUpdate.status || ""}
                            onChangeValue={(value) => {
                              onChangeUpdateValue("status", value);
                            }}
                          />
                        </Form.Group>
                      </Form.Row>
                      <Form.Row>
                        <Form.Group as={Col} controlId="formBasicNameBank">
                          <Form.Label>Description</Form.Label>
                          <Form.Control
                            type="text"
                            as="textarea"
                            rows="5"
                            maxLength={255}
                            placeholder="Enter description"
                            value={dataUpdate.descriptions || ""}
                            onChange={(e) => onChangeUpdateValue("descriptions", e.target.value)}
                            onBlur={(e) => onChangeUpdateValue("descriptions", e.target.value.trim())}
                          />
                        </Form.Group>
                      </Form.Row>
                    </Form>
                  </div>
                </div>
              </div>
            </ModalAntd>
            <ModalAntd
              title={`Detail ${pageType === "ExamType" ? "Exam Type" : pageType}`}
              visible={dataDetail.visible}
              cancelText="Cancel"
              okText="OK"
              onCancel={clickModalDetailCancel}
              onOk={clickModalDetailCancel}
              cancelButtonProps={{ style: { display: "none" } }}>
