                <div className="col-md-12">
                  <div className="kt-section">
                    <Form>
                      <Form.Row>
                        <Form.Group as={Col} controlId="formBasicNameBank">
                          <Form.Label className="starDanger">Name</Form.Label>
                          <Form.Control
                            readOnly
                            type="text"
                            maxLength={255}
                            placeholder={`Enter name ${pageType === "ExamType" ? "Exam Type" : pageType}`}
