                          />
                        </Form.Group>
                      </Form.Row>
                      <Form.Row>
                        <Form.Group as={Col}>
                          <Form.Label>Description</Form.Label>
                          <Form.Control
                            readOnly
                            type="text"
                            as="textarea"
                            rows="5"
                            maxLength={255}
                            placeholder="Enter descriptions"
                            value={dataDetail.descriptions || ""}
                          />
                        </Form.Group>
                      </Form.Row>
                    </Form>
                  </div>
                </div>
              </div>
