export function getChanged(current, updated) {
    const data = {};
    for (const updatedKey in updated) {
        if (!!updatedKey && updated[updatedKey] !== current[updatedKey]) {
            data[updatedKey] = updated[updatedKey];
        }
    }
    return data;
}

export function getRemoveEmptyObject(ob) {
    const data = {};
    for (const updatedKey in ob) {
        if (!!updatedKey && ob[updatedKey] !== "" && ob[updatedKey] !== null) {
            data[updatedKey] = ob[updatedKey];
        }
    }
    return data;
}

export function getNameById(id, arr) {
    const data = arr.find(x => x.id === id);
    return data ? data.name : '';
}

export function loadPaging(page, limit) {
    return {page, limit}
}

export function indexPaging(key, page, limit) {
    return key + ((page - 1) * limit) + 1
}
