import React, {useState, useEffect} from "react";
import moment from "moment";
import {
    Paper,
    Table,
    TableHead,
    TableRow,
    TableCell,
    TableBody,
    Button, ButtonGroup, Icon, CircularProgress,
} from "@material-ui/core";
import {Form, Col} from "react-bootstrap";
import PropertyTypes from "../../config/propertyTypes";
import ModalAntd from "../../common/antd/component/Modal";
import {showSuccessMessageIcon, showErrorMessage, showSuccessMessage} from "../../actions/notification";
import {ACTIVE_STATUS} from "../../config/common/topic";
import SelectForm from "../../partials/common/SelectForm";
import PetService from "../../services/PetService";
import {getChanged} from "../../services/AppService";
import {setHeaderTitle} from "../../store/actions/HeaderAction";
import "./style.css";
import {useDispatch} from "react-redux";
import {useParams} from "react-router-dom";
import {Modal, Select} from "antd";
import {ImageUpload} from "../../common/upload/single";
import EmojiService from "../../services/EmojiService";
import ActionItem from "../../common/action";
import {checkPermission, checkPermissionStyle, checkPermissionTextView} from "../../services/CognitoService";

export default function EmojiType(props) {
    const dispatch = useDispatch();
    let {pageType} = useParams();
    /**
     * State
     */
    const [page, setPage] = useState(0);
    const [rowsPerPage, setRowsPerPage] = useState(10);
    const [rows, setRow] = useState();
    const [dataSearch, setData] = useState({name: "", Active: 0});
    const [dataDelete, setDataDelete] = useState({visible: false});
    const [dataAdd, setDataAdd] = useState({});
    const [dataUpdate, setDataUpdate] = useState({visible: false});
    const [dataDetail, setDataDetail] = useState({visible: false});
    const [total, setTotal] = useState(0);
    const [isLoadSubmit, setLoadSubmit] = useState(false);
    const [isLoad, setLoad] = useState(false);
    const [stackPage, setStackPage] = useState([]);
    const [propertyType, setPropertyType] = useState(pageType);
    /**
     * Effect
     */

    useEffect(() => {
        setPage(0);
        dispatch(setHeaderTitle(`Pet Type management`));
        searchTopic(null, true);
    }, [rowsPerPage, pageType]);

    const showModalAdd = () => {
        setDataAdd({
            visible: true,
            name: '',
            backgroundUrl: 'remove',
            imageUrl: 'remove',
        });
    };

    const clickModalAddCancel = () => {
        setDataAdd({
            visible: false,
            backgroundUrl: '',
            imageUrl: '',
        });
    };

    const onChangeAddValue = (key, value) => {
        setDataAdd({
            ...dataAdd,
            [key]: value,
        });
    };

    const searchTopic = (nextToken = "", isReset = false) => {
        PetService.search({nextToken, limit: rowsPerPage}, PropertyTypes[pageType]).then(value => {
            if (value) {
                setRow(value.data.items);
                if (!stackPage.find(x => x === value.data.nextToken)) {
                    let stackPageNew = isReset ? [] : JSON.parse(JSON.stringify(stackPage));
                    stackPageNew.push(value.data.nextToken);
                    setStackPage(stackPageNew);
                }
            }
        });
    };

    const nextStackPage = () => {
        const nextToken = stackPage[page] || null;
        searchTopic(nextToken);
        setPage(page + 1);
    };

    const prevStackPage = async () => {
        if (page >= 1) {
            const nextToken = stackPage[page - 2] || null;
            searchTopic(nextToken);
            setPage(page - 1);
        }
    };

    const showModalUpdate = (row) => {
        setDataUpdate({
            ...row,
            visible: true,
        });
    };

    const showModalDetail = (row) => {
        setDataDetail({
            ...row,
            visible: true,
        });
    };

    const showModal = (idDel) => {
        setDataDelete({
            ...dataDelete,
            visible: true,
            idDel,
        });
    };

    const clickModalUpdateCancel = () => {
        setDataUpdate({
            ...dataUpdate,
            visible: false,
        });
    };

    const clickModalDetailCancel = () => {
        setDataDetail({
            ...dataDetail,
            visible: false,
        });
    };

    const clickModalCancel = () => {
        setDataDelete({
            ...dataDelete,
            visible: false,
            idDel: 0,
        });
    };

    const submitAdd = async (e) => {
        e.preventDefault();
        if (!dataAdd.backgroundUrl) {
            return showErrorMessage(`Background must not be empty!`);
        }
        if (!dataAdd.imageUrl) {
            return showErrorMessage(`Avatar must not be empty!`);
        }
        const result = await PetService.create({
            name: dataAdd.name,
            status: 'active',
            backgroundUrl: dataAdd.backgroundUrl,
            imageUrl: dataAdd.imageUrl,
        }, PropertyTypes[pageType]);
        if (result) {
            showSuccessMessageIcon(`Added Pet Type Successfully!`);
            clickModalAddCancel();
        }
        searchTopic("", true);
        setPage(0);
        disableLoadSubmit(false);
    };

    const submitUpdate = async (e) => {
        if (!dataUpdate.name) {
            return showErrorMessage(`Please enter Pet Type name`);
        }

        if (!dataUpdate.backgroundUrl) {
            return showErrorMessage(`Background must not be empty!`);
        }
        if (!dataUpdate.imageUrl) {
            return showErrorMessage(`Avatar must not be empty!`);
        }
        // changed
        console.log(dataUpdate);
        const currentTopic = rows.find(topic => topic.id === dataUpdate.id);
        const changed = getChanged(currentTopic, dataUpdate);
        changed.status = 'active';
        delete changed.visible;
        const result = await PetService.update(dataUpdate.id, changed);
        if (result) {
            showSuccessMessage(`Updated Pet Type Successfully!`);
            clickModalUpdateCancel();
        }
        searchTopic(stackPage[page - 1] || null);
        // setPage(0);
    };

    const onChangeUpdateValue = (key, value) => {
        setDataUpdate({
            ...dataUpdate,
            [key]: value,
        });
    };

    const clickModalOk = async () => {
        let idDel = dataDelete.idDel;
        const result = await PetService.remove(idDel);
        if (result) {
            showSuccessMessage(`Deleted Pet Type Successfully!`);
            clickModalCancel();
        }
        searchTopic("", true);
        setPage(0);
    };

    const onChangeValueSearch = (key, value) => {
        setData({
            ...dataSearch,
            [key]: value,
        });
    };

    const disableLoadSubmit = () => {
        setLoadSubmit(false);
    };

    const handleChangePage = (newPage) => {
        setPage(newPage);
        if (isLoad) {
