import React, {useState, useEffect} from "react";
import {showErrorMessage, showSuccessMessage} from "../../actions/notification";
import {Card, Col, Row} from "react-bootstrap";
import {InputNumber, Modal, Space} from "antd";
import {useDispatch, useSelector} from "react-redux";
import {setHeaderTitle} from "../../store/actions/HeaderAction";
import {Form, Input, Button, Select} from 'antd';
import "./question.css";
import {useHistory, useParams} from "react-router-dom";
import {getChanged} from "../../services/AppService";
import {CircularProgress} from "@material-ui/core";
import {MinusCircleOutlined, PlusOutlined} from "@ant-design/icons";
import QuestionService from "../../services/QuestionService";
import EmojiStateService from "../../services/EmojiStateService";
import * as XLSX from "xlsx";
import {checkPermissionStyle, checkPermissionTextView} from "../../services/CognitoService";

const {Option} = Select;

export const FREE_TEXT = 'free-text';


export default function AddQuestion(props) {
    const dispatch = useDispatch();
    const history = useHistory();
    const [modalCancelVisible, setModalCancelVisible] = useState(false);
    const [data, setData] = useState();
    const [workouts, setWorkouts] = useState([]);
    let {id} = useParams();

    const user = useSelector(state => state.auth.user);
    const [states, setStates] = useState([]);
    const [limit, setLimit] = useState(true);
    const [type, setType] = useState('choice');
    const [form] = Form.useForm();
    const injectMode = props.inject;
    const onSubmitUpdate = props.onSubmit;
    const onCancelUpdate = props.onCancel;
    id = injectMode ? props.id : id;
    const isEditMode = !(!id);
    console.log('dđ', id);

    useEffect(() => {
        dispatch(setHeaderTitle(isEditMode ? `${checkPermissionTextView('edit', 'question-management')} Question` : "Add New Question"));
        EmojiStateService.search({all: true}).then(value => {
            setStates(value.data.items);
        })
    }, []);

    useEffect(() => {
        if (isEditMode) {
            QuestionService.getQuestion(id).then(value => {
                if (value) {
                    setData(value.data);
                    form.setFieldsValue(value.data);
                    setType(value.data.type);
                    setLimit(value.data.allowUnlimitedChoice);
                }
            })
        }
    }, [isEditMode, id]);


    const onCancel = () => {
        if (!injectMode) {
            props.history.goBack();
        } else {
            onCancelUpdate ? onCancelUpdate() : null;
        }

    };

    const onFinish = (values) => {
        values.title = (values.title || '').trim();
        values.contents = (values.contents || '').trim();
        if (!values.title) {
            return showErrorMessage("Title must not be empty!");
        }
        if (!values.stateId) {
            return showErrorMessage("State must not be empty!");
        }
        if (!values.contents) {
            return showErrorMessage("Content must not be empty!");
        }
        if (!values.quantityChoice) {
            values.quantityChoice = 1;
        }

        if (values.answers) {
            values.answers = values.answers.filter(x => {
                x.value = (x.value || '').trim();
                return x;
            }).filter(x => {
                return !(!x.value);
            });
        }

        if ((!values.answers || values.answers.length <= 0) && values.type !== 'linear-slider' && values.type !== FREE_TEXT) {
            return showErrorMessage("Answers must not be empty!");
        }

        if (values.type === 'linear-slider' || values.type === FREE_TEXT) {
            values.answers = undefined
        }

        if (values.type !== 'search-selection') {
            delete values.allowUnlimitedChoice;
            delete values.quantityChoice;
        }

        if (isEditMode) {
            const changed = getChanged(data, values);
            if (values.type === 'linear-slider') {
                changed.ranges = getChanged(data.ranges, changed.ranges);
                if (Object.keys(changed.ranges).length === 0) {
                    delete changed.ranges;
                }
            }

            if (values.type !== 'linear-slider' && values.type !== FREE_TEXT) {
                let changedAnswers = false;
                if (data.answers.length === values.answers.length) {
                    for (let i = 0; i < changed.answers.length; i++) {

                        if (changed.answers[i].value !== data.answers[i].value) {
                            changedAnswers = true;
                        }
                    }
                } else {
                    changedAnswers = true;
                }
                if (!changedAnswers) {
                    delete changed.answers;
                }
            }

            QuestionService.updateQuestion(data.id, changed).then(value => {
                if (value) {
                    showSuccessMessage("Updated Question Successfully!!");
                    if (!injectMode) {
                        history.goBack();
                    } else {
                        setData({...data, ...changed});
                        onSubmitUpdate ? onSubmitUpdate({...data, ...changed}) : null;
                    }
                }
            })
        } else {
            QuestionService.createQuestion(values).then(value => {
                if (value) {
                    showSuccessMessage("Added Question Successfully!!");
                    history.goBack();
                }
            })
        }
    };

    const onFinishFailed = (errorInfo) => {
        console.log('Failed:', errorInfo);
    };

    const handleUpload = (e) => {
        e.preventDefault();

        const files = e.target.files, f = files[0];
        const reader = new FileReader();
        if (f.name.indexOf('.xlsx') > 0 || f.name.indexOf('.xls') > 0 || f.name.indexOf('.csv') > 0) {
            reader.onload = (e) => {
                var data = e.target.result;
                let readedData = XLSX.read(data, {type: 'binary'});
                const wsname = readedData.SheetNames[0];
                const ws = readedData.Sheets[wsname];

                /* Convert array to json*/
                const dataParse = XLSX.utils.sheet_to_json(ws, {header: 1});
                console.log(dataParse);
                const answers = form.getFieldsValue().answers || [];
                dataParse.map((value, index) => {
                    if (index > 0) {
                        answers.push({
                            key: answers.length,
                            value: value[0]
                        });
                    }
                });
                form.setFieldsValue({answers});
            };
            reader.readAsBinaryString(f)
        } else {
            showErrorMessage('Only accept .xlsx/.xls/.csv files')
        }

    }

    return (
        <>
            {
                !isEditMode || data ? (
                    <Form
                        form={form}
                        initialValues={data}
                        layout="vertical"
                        onFinish={onFinish}
                        onFinishFailed={onFinishFailed}
                    >
                        <div className="row">
                            <div className="col-md-12">
                                <div className={`kt-section ${injectMode ? 'inject-mode' : ''}`}>
                                    <Card>
                                        <Card.Body>

                                            <Row>
                                                <Col>
                                                    <Form.Item label="Title" name="title">
                                                        <Input/>
                                                    </Form.Item>
                                                </Col>
                                            </Row>
                                            <Row style={{display: injectMode ? 'none' : 'flex'}}>
                                                <Col>
                                                    <Form.Item label={'Type'} name="type" initialValue={'choice'}>
                                                        <Select disabled={isEditMode}
                                                                onChange={value => setType(value)}>
                                                            <Select.Option value={'choice'}>Multiple
                                                                Choice</Select.Option>
                                                            <Select.Option value={'multiple-selection'}>Multi
                                                                Selection</Select.Option>
                                                            <Select.Option
                                                                value={'linear-slider'}>Slider</Select.Option>
                                                            <Select.Option
                                                                value={'search-selection'}>Search &
                                                                Selection</Select.Option>
                                                            <Select.Option
                                                                value={'free-text'}>Free Text</Select.Option>
                                                        </Select>
                                                    </Form.Item>
                                                </Col>
                                            </Row>
                                            {
                                                type === 'linear-slider' ? (
                                                    <>
                                                        <Row>
                                                            <Col>
                                                                <Form.Item label={'Min'}
                                                                           name={['ranges', 'minRange']}>
                                                                    <InputNumber
                                                                        style={{width: '100%'}}/>
                                                                </Form.Item>
                                                            </Col>
                                                        </Row>
                                                        <Row>
                                                            <Col>
                                                                <Form.Item label={'Max'}
                                                                           name={['ranges', 'maxRange']}>
                                                                    <InputNumber
                                                                        style={{width: '100%'}}/>
                                                                </Form.Item>
                                                            </Col>
                                                        </Row>
                                                        <Row>
                                                            <Col>
                                                                <Form.Item label={'Min label'}
                                                                           name={['ranges', 'minTitle']}>
                                                                    <Input
                                                                        style={{width: '100%'}}/>
                                                                </Form.Item>
                                                            </Col>
                                                        </Row>
                                                        <Row>
                                                            <Col>
                                                                <Form.Item label={'Max label'}
                                                                           name={['ranges', 'maxTitle']}>
                                                                    <Input
                                                                        style={{width: '100%'}}/>
                                                                </Form.Item>
                                                            </Col>
                                                        </Row>
                                                    </>
                                                ) : null
                                            }

                                            <Row>
                                                <Col>
                                                    <Form.Item label="Content" name="contents">
                                                        <Input.TextArea rows={3}/>
                                                    </Form.Item>
                                                </Col>
                                            </Row>
                                            <Row>
                                                <Col>
                                                    <Form.Item label="State" name="stateId">
                                                        <Select>
                                                            {states.map((value, index) => (
                                                                <Select.Option key={index.toString()}
                                                                               value={value.id}>{value.name}</Select.Option>
                                                            ))}
                                                        </Select>
                                                    </Form.Item>
                                                </Col>
                                            </Row>
                                            {
                                                type === 'free-text' ? (
                                                    <>
                                                        <Row>
                                                            <Col>
                                                                <Form.Item label="Allow Multiple Answers"
                                                                           name="allowMultipleFreeText"
                                                                           initialValue={false}
                                                                >
                                                                    <Select>
                                                                        <Select.Option key={1}
                                                                                       value={true}>Yes</Select.Option>
                                                                        <Select.Option key={2}
                                                                                       value={false}>No</Select.Option>
                                                                    </Select>
                                                                </Form.Item>
                                                            </Col>
                                                        </Row>
                                                        <Row>
                                                            <Col>
                                                                <Form.Item label="Required answer"
                                                                           name="requiredContentFreeText"
                                                                           initialValue={false}
                                                                >
                                                                    <Select>
                                                                        <Select.Option key={1}
                                                                                       value={true}>Mandatory</Select.Option>
                                                                        <Select.Option key={2}
                                                                                       value={false}>Optional</Select.Option>
                                                                    </Select>
                                                                </Form.Item>
                                                            </Col>
                                                        </Row>
                                                    </>
                                                ) : null
                                            }
                                            {
                                                type === 'search-selection' ? (
                                                    <Row>
                                                        <Col>
                                                            <Form.Item label="Allow Unlimited Choice"
                                                                       name="allowUnlimitedChoice" initialValue={true}>
                                                                <Select onChange={(value) => setLimit(value)}>
                                                                    <Select.Option key={1}
                                                                                   value={true}>Yes</Select.Option>
                                                                    <Select.Option key={2}
                                                                                   value={false}>No</Select.Option>
                                                                </Select>
                                                            </Form.Item>
                                                        </Col>
                                                        {
                                                            !limit ? (
                                                                <Col>
                                                                    <Form.Item label="Quantity Choice"
                                                                               name="quantityChoice" initialValue={1}>
                                                                        <InputNumber
                                                                            formatter={value => value.replace('.', '').replace(',', '').replace('-', '')}
                                                                            min={1} style={{width: '100%'}}
                                                                            size={"large"}/>
                                                                    </Form.Item>
                                                                </Col>
                                                            ) : null
                                                        }
                                                    </Row>
                                                ) : null
                                            }
                                            {
                                                type !== 'linear-slider' && type !== 'free-text' ? (
                                                    <Row>
                                                        <Col>
                                                            <div className={'ant-col ant-form-item-label'}>
                                                                <label>Answers</label>
                                                                <input accept=".xls,.xlsx,.csv"
                                                                       onChange={event => handleUpload(event)}
                                                                       id='file-upload'
                                                                       type={'file'} style={{display: 'none'}}/>
                                                                {
                                                                    type === 'search-selection' ? (
                                                                        <>
                                                                            <Button className={'btn-import'}
                                                                                    size={"small"}
                                                                                    onClick={event => {
                                                                                        document.querySelector('#file-upload').click();
                                                                                    }}>Import answers</Button>
                                                                            <Button className={'btn-import'}
                                                                                    size={"small"}
                                                                                    onClick={event => {
                                                                                        window.open(`/ListAnswer.xlsx`);
                                                                                    }}>Download sample</Button>
                                                                        </>
                                                                    ) : null
                                                                }
                                                            </div>
                                                            <Form.List name="answers">
                                                                {(fields, {add, remove}) => (
                                                                    <>
                                                                        {fields.map(({
                                                                                         key,
                                                                                         name,
                                                                                         fieldKey,
                                                                                         ...restField
                                                                                     }) => (
                                                                            <div key={key}
                                                                                 style={{
                                                                                     display: 'flex',
                                                                                     marginBottom: 8
                                                                                 }}
                                                                                 align="baseline">
                                                                                <Form.Item
                                                                                    {...restField}
                                                                                    style={{flex: 1, width: '100%'}}
                                                                                    name={[name, 'value']}
                                                                                    fieldKey={[fieldKey, 'value']}
                                                                                >
                                                                                    <Input
                                                                                        style={{flex: 1, width: '100%'}}
                                                                                        placeholder="Answer content"/>
                                                                                </Form.Item>
                                                                                <Form.Item
                                                                                    initialValue={key}
                                                                                    hidden={true}
                                                                                    name={[name, 'key']}
                                                                                    style={{marginLeft: 10}}
                                                                                    fieldKey={[fieldKey, 'key']}
                                                                                >
                                                                                    <Input/>
                                                                                </Form.Item>
                                                                                <MinusCircleOutlined
                                                                                    style={{
                                                                                        height: 38,
                                                                                        display: "flex",
                                                                                        alignItems: "center",
                                                                                        justifyContent: "center",
                                                                                        marginLeft: 10
                                                                                    }}
                                                                                    onClick={() => remove(name)}/>
                                                                            </div>
                                                                        ))}
                                                                        <Form.Item>
                                                                            <Button type="dashed" onClick={() => add()}
                                                                                    block
                                                                                    icon={<PlusOutlined style={{
                                                                                        height: 38,
                                                                                        display: "inline-flex",
                                                                                        alignItems: "center",
                                                                                        justifyContent: "center",
                                                                                        marginLeft: 10
                                                                                    }}/>}>
                                                                                Add answer
                                                                            </Button>
                                                                        </Form.Item>
                                                                    </>
                                                                )}
                                                            </Form.List>
                                                        </Col>
                                                    </Row>
                                                ) : null
                                            }


                                            <Form.Item style={{marginTop: 20}}>
                                                <Button
                                                    style={{marginRight: 12}} {...checkPermissionStyle('edit', 'question-management', true, isEditMode)}
                                                    type="primary" htmlType="submit">
                                                    Submit
                                                </Button>

                                                <Button htmlType="button"
                                                        onClick={() => injectMode ? onCancel() : setModalCancelVisible(true)}>
                                                    Cancel
                                                </Button>
                                            </Form.Item>
                                        </Card.Body>
                                    </Card>
                                    <Modal
                                        title={`Cancel ${isEditMode ? 'Edit' : 'Add'} Question`}
                                        visible={modalCancelVisible}
                                        onOk={() => onCancel()}
                                        onCancel={() => setModalCancelVisible(false)}
                                        cancelText="Cancel"
                                        okText="Ok">
                                        <p>Do you want to cancel and quit ?</p>
                                    </Modal>
                                </div>
                            </div>
                        </div>
                    </Form>
                ) : <CircularProgress size={20}/>
            }
        </>
    );
};
