import React, {useEffect, useState} from "react";
import {showErrorMessage, showSuccessMessage} from "../../actions/notification";
import {Card, Col, Row} from "react-bootstrap";
import {Button, Form, Input, Modal, Select} from "antd";
import {useDispatch} from "react-redux";
import {setHeaderTitle} from "../../store/actions/HeaderAction";
import "./field.css";
import {useHistory, useParams} from "react-router-dom";
import {getChanged} from "../../services/AppService";
import {CircularProgress} from "@material-ui/core";
import {checkPermissionStyle, checkPermissionTextView} from "../../services/CognitoService";
import FormService from "../../services/FormService";
import ModuleService from "../../services/ModuleService";

export default function Add(props) {
    const dispatch = useDispatch();
    const history = useHistory();
    const [modalCancelVisible, setModalCancelVisible] = useState(false);
    const [data, setData] = useState();
    const [modules, setModules] = useState();
    const {id, businessId} = useParams();
    const isEditMode = !(!id);
    const [hideModules, setHideModules] = useState([]);
    const [form] = Form.useForm();

    useEffect(() => {
        dispatch(setHeaderTitle(isEditMode ? `${checkPermissionTextView('edit', 'form-generation')} Form` : "Add New Form"));
        initData().then();
    }, [dispatch, isEditMode]);

    useEffect(() => {
        if (isEditMode) {
            FormService.getForm(id).then(value => {
                if (value) {
                    setData(value.data);
                    form.setFieldsValue(value.data.form);
                }
            })
        }
    }, [isEditMode]);


    const initData = async () => {
        ModuleService.search({businessId, all: true}).then(value => {
            setModules(value.data.items.sort((a, b) => {
                return a.name.localeCompare(b.name);
            }));
        })
    }


    const onCancel = () => {
        props.history.goBack();
    };

    const onFinish = (values) => {
        values.name = (values.name || '').trim();

        if (!values.name) {
            return showErrorMessage("Name must not be empty!");
        }

        values.businessId = businessId;

        if (isEditMode) {
            const changed = getChanged(data, values);
            if (changed.type !== 'multiple-choice') {
                changed.answers = undefined
            }
            FormService.updateForm(id, changed).then(value => {
                if (value) {
                    showSuccessMessage("Updated Form Successfully!!");
                    history.goBack();
                }
            })
        } else {
            FormService.createForm(values).then(value => {
                if (value) {
                    showSuccessMessage("Added Form Successfully!!");
                    history.goBack();
                }
            })
        }
    };

    const onFinishFailed = (errorInfo) => {
        console.log('Failed:', errorInfo);
    };


    return (
        <>
            {
                !isEditMode || data ? (
                    <Form
                        form={form}
                        initialValues={data}
                        layout="vertical"
                        onFinish={onFinish}
                        onFinishFailed={onFinishFailed}
                    >
                        <div className="row">
                            <div className="col-md-12">
                                <div className="kt-section">
                                    <Card>
                                        <Card.Body>
                                            <Row>
                                                <Col>
                                                    <Form.Item label="Name" name="name">
                                                        <Input/>
                                                    </Form.Item>
                                                </Col>
                                            </Row>
                                            <Row>
                                                <Col>
                                                    <Form.Item label="Module" name="moduleId">
                                                        <Select
                                                            onChange={(value, option) => {
                                                                let rs = (form.getFieldsValue().modules || []).map(x => (x || {}).moduleId);
                                                                setHideModules(rs);
                                                            }}
                                                            showSearch
                                                            filterOption={(input, option) =>
                                                                option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                                                            }
                                                            allowClear
                                                            style={{width: '100%'}}>
                                                            {modules ? modules.map((value, index) => {
                                                                let rs = [...hideModules];
                                                                return (
                                                                    <Select.Option
                                                                        disabled={!!rs.find(y => y === value.id)}
                                                                        key={index}
                                                                        value={value.id}>{value.name}</Select.Option>
                                                                )
                                                            }) : []}
                                                        </Select>
                                                    </Form.Item>
                                                </Col>
                                            </Row>

                                            <Row>
                                                <Col>
                                                    <Form.Item label="Status" name="status">
                                                        <Select>
                                                            <Select.Option value={'active'}>Active</Select.Option>
                                                            <Select.Option value={'inactive'}>Inactive</Select.Option>
                                                        </Select>
                                                    </Form.Item>
                                                </Col>
                                            </Row>

                                            <Form.Item>
                                                <Button style={{marginRight: 12}} type="primary"
                                                        htmlType="submit" {...checkPermissionStyle('edit', 'form-generation', true, isEditMode)}>
                                                    Submit
                                                </Button>
                                                <Button htmlType="button"
                                                        onClick={() => setModalCancelVisible(true)}>
                                                    Cancel
                                                </Button>
                                            </Form.Item>
                                        </Card.Body>
                                    </Card>
                                    <Modal
                                        title={`Cancel ${isEditMode ? 'Edit' : 'Add'} Form`}
                                        visible={modalCancelVisible}
                                        onOk={() => onCancel()}
                                        onCancel={() => setModalCancelVisible(false)}
                                        cancelText="Cancel"
                                        okText="Ok">
                                        <p>Do you want to cancel and quit ?</p>
                                    </Modal>
                                </div>
                            </div>
                        </div>
                    </Form>
                ) : <CircularProgress size={20}/>
            }
        </>
    );
};
