import React, {Component, useState} from "react";
import {Formik} from "formik";
import {connect, useDispatch} from "react-redux";
import {TextField} from "@material-ui/core";
import {Link, Redirect, useHistory} from "react-router-dom";
import {FormattedMessage, injectIntl} from "react-intl";
import * as auth from "../../store/ducks/auth.duck";
import {Auth} from "aws-amplify";
import {showErrorMessage, showSuccessMessage} from "../../actions/notification";
import {toAbsoluteUrl} from "../../../_metronic";
import ReCAPTCHA from "react-google-recaptcha";
import {setAppLoading} from "../../store/actions/HeaderAction";

function ForgotPassword(props) {
    const [isRequested, setIsRequested] = useState(false);
    const [email, setEmail] = useState("");
    const history = useHistory();
    const dispatch = useDispatch();
    const [loginAble, setLoginAble] = useState(false);

    const submit = (values, {setStatus, setSubmitting}) => {
        dispatch(setAppLoading(true));
        Auth.forgotPassword(values.email).then((value) => {
            if (value && value.Destination) {
                showSuccessMessage(`Sent code to ${value.Destination}`);
            }
            setIsRequested(true);
            setEmail(values.email);
            setSubmitting(false);
        }).catch((reason) => {
            showErrorMessage(reason.message);
            setSubmitting(false);
        }).finally(() => {
            dispatch(setAppLoading(false));
        });
    };
    const submitChange = (values, {setStatus, setSubmitting}) => {

        if (values.rePassword !== values.password) {
            setSubmitting(false);
            return showErrorMessage('Password not match!');
        }
        dispatch(setAppLoading(true));
        Auth.forgotPasswordSubmit(values.email, values.code, values.password).then((value) => {
            showSuccessMessage(`Change password success`);
            history.push("/login");
        }).catch((reason) => {
            showErrorMessage(reason.message);
            setSubmitting(false);
        }).finally(() => {
            dispatch(setAppLoading(false));
        });
    };

    return (
        <div
            className="kt-grid__item kt-grid__item--fluid  kt-grid__item--order-tablet-and-mobile-1  kt-login__wrapper">
            <div className="kt-login__body">
                <div className="kt-login__form">
                    <div className="kt-login__title">
                        <img src={toAbsoluteUrl("/media/bg/logo.svg")}/>
                        <h3>
                            Layla Content Management System
                        </h3>
                    </div>

                    {!isRequested ? (
                        <Formik
                            initialValues={{email: ""}}
                            onSubmit={(values, {setStatus, setSubmitting}) =>
                                submit(values, {setStatus, setSubmitting})
                            }>
                            {({
                                  values,
                                  status,
                                  errors,
                                  touched,
                                  handleChange,
                                  handleBlur,
                                  handleSubmit,
                                  isSubmitting,
                              }) => (
                                <form onSubmit={handleSubmit} className="kt-form">
                                    <div role="alert" className="alert alert-info">
                                        <div className="alert-text">
                                            Enter email or username to recover password
                                        </div>
                                    </div>


                                    <div className="form-group">
                                        <input
                                            placeholder={'Email/Username'}
                                            autoFocus
                                            type="text"
                                            className="kt-width-full"
                                            name="email"
                                            onBlur={handleBlur}
                                            onChange={handleChange}
                                            value={(values.email || '').split(' ').join('')}
                                        />
                                    </div>
                                    <div className="form-group">
                                        <ReCAPTCHA sitekey={process.env.RECAPTCHA}
                                                   onChange={() => {
                                                       setLoginAble(true);
                                                   }}
                                        />
                                    </div>

                                    <div className="kt-login__actions">
                                        <button id="kt_login_signin_submit" type="submit" disabled={!loginAble}>
                                            Sent Code
                                        </button>

                                    </div>
                                    <Link to={'/auth/login'} className={'backLogin'}>Back to Login</Link>
                                </form>
                            )}
                        </Formik>
                    ) : (
                        <Formik
                            initialValues={{code: "", password: "", rePassword: ""}}
                            onSubmit={(values, {setStatus, setSubmitting}) =>
                                submitChange(values, {setStatus, setSubmitting})
                            }>
                            {({
                                  values,
                                  status,
                                  errors,
                                  touched,
                                  handleChange,
                                  handleBlur,
                                  handleSubmit,
                                  isSubmitting,
                              }) => (
                                <form onSubmit={handleSubmit} className="kt-form">
                                    <div role="alert" className="alert alert-info">
                                        <div className="alert-text">We sent you the password reset code via email</div>
                                    </div>

                                    <div className="form-group">
                                        <input
                                            placeholder={'Code'}
                                            autoFocus
                                            type="text"
                                            className="kt-width-full"
                                            name="code"
                                            onBlur={handleBlur}
                                            onChange={handleChange}
                                            value={(values.code || "").split(' ').join('')}
                                        />
                                    </div>

                                    <div className="form-group">
                                        <input
                                            placeholder={'New password'}
                                            autoFocus
                                            type="password"
                                            className="kt-width-full"
                                            name="password"
                                            onBlur={handleBlur}
                                            onChange={handleChange}
                                            value={(values.password || "").split(' ').join('')}
                                        />
                                    </div>

                                    <div className="form-group">
                                        <input
                                            placeholder={'Retype new password'}
                                            autoFocus
                                            type="password"
                                            className="kt-width-full"
                                            name="rePassword"
                                            onBlur={handleBlur}
                                            onChange={handleChange}
                                            value={(values.rePassword || "").split(' ').join('')}
                                        />
                                    </div>

                                    <div className="kt-login__actions">
                                        <button id="kt_login_signin_submit" type="submit">
                                            Change password
                                        </button>
                                    </div>
                                    <Link to={'/auth/login'} className={'backLogin'}>Back to Login</Link>

                                </form>
                            )}
                        </Formik>
                    )}
                </div>
            </div>
        </div>
    );
}

export default injectIntl(connect(null, auth.actions)(ForgotPassword));
