import React, {useState, useEffect} from "react";
import {showErrorMessage, showSuccessMessage} from "../../actions/notification";
import {Card, Col, Row} from "react-bootstrap";
import {Checkbox, InputNumber, Modal, Space} from "antd";
import {useDispatch, useSelector} from "react-redux";
import {setHeaderTitle} from "../../store/actions/HeaderAction";
import {Form, Input, Button, Select} from 'antd';
import "./field.css";
import PropertyTypes from "../../config/propertyTypes";
import {useHistory, useParams} from "react-router-dom";
import {getChanged} from "../../services/AppService";
import {ImageUpload} from "../../common/upload/single";
import {CircularProgress} from "@material-ui/core";
import {PropertiesSelect} from "../exercise/Add";
import WorkoutService from "../../services/WorkoutService";
import {MinusCircleOutlined, PlusOutlined} from "@ant-design/icons";
import ProgramService from "../../services/ProgramService";
import FieldService from "../../services/FieldService";
import EmojiStateService from "../../services/EmojiStateService";
import {checkPermissionStyle, checkPermissionTextView} from "../../services/CognitoService";

const {Option} = Select;


export default function Add(props) {
    const dispatch = useDispatch();
    const history = useHistory();
    const [modalCancelVisible, setModalCancelVisible] = useState(false);
    const [data, setData] = useState();
    const [workouts, setWorkouts] = useState([]);
    const {id, businessId} = useParams();
    const isEditMode = !(!id);
    const user = useSelector(state => state.auth.user);
    const [states, setStates] = useState([]);
    const [type, setType] = useState('');

    useEffect(() => {
        dispatch(setHeaderTitle(isEditMode ? `${checkPermissionTextView('edit', 'biz-option-fields')} Field` : "Add New Field"));
    }, []);

    useEffect(() => {
        if (isEditMode) {
            FieldService.getField(id, businessId).then(value => {
                if (value) {
                    setData(value.data);
                    setType(value.data.type);
                }
            })
        }
    }, [isEditMode]);


    const onCancel = () => {
        props.history.goBack();
    };

    const onFinish = (values) => {
        values.name = (values.name || '').trim();

        if (!values.name) {
            return showErrorMessage("Name must not be empty!");
        }

        if (values.answers) {
            values.answers = values.answers.filter(x => {
                x.value = (x.value || '').trim();
                return x;
            }).filter(x => {
                return !(!x.value);
            });
        }

        if ((!values.answers || values.answers.length <= 0) && values.type === 'multiple-choice') {
            return showErrorMessage("Options must not be empty!");
        }


        if (isEditMode) {
            const changed = getChanged(data, values);
            if (changed.type !== 'multiple-choice') {
                changed.answers = undefined
            }
            FieldService.updateField(data.id, changed, businessId).then(value => {
                if (value) {
                    showSuccessMessage("Updated Field Successfully!!");
                    history.goBack();
                }
            })
        } else {
            FieldService.createField(values, businessId).then(value => {
                if (value) {
                    showSuccessMessage("Added Field Successfully!!");
                    history.goBack();
                }
            })
        }
    };

    const onFinishFailed = (errorInfo) => {
        console.log('Failed:', errorInfo);
    };


    return (
        <>
            {
                !isEditMode || data ? (
                    <Form
                        initialValues={data}
                        layout="vertical"
                        onFinish={onFinish}
                        onFinishFailed={onFinishFailed}
                    >
                        <div className="row">
                            <div className="col-md-12">
                                <div className="kt-section">
                                    <Card>
                                        <Card.Body>
                                            <Row>
                                                <Col>
                                                    <Form.Item label="Name" name="name">
                                                        <Input/>
                                                    </Form.Item>
                                                </Col>
                                            </Row>
                                            <Row>
                                                <Col>
                                                    <Form.Item label="Type" name="type">
                                                        <Select onChange={value => setType(value)}>
                                                            <Select.Option value={'free-text'}>Free Text</Select.Option>
                                                            <Select.Option value={'multiple-choice'}>Multiple
                                                                Choice</Select.Option>
                                                        </Select>
                                                    </Form.Item>
                                                </Col>
                                            </Row>

                                            {
                                                type === 'multiple-choice' ? (
                                                    <Row>
                                                        <Col>
                                                            <label>Options</label>
                                                            <Form.List name="answers">
                                                                {(fields, {add, remove}) => (
                                                                    <>
                                                                        {fields.map(({
                                                                                         key,
                                                                                         name,
                                                                                         fieldKey,
                                                                                         ...restField
                                                                                     }) => (
                                                                            <div key={key}
                                                                                 style={{
                                                                                     display: 'flex',
                                                                                     marginBottom: 8
                                                                                 }}
                                                                                 align="baseline">
                                                                                <Form.Item
                                                                                    {...restField}
                                                                                    style={{flex: 1, width: '100%'}}
                                                                                    name={[name, 'value']}
                                                                                    fieldKey={[fieldKey, 'value']}
                                                                                >
                                                                                    <Input
                                                                                        style={{flex: 1, width: '100%'}}
                                                                                        placeholder={`Option ${fieldKey + 1}`}/>
                                                                                </Form.Item>
                                                                                <Form.Item
                                                                                    initialValue={key}
                                                                                    hidden={true}
                                                                                    name={[name, 'key']}
                                                                                    style={{marginLeft: 10}}
                                                                                    fieldKey={[fieldKey, 'key']}
                                                                                >
                                                                                    <Input/>
                                                                                </Form.Item>
                                                                                <MinusCircleOutlined
                                                                                    style={{
                                                                                        height: 38,
                                                                                        display: "flex",
                                                                                        alignItems: "center",
                                                                                        justifyContent: "center",
                                                                                        marginLeft: 10
                                                                                    }}
                                                                                    onClick={() => remove(name)}/>
                                                                            </div>
                                                                        ))}
                                                                        <Form.Item>
                                                                            <Button type="dashed" onClick={() => add()}
                                                                                    block
                                                                                    icon={<PlusOutlined style={{
                                                                                        height: 38,
                                                                                        display: "inline-flex",
                                                                                        alignItems: "center",
                                                                                        justifyContent: "center",
                                                                                        marginLeft: 10
                                                                                    }}/>}>
                                                                                Add option
                                                                            </Button>
                                                                        </Form.Item>
                                                                    </>
                                                                )}
                                                            </Form.List>
                                                        </Col>
                                                    </Row>
                                                ) : null
                                            }

                                            <Form.Item>
                                                <Button style={{marginRight: 12}} type="primary"
                                                        htmlType="submit" {...checkPermissionStyle('edit', 'biz-option-fields', true, isEditMode)}>
                                                    Submit
                                                </Button>
                                                <Button htmlType="button"
                                                        onClick={() => setModalCancelVisible(true)}>
                                                    Cancel
                                                </Button>
                                            </Form.Item>
                                        </Card.Body>
                                    </Card>
                                    <Modal
                                        title={`Cancel ${isEditMode ? 'Edit' : 'Add'} Field`}
                                        visible={modalCancelVisible}
                                        onOk={() => onCancel()}
                                        onCancel={() => setModalCancelVisible(false)}
                                        cancelText="Cancel"
                                        okText="Ok">
                                        <p>Do you want to cancel and quit ?</p>
                                    </Modal>
                                </div>
                            </div>
                        </div>
                    </Form>
                ) : <CircularProgress size={20}/>
            }
        </>
    );
};
